    const response = await fetch(`${API_CONFIG.ENDPOINTS.LOGIN}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      },
      credentials: 'include', // Important for cookie
      body: JSON.stringify({
        userId,
        businessId,
        businessApiKey
      })
    });

    const data = await handleApiResponse(response);
    
    if (data.success) {
      // Store credentials in localStorage for persistent login
      localStorage.setItem(AUTH_CONFIG.STORAGE_KEYS.USER_ID, userId);
      localStorage.setItem(AUTH_CONFIG.STORAGE_KEYS.BUSINESS_ID, businessId);
      localStorage.setItem(AUTH_CONFIG.STORAGE_KEYS.BUSINESS_API_KEY, businessApiKey);
    }
    
    return data;
