// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { Container, Snackbar, Alert } from '@mui/material';
import Configuration from './components/Configuration';
import Login from './components/Login';

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [apiKey, setApiKey] = useState('');
    const [userId, setUserId] = useState('');
    const [businessId, setBusinessId] = useState('');
    const [businessApiKey, setBusinessApiKey] = useState('');
    const [snackbar, setSnackbar] = useState({
        open: false,
        message: '',
        severity: 'info'
    });

    const handleSnackbarOpen = (message, severity = 'info') => {
        setSnackbar({
            open: true,
            message,
            severity
        });
    };

    const handleSnackbarClose = () => {
        setSnackbar(prev => ({
            ...prev,
            open: false
        }));
    };

    // Check authentication status on mount
    useEffect(() => {
        const checkAuth = async () => {
            try {
                const response = await fetch('/validate_config', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    credentials: 'include'
                });
                const data = await response.json();
                setIsAuthenticated(response.ok && data.isValid);
            } catch (error) {
                console.error('Auth check failed:', error);
                setIsAuthenticated(false);
            }
        };
        checkAuth();
    }, []);

    return (
        <Router>
            <Container maxWidth="md">
                <Routes>
                    <Route 
                        path="/login" 
                        element={
                            isAuthenticated ? 
                            <Navigate to="/" replace /> : 
                            <Login 
                                setIsAuthenticated={setIsAuthenticated}
                                handleSnackbarOpen={handleSnackbarOpen}
                            />
                        } 
                    />
                    <Route 
                        path="/" 
                        element={
                            isAuthenticated ? (
                                <Configuration
                                    apiKey={apiKey}
                                    setApiKey={setApiKey}
                                    userId={userId}
                                    setUserId={setUserId}
                                    businessId={businessId}
                                    setBusinessId={setBusinessId}
                                    businessApiKey={businessApiKey}
                                    setBusinessApiKey={setBusinessApiKey}
                                    handleSnackbarOpen={handleSnackbarOpen}
                                    setIsAuthenticated={setIsAuthenticated}
                                />
                            ) : (
                                <Navigate to="/login" replace />
                            )
                        } 
                    />
                </Routes>
                <Snackbar 
                    open={snackbar.open} 
                    autoHideDuration={6000} 
                    onClose={handleSnackbarClose}
                >
                    <Alert 
                        onClose={handleSnackbarClose} 
                        severity={snackbar.severity}
                    >
                        {snackbar.message}
                    </Alert>
                </Snackbar>
            </Container>
        </Router>
    );
}

export default App;
