import React, { useState } from '@mui/material/core';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import { Box } from '@mui/material';

function SendMessage() {
    const [messageInput, setMessageInput] = useState('');
    const [messageOutput, setMessageOutput] = useState('');

    const handleSendMessage = () => {
        // Send message logic here (replace with your actual logic)
        setMessageOutput(`Message Sent (Dummy):\nMessage: ${messageInput}`);
    };

    return (
        <Card>
            <CardContent>
                <Typography variant="h6" gutterBottom>Send Message</Typography>
                <Box sx={{ '& > :not(style)': { m: 1, width: '25ch' } }}>
                    <TextField
                        label="Message"
                        multiline
                        rows={4}
                        variant="outlined"
                        value={messageInput}
                        onChange={(e) => setMessageInput(e.target.value)}
                        style={{ width: 300 }}
                    />
                    <Button variant="contained" onClick={handleSendMessage}>Send</Button>
                    <Typography variant="body1">{messageOutput}</Typography>
                </Box>
            </CardContent>
        </Card>
    );
}

export default SendMessage;
