import React, {useState} from 'react';
import {TextField, Button, Typography, Card, CardContent, Box} from '@mui/material';

function Configuration({
                         apiKey,
                         setApiKey,
                         userId,
                         setUserId,
                         businessId,
                         setBusinessId,
                         configOutput,
                         handleSnackbarOpen
                     }) {
    const [validationError, setValidationError] = useState('');

    const saveConfig = async () => {
        try {
            // Validate configuration with the backend
            const validationResponse = await fetch('http://localhost:5000/validate_config', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({apiKey, userId, businessId})
            });

            const validationData = await validationResponse.json();

            if (validationData.isValid) {
                localStorage.setItem('icmpApiKey', apiKey);
                setConfigOutput(`Configuration Saved:\nUser ID: ${userId}\nBusiness ID: ${businessId}\nAPI Key: ${apiKey}`);
                handleSnackbarOpen("Configuration Saved!", "success");
                setValidationError(''); // Clear any previous error
            } else {
                setValidationError(validationData.error || 'Invalid configuration');
                setConfigOutput(`Error: ${validationData.error || 'Invalid configuration'}`);
                handleSnackbarOpen(validationData.error || "Invalid configuration", "error");
            }
        } catch (error) {
            setValidationError(error.message);
            setConfigOutput(`Error: ${error.message}`);
            handleSnackbarOpen(error.message, "error");
        }
    };

    return (
        <Card sx={{mt: 2}}>
            <CardContent>
                <Typography variant="h6" gutterBottom>Configuration</Typography>
                <Typography variant="body2" color="error">
                    <b>DISCLAIMER:</b> Storing the API Key in local storage is NOT SECURE and should never be done in a
                    production environment. This is for demonstration purposes only!
                </Typography>
                <Box sx={{display: 'flex', flexDirection: 'column', gap: 2, mt: 2}}>
                    <TextField label="API Key" variant="outlined" value={apiKey}
                               onChange={(e) => setApiKey(e.target.value)}/>
                    <TextField label="User ID" variant="outlined" value={userId}
                               onChange={(e) => setUserId(e.target.value)}/>
                    <TextField label="Business ID" variant="outlined" value={businessId}
                               onChange={(e) => setBusinessId(e.target.value)}/>
                    <Button variant="contained" onClick={saveConfig}>Save Config</Button>
                    {validationError && (
                        <Typography variant="body1" color="error">{validationError}</Typography>
                    )}
                    <Typography variant="body1">{configOutput}</Typography>
                </Box>
            </CardContent>
        </Card>
    );
}

export default Configuration;
