import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Typography,
  Grid,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Alert,
  Snackbar,
  IconButton,
  Tooltip
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import HelpOutlineIcon from '@mui/icons-material/HelpOutline';

const StageFlow = ({ stages, businessId }) => {
  const [transitions, setTransitions] = useState([]);
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedTransition, setSelectedTransition] = useState(null);
  const [newTransition, setNewTransition] = useState({
    from_stage: '',
    to_stage: '',
    condition: '',
    description: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    fetchTransitions();
  }, [businessId]);

  const fetchTransitions = async () => {
    try {
      const response = await fetch(`/api/v1/stages/${businessId}/transitions`);
      if (!response.ok) throw new Error('Failed to fetch transitions');
      const data = await response.json();
      setTransitions(data);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleCreateTransition = async () => {
    try {
      const response = await fetch(`/api/v1/stages/${businessId}/transitions`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newTransition),
      });
      if (!response.ok) throw new Error('Failed to create transition');
      const data = await response.json();
      setTransitions([...transitions, data]);
      setOpenDialog(false);
      setNewTransition({
        from_stage: '',
        to_stage: '',
        condition: '',
        description: ''
      });
      setSuccess('Transition created successfully');
    } catch (err) {
      setError(err.message);
    }
  };

  const handleDeleteTransition = async (transitionId) => {
    try {
      const response = await fetch(`/api/v1/stages/${businessId}/transitions/${transitionId}`, {
        method: 'DELETE',
      });
      if (!response.ok) throw new Error('Failed to delete transition');
      setTransitions(transitions.filter(t => t.transition_id !== transitionId));
      setSuccess('Transition deleted successfully');
    } catch (err) {
      setError(err.message);
    }
  };

  const renderTransitionCard = (transition) => {
    const fromStage = stages.find(s => s.stage_id === transition.from_stage);
    const toStage = stages.find(s => s.stage_id === transition.to_stage);

    return (
      <Paper key={transition.transition_id} sx={{ p: 2, mb: 2 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            {fromStage?.stage_name} â†’ {toStage?.stage_name}
          </Typography>
          <Tooltip title="Edit transition">
            <IconButton
              size="small"
              onClick={() => {
                setSelectedTransition(transition);
                setNewTransition(transition);
                setOpenDialog(true);
              }}
            >
              <EditIcon />
            </IconButton>
          </Tooltip>
          <Tooltip title="Delete transition">
            <IconButton
              size="small"
              color="error"
              onClick={() => handleDeleteTransition(transition.transition_id)}
            >
              <DeleteIcon />
            </IconButton>
          </Tooltip>
        </Box>
        <Typography variant="body2" color="text.secondary" gutterBottom>
          Condition: {transition.condition}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {transition.description}
        </Typography>
      </Paper>
    );
  };

  return (
    <Box sx={{ p: 3 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h5">Stage Transitions</Typography>
        <Button
          startIcon={<AddIcon />}
          variant="contained"
          onClick={() => {
            setSelectedTransition(null);
            setNewTransition({
              from_stage: '',
              to_stage: '',
              condition: '',
              description: ''
            });
            setOpenDialog(true);
          }}
        >
          Add Transition
        </Button>
      </Box>

      <Grid container spacing={2}>
        <Grid item xs={12}>
          {transitions.map(renderTransitionCard)}
        </Grid>
      </Grid>

      <Dialog open={openDialog} onClose={() => setOpenDialog(false)} maxWidth="sm" fullWidth>
        <DialogTitle>
          {selectedTransition ? 'Edit Transition' : 'Create New Transition'}
        </DialogTitle>
        <DialogContent>
          <FormControl fullWidth sx={{ mt: 2 }}>
            <InputLabel>From Stage</InputLabel>
            <Select
              value={newTransition.from_stage}
              onChange={(e) => setNewTransition({ ...newTransition, from_stage: e.target.value })}
              label="From Stage"
            >
              {stages.map((stage) => (
                <MenuItem key={stage.stage_id} value={stage.stage_id}>
                  {stage.stage_name}
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          <FormControl fullWidth sx={{ mt: 2 }}>
            <InputLabel>To Stage</InputLabel>
            <Select
              value={newTransition.to_stage}
              onChange={(e) => setNewTransition({ ...newTransition, to_stage: e.target.value })}
              label="To Stage"
            >
              {stages.map((stage) => (
                <MenuItem key={stage.stage_id} value={stage.stage_id}>
                  {stage.stage_name}
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          <TextField
            fullWidth
            label="Condition"
            value={newTransition.condition}
            onChange={(e) => setNewTransition({ ...newTransition, condition: e.target.value })}
            sx={{ mt: 2 }}
            helperText="Enter the condition that triggers this transition (e.g., 'user_agreed', 'payment_completed')"
          />

          <TextField
            fullWidth
            label="Description"
            value={newTransition.description}
            onChange={(e) => setNewTransition({ ...newTransition, description: e.target.value })}
            multiline
            rows={3}
            sx={{ mt: 2 }}
            helperText="Describe when and why this transition occurs"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Cancel</Button>
          <Button onClick={handleCreateTransition} variant="contained">
            {selectedTransition ? 'Update' : 'Create'}
          </Button>
        </DialogActions>
      </Dialog>

      <Snackbar
        open={!!error}
        autoHideDuration={6000}
        onClose={() => setError('')}
      >
        <Alert severity="error" onClose={() => setError('')}>
          {error}
        </Alert>
      </Snackbar>

      <Snackbar
        open={!!success}
        autoHideDuration={6000}
        onClose={() => setSuccess('')}
      >
        <Alert severity="success" onClose={() => setSuccess('')}>
          {success}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default StageFlow;
