// File: src/components/TemplateManagement.js
// Last Modified: 2026-03-29
import React, { useState, useEffect } from 'react';
import {
    TextField, Button, Typography, Card, CardContent, Box, TextareaAutosize,
    Select, MenuItem, InputLabel, FormControl, Grid
} from '@mui/material';

function TemplateManagement({ templateID, setTemplateID, availableVariable, setAvailableVariable, selectedVariable, setSelectedVariable, handleTemplateSelection, handleVariableSelection, addVariableToTemplate, templateName, setTemplateName, templateText, setTemplateText, templateOutput, createTemplate, fetchTemplates, handleSnackbarOpen }) {

    return (
        <Card sx={{ mt: 2 }}>
            <CardContent>
                <Typography variant="h6" gutterBottom>Template Management</Typography>
                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
                    <TextField label="Template Name" variant="outlined" value={templateName} onChange={(e) => setTemplateName(e.target.value)} />

                    {/* Default Template Dropdown */}
                    <FormControl fullWidth variant="outlined" sx={{ mb: 2 }}>
                        <InputLabel id="default-template-label">Default Templates</InputLabel>
                        <Select
                            labelId="default-template-label"
                            id="default-template-select"
                            value={templateID}
                            label="Default Templates"
                            onChange={handleTemplateSelection}
                        >
                            <MenuItem value=""><em>None</em></MenuItem>
                            {/*   {defaultTemplates.map((template) => (
                                    <MenuItem key={template.template_id} value={template.template_id}>
                                        {template.template_name}
                                    </MenuItem>
                                ))} */}
                        </Select>
                    </FormControl>
                    {/* Text Editor */}
                    <TextareaAutosize
                        minRows={5}
                        placeholder="Template Text"
                        style={{ width: '100%', padding: 8, marginBottom: 16 }}
                        value={templateText}
                        onChange={(e) => setTemplateText(e.target.value)}
                    />
                    {/* Add Dynamic Data  */}

                    <FormControl fullWidth variant="outlined" sx={{ mb: 2 }}>
                        <InputLabel id="dynamic-variables-label">Dynamic Variables</InputLabel>
                        <Select
                            labelId="dynamic-variables-label"
                            id="dynamic-variables-select"
                            value={selectedVariable}
                            label="Select Variable"
                            onChange={handleVariableSelection}
                        >
                            <MenuItem value=""><em>None</em></MenuItem>
                            {/*   {availableVariable.map((variable) => (
                                    <MenuItem key={variable} value={variable}>
                                        {variable}
                                    </MenuItem>
                                ))} */}
                        </Select>
                    </FormControl>
                    <Box sx={{ display: 'flex', gap: 2, mt: 2 }}>
                        <Button variant="contained" onClick={createTemplate}>Create Template</Button>
                        <Button variant="contained" onClick={fetchTemplates}>Fetch Templates</Button>

                    </Box>
                    {/* Buttons */}
                    <Grid>
                        <Grid item xs={12} md={6}>
                            <Button variant="contained" onClick={addVariableToTemplate}>Add Variables</Button>
                        </Grid>
                        {/*<Grid item xs={12} md={6}>
                                <TextareaAutosize
                                    minRows={1}
                                    placeholder="Add New  Variable"
                                    style={{ width: '100%', padding: 8, marginBottom: 16 }}
                                />
                            </Grid> */}
                        {/*    <Button variant="contained" onClick={handleSaveDefaultTemplate}>Save Default</Button> */}
                    </Grid>
                    <Typography variant="body1">{templateOutput}</Typography>
                </Box>
            </CardContent>
        </Card>
    );
}

export default TemplateManagement;
