import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { cachedFetch } from '../utils/fetchUtils';
import {
  Box,
  Paper,
  Typography,
  TextField,
  Button,
  Tabs,
  Tab,
  Grid,
  Alert,
  Snackbar,
  IconButton,
  Tooltip,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  CircularProgress,
  Divider,
  Chip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions
} from '@mui/material';
import SaveIcon from '@mui/icons-material/Save';
import PreviewIcon from '@mui/icons-material/Preview';
import RestartAltIcon from '@mui/icons-material/RestartAlt';
import HelpOutlineIcon from '@mui/icons-material/HelpOutline';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';

// Global request cache to prevent duplicate API calls
const apiCache = {
  cache: {},
  get: function(url) {
    return this.cache[url]?.data;
  },
  set: function(url, data, ttl = 60000) { // Default TTL: 1 minute
    this.cache[url] = {
      data,
      expiry: Date.now() + ttl
    };
  },
  isValid: function(url) {
    const item = this.cache[url];
    return item && item.expiry > Date.now();
  },
  clear: function() {
    this.cache = {};
  }
};

// TabPanel component for the tabs
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`tabpanel-${index}`}
      aria-labelledby={`tab-${index}`}
      {...other}
      style={{ padding: '20px 0' }}
    >
      {value === index && (
        <Box>
          {children}
        </Box>
      )}
    </div>
  );
}

const StageEditor = () => {
  const { stageId: urlStageId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  
  // Add stageId state
  const [stageId, setStageId] = useState(urlStageId || 'new');
  
  // Local implementation of getStoredCredentials
  const getStoredCredentials = () => {
    return {
      businessId: localStorage.getItem('businessId') || '',
      businessApiKey: localStorage.getItem('businessApiKey') || ''
    };
  };
  
  // Check local storage and cookies for business ID and API key
  const getStoredValue = (key) => {
    // Try localStorage first
    const localStorageValue = localStorage.getItem(key);
    if (localStorageValue) return localStorageValue;
    
    // Then try cookies
    const cookieValue = document.cookie
      .split('; ')
      .find(row => row.startsWith(`${key}=`))
      ?.split('=')[1];
    if (cookieValue) return cookieValue;
    
    // Finally try getStoredCredentials
    const storedCredentials = getStoredCredentials();
    if (key === 'businessId') return storedCredentials.businessId;
    if (key === 'businessApiKey') return storedCredentials.businessApiKey;
    
    return null;
  };
  
  const queryParams = new URLSearchParams(location.search);
  const queryBusinessId = queryParams.get('business_id');
  
  // Location state values as fallback
  const { businessId: locationBusinessId, agentId: locationAgentId, isNewStage } = location.state || {};
  
  // Get stored values
  const storedBusinessId = getStoredValue('businessId') || getStoredCredentials().businessId;
  const storedApiKey = getStoredValue('businessApiKey') || getStoredCredentials().businessApiKey;
  
  // Use query parameters first, then location state, then stored values
  const [businessId, setBusinessId] = useState(queryBusinessId || locationBusinessId || storedBusinessId || '');
  const [businessApiKey, setBusinessApiKey] = useState(storedApiKey || '');
  const [showBusinessIdInput, setShowBusinessIdInput] = useState(!businessId || !businessApiKey);
  
  const agentId = queryParams.get('agent_id') || locationAgentId;
  
  const [stage, setStage] = useState(null);
  const [stageName, setStageName] = useState('');
  const [stageDescription, setStageDescription] = useState('');
  const [tabValue, setTabValue] = useState(0);
  const [templates, setTemplates] = useState({
    stage_selection: { id: '', text: '', type: '' },
    data_extraction: { id: '', text: '', type: '' },
    response_generation: { id: '', text: '', type: '' }
  });
  const [availableTemplates, setAvailableTemplates] = useState({
    stage_selection: [],
    data_extraction: [],
    response_generation: [],
    default_stage_selection: [],
    default_data_extraction: [],
    default_response_generation: []
  });
  const [variables, setVariables] = useState({
    stage_selection: [],
    data_extraction: [],
    response_generation: []
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [previewData, setPreviewData] = useState(null);
  const [previewLoading, setPreviewLoading] = useState(false);
  const [previewError, setPreviewError] = useState('');
  const [showPreview, setShowPreview] = useState(false);
  
  // Add template saving loading states
  const [savingTemplates, setSavingTemplates] = useState({
    stage_selection: false,
    data_extraction: false,
    response_generation: false
  });

  // Add template save confirmation dialog
  const [showConfirmDialog, setShowConfirmDialog] = useState(false);
  const [templateToSave, setTemplateToSave] = useState('');
  const [saveAsDefault, setSaveAsDefault] = useState(false);

  // Track if component is mounted
  const isMounted = useRef(true);
  
  // Clear any previous fetch timeouts on unmount
  useEffect(() => {
    return () => {
      isMounted.current = false;
    };
  }, []);

  // Function to open the confirmation dialog
  const confirmSaveTemplate = (type, asDefault = false) => {
    setTemplateToSave(type);
    setSaveAsDefault(asDefault);
    setShowConfirmDialog(true);
  };

  // Function to handle dialog confirm
  const handleConfirmSave = async () => {
    setShowConfirmDialog(false);
    if (templateToSave) {
      await saveTemplate(templateToSave, saveAsDefault);
    }
  };

  // Add validation function for credentials
  const validateCredentials = useCallback(async () => {
    if (!businessId || !businessApiKey) {
      setError('Business ID and API Key are required');
      setShowBusinessIdInput(true);
      return false;
    }

    try {
      // Test credentials with the validate-credentials endpoint
      const response = await fetch(`/businesses/validate-credentials?business_id=${businessId}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Authorization': `Bearer ${businessApiKey}`
        },
        credentials: 'include'
      });

      if (!response.ok) {
        throw new Error('Invalid business ID or API key');
      }

      return true;
    } catch (err) {
      console.error('Credential validation error:', err);
      setError('Failed to validate credentials: ' + err.message);
      setShowBusinessIdInput(true);
      return false;
    }
  }, [businessId, businessApiKey]);

  // Add a throttled fetch for all templates with proper dependencies
  const fetchAllTemplates = useCallback(async () => {
    const { businessId, businessApiKey } = getStoredCredentials();
    
    if (!businessId || !businessApiKey || !isMounted.current) {
      return;
    }
    
    console.log('Fetching templates for business:', businessId);
    
    try {
      const response = await fetch(`/templates?business_id=${businessId}`, {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
          'Authorization': `Bearer ${businessApiKey}`
        },
        credentials: 'include'
      });
      
      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        console.error('Error fetching templates:', response.status, errorData);
        throw new Error(`Failed to fetch templates: ${response.status} ${errorData.message || ''}`);
      }
      
      const templates = await response.json();
      console.log('Fetched templates:', templates);
      
      // Initialize template categories
      const templatesByType = {
        stage_selection: [],
        data_extraction: [],
        response_generation: [],
        default_stage_selection: [],
        default_data_extraction: [],
        default_response_generation: [],
        other: []
      };
      
      // Map template types to their corresponding categories
      const typeMapping = {
        'stage_selection': 'stage_selection',
        'data_extraction': 'data_extraction', 
        'response_generation': 'response_generation',
        'default_stage_selection': 'default_stage_selection',
        'default_data_extraction': 'default_data_extraction',
        'default_response_generation': 'default_response_generation'
      };
      
      // Organize templates by type
      templates.forEach(template => {
        const type = template.template_type || 'generic';
        const mappedType = typeMapping[type] || 'other';
        
        // If template has no template_type, check if name contains any type keywords
        let assignedType = mappedType;
        if (mappedType === 'other' || mappedType === 'generic') {
          const name = (template.template_name || '').toLowerCase();
          if (name.includes('selection')) assignedType = 'stage_selection';
          else if (name.includes('extract')) assignedType = 'data_extraction';
          else if (name.includes('response') || name.includes('generation')) assignedType = 'response_generation';
        }
        
        // Add the template to the appropriate category
        if (!templatesByType[assignedType]) {
          templatesByType[assignedType] = [];
        }
        templatesByType[assignedType].push(template);
      });
      
      console.log('Organized templates by type:', templatesByType);
      
      if (isMounted.current) {
        setAvailableTemplates(templatesByType);
      }
    } catch (error) {
      console.error('Error in fetchAllTemplates:', error);
      if (isMounted.current) {
        setError(`Failed to fetch templates: ${error.message}`);
      }
    }
  }, []);

  useEffect(() => {
    let isActive = true;
    
    const initializeComponent = async () => {
      try {
        // Start with loading state
        setIsLoading(true);
        
        // Check if we have the required credentials
        if (!businessId || !businessApiKey) {
          setShowBusinessIdInput(true);
          setIsLoading(false);
          return;
        }
        
        // Validate credentials before proceeding
        const isValid = await validateCredentials();
        if (!isActive) return;
        
        if (!isValid) {
          setIsLoading(false);
          return;
        }
        
        // Store validated credentials
        localStorage.setItem('businessId', businessId);
        localStorage.setItem('businessApiKey', businessApiKey);
        document.cookie = `businessId=${businessId}; path=/; max-age=86400`;
        document.cookie = `businessApiKey=${businessApiKey}; path=/; max-age=86400`;
        
        // Load stage data if needed
        if (stageId === 'new' || isNewStage) {
          // For new stage, just set up defaults
          if (isActive) {
            setStage({
              stage_name: '',
              stage_description: '',
              business_id: businessId,
              agent_id: agentId,
              stage_selection_template_id: null,
              data_extraction_template_id: null,
              response_generation_template_id: null,
              stage_config: { order: 0 }
            });
          }
        } else if (stageId) {
          // Fetch existing stage
          await fetchStage(stageId);
        }
        
        // Always fetch templates with validated credentials
        if (isActive) {
          await fetchAllTemplates();
        }
      } catch (err) {
        console.error('Error initializing component:', err);
        if (isActive) {
          setError('Error initializing: ' + err.message);
        }
      } finally {
        if (isActive) {
          setIsLoading(false);
        }
      }
    };
    
    initializeComponent();
    
    return () => {
      isActive = false;
    };
  }, [stageId, businessId, businessApiKey, agentId, isNewStage, fetchAllTemplates, validateCredentials]);

  // Fetch specific stage data
  const fetchStage = async (id) => {
    if (!isMounted.current || !businessId || !businessApiKey) return;
    
    setIsLoading(true);
    try {
      console.log(`Fetching stage with ID: ${id} for business: ${businessId} and agent: ${agentId}`);
      
      // Build query parameters
      const params = new URLSearchParams();
      params.append('business_id', businessId);
      if (agentId) {
        params.append('agent_id', agentId);
      }
      
      // Use direct fetch with proper headers
      const response = await fetch(`/stages/${id}?${params.toString()}`, {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${businessApiKey}`
        },
        credentials: 'include'
      });
      
      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(`Failed to fetch stage: ${response.status} ${errorData.message || ''}`);
      }
      
      const data = await response.json();
      console.log('Fetched stage data:', data);
      
      if (isMounted.current) {
        setStage(data);
        setStageName(data.stage_name || '');
        setStageDescription(data.stage_description || '');
        
        // For each template ID in the stage, fetch the template content
        const templatePromises = [];
        if (data.stage_selection_template_id) {
          templatePromises.push(fetchTemplate(data.stage_selection_template_id, 'stage_selection'));
        }
        if (data.data_extraction_template_id) {
          templatePromises.push(fetchTemplate(data.data_extraction_template_id, 'data_extraction'));
        }
        if (data.response_generation_template_id) {
          templatePromises.push(fetchTemplate(data.response_generation_template_id, 'response_generation'));
        }
        
        // Wait for all templates to be fetched
        await Promise.all(templatePromises);
      }
    } catch (err) {
      console.error('Error fetching stage:', err);
      if (isMounted.current) {
        setError(err.message);
      }
    } finally {
      if (isMounted.current) {
        setIsLoading(false);
      }
    }
  };

  const fetchTemplate = async (templateId, type) => {
    if (!isMounted.current || !businessId || !businessApiKey || !templateId) {
      console.error('Missing required parameters for fetchTemplate:', { businessId, businessApiKey, templateId });
      return null;
    }
    
    try {
      console.log(`Fetching template with ID: ${templateId} for type: ${type}`);
      
      // Build query parameters
      const params = new URLSearchParams();
      params.append('business_id', businessId);
      if (agentId) {
        params.append('agent_id', agentId);
      }
      
      const response = await fetch(`/templates/${templateId}?${params.toString()}`, {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${businessApiKey}`
        },
        credentials: 'include'
      });
      
      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(`Failed to fetch template: ${response.status} ${errorData.message || ''}`);
      }
      
      const template = await response.json();
      console.log('Fetched template:', template);
      
      if (isMounted.current) {
        // Update templates state with the fetched template
        setTemplates(prev => ({
          ...prev,
          [type]: {
            id: templateId,
            text: template.template_text || '',
            type: template.template_type || type,
            name: template.template_name || ''
          }
        }));
        
        // Update variables based on template text
        const variables = extractVariables(template.template_text || '');
        setVariables(prev => ({
          ...prev,
          [type]: variables
        }));
      }
      
      return template;
    } catch (err) {
      console.error('Error fetching template:', err);
      if (isMounted.current) {
        setError(err.message);
      }
      return null;
    }
  };

  const extractVariables = (template) => {
    if (!template) return [];
    const matches = template.match(/\{([^}]+)\}/g) || [];
    return matches.map(match => match.slice(1, -1));
  };

  const handleTemplateChange = (type, value) => {
    setTemplates(prev => ({
      ...prev,
      [type]: {
        ...prev[type],
        text: value
      }
    }));
    // Update variables when template changes
    setVariables(prev => ({
      ...prev,
      [type]: extractVariables(value)
    }));
  };

  const handleTemplateSelect = async (type, templateId) => {
    // If selecting "None", clear the template
    if (!templateId) {
      setTemplates(prev => ({
        ...prev,
        [type]: { id: '', text: '', type: '' }
      }));
      setVariables(prev => ({
        ...prev,
        [type]: []
      }));
      return;
    }
    
    // Set loading state for this template type
    setSavingTemplates(prev => ({
      ...prev,
      [type]: true
    }));
    
    try {
      // Fetch the selected template
      const template = await fetchTemplate(templateId, type);
      
      if (!template) {
        throw new Error(`Failed to fetch template with ID: ${templateId}`);
      }
      
      // Template state is already updated in fetchTemplate
      setSuccess(`Template for ${type} loaded successfully`);
    } catch (err) {
      console.error(`Error selecting template for ${type}:`, err);
      setError(`Failed to load template: ${err.message}`);
    } finally {
      // Clear loading state
      setSavingTemplates(prev => ({
        ...prev,
        [type]: false
      }));
    }
  };

  const handleApplyDefaultTemplate = async (type) => {
    // Map regular types to their default counterparts
    const defaultTypeMap = {
      'stage_selection': 'default_stage_selection',
      'data_extraction': 'default_data_extraction',
      'response_generation': 'default_response_generation'
    };
    
    const defaultType = defaultTypeMap[type];
    
    // Find default templates of the corresponding type
    const defaultTemplates = availableTemplates[defaultType];
    
    if (defaultTemplates.length === 0) {
      setError(`No default templates available for ${type}`);
      return;
    }
    
    // For simplicity, use the first default template
    const defaultTemplate = defaultTemplates[0];
    
    // Apply the default template text (but keep the original ID)
    setTemplates(prev => ({
      ...prev,
      [type]: {
        ...prev[type],
        text: defaultTemplate.template_text,
        type: type // Keep the original type, not the default type
      }
    }));
    
    // Update variables
    setVariables(prev => ({
      ...prev,
      [type]: extractVariables(defaultTemplate.template_text)
    }));
    
    setSuccess(`Applied default template for ${type}`);
  };

  const handlePreview = async (templateType) => {
    try {
      setPreviewLoading(true);
      setPreviewError('');
      
      const templateText = templates[templateType].text;
      if (!templateText.trim()) {
        setPreviewError('Template text is empty');
        return;
      }
      
      if (!businessApiKey) {
        setPreviewError('Business API key is required but was not found');
        setShowBusinessIdInput(true);
        return;
      }
      
      // Prepare preview request
      const previewRequest = {
        template_type: templateType,
        template_text: templateText,
        context: {
          conversation_history: [
            { role: "user", content: "I'm interested in your products" },
            { role: "assistant", content: "I'd be happy to tell you about our products. What type are you interested in?" }
          ],
          extracted_data: {
            customer_name: "John Doe",
            product_interest: "shoes",
            budget: "$100"
          }
        }
      };
      
      // Try multiple possible endpoints (for compatibility)
      const endpoints = [
        '/templates/render',
        '/render_template',
        '/api/templates/preview'
      ];
      
      let response = null;
      for (const endpoint of endpoints) {
        try {
          console.log(`Trying to preview template with endpoint: ${endpoint}`);
          
          response = await cachedFetch(endpoint, {
            method: 'POST',
            body: JSON.stringify(previewRequest)
          });
          
          if (response.ok) break;
        } catch (endpointErr) {
          console.warn(`Endpoint ${endpoint} failed:`, endpointErr);
        }
      }
      
      // If we got a successful response from any endpoint
      if (response && response.ok) {
        const data = await response.json();
        console.log('Preview response:', data);
        setPreviewData(data);
        setShowPreview(true);
      } else {
        // Local fallback preview if no endpoint worked
        console.log('All endpoint attempts failed, using local preview');
        
        // Simple variable replacement for demonstration
        let previewText = templateText;
        const variables = extractVariables(templateText);
        
        // Replace variables with sample values
        variables.forEach(variable => {
          const sampleValue = `[Sample ${variable}]`;
          previewText = previewText.replace(new RegExp(`{{\\s*${variable}\\s*}}`, 'g'), sampleValue);
        });
        
        setPreviewData({ rendered_text: previewText });
        setShowPreview(true);
      }
    } catch (err) {
      console.error('Error generating preview:', err);
      setPreviewError(`Failed to generate preview: ${err.message}`);
    } finally {
      setPreviewLoading(false);
    }
  };

  // Update saveTemplate function to use cachedFetch
  const saveTemplate = async (templateType, asDefault = false) => {
    // Don't allow saving if we don't have valid credentials
    if (!businessId) {
      setError('Business ID is required to save templates');
      return;
    }
    
    // Set loading state for this specific template type
    setSavingTemplates(prev => ({
      ...prev,
      [templateType]: true
    }));
    
    try {
      const templateData = {
        business_id: businessId,
        template_text: templates[templateType].text,
        template_type: asDefault ? `default_${templateType}` : templateType,
        variables: variables[templateType]
      };
      
      // Add template name if available
      if (templates[templateType].name) {
        templateData.template_name = templates[templateType].name;
      }
      
      // If we have an existing template ID and we're not saving as default, update it
      const templateId = templates[templateType].id;
      let url, method;
      
      if (templateId && !asDefault) {
        // Update existing template
        url = `/templates/${templateId}`;
        method = 'PUT';
      } else {
        // Create new template
        url = '/templates';
        method = 'POST';
      }
      
      console.log(`${method} template to ${url}`, templateData);
      
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(templateData),
      });
      
      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || `Failed to save ${templateType} template`);
      }
      
      const result = await response.json();
      console.log('Template saved successfully:', result);
      
      // Update local state with new template ID if it was created
      if (method === 'POST' && result.template_id) {
        setTemplates(prev => ({
          ...prev,
          [templateType]: {
            ...prev[templateType],
            id: result.template_id
          }
        }));
        
        // Update stage with new template ID if needed
        if (stage && !asDefault) {
          const templateIdField = `${templateType}_template_id`;
          setStage(prev => ({
            ...prev,
            [templateIdField]: result.template_id
          }));
        }
      }
      
      // Show success message
      const successAction = method === 'POST' ? 'created' : 'updated';
      const successType = asDefault ? 'default template' : 'template';
      setSuccess(`${templateType} ${successType} ${successAction} successfully`);
      
      // Refresh templates list
      fetchAllTemplates();
      
    } catch (err) {
      console.error(`Error saving ${templateType} template:`, err);
      setError(err.message || `Failed to save ${templateType} template`);
    } finally {
      // Clear loading state
      setSavingTemplates(prev => ({
        ...prev,
        [templateType]: false
      }));
    }
  };

  // Update handleSave function
  const handleSave = async () => {
    if (!stageName.trim()) {
      setError('Stage name is required');
      return;
    }

    if (!businessId) {
      setError('Business ID is required');
      return;
    }

    setIsLoading(true);
    try {
      // First save any unsaved templates
      const templatePromises = [];
      
      // Create template save promises for each template type
      const templateTypes = ['stage_selection', 'data_extraction', 'response_generation'];
      for (const templateType of templateTypes) {
        // Only save templates that have content but no ID
        if (templates[templateType].text && !templates[templateType].id) {
          // Set template name based on stage name
          templates[templateType].name = `${stageName} - ${templateType.replace('_', ' ').toUpperCase()}`;
          templatePromises.push(saveTemplate(templateType, false));
        }
      }
      
      // Wait for all templates to be saved
      await Promise.all(templatePromises);
      
      const method = stageId === 'new' ? 'POST' : 'PUT';
      const url = stageId === 'new' ? '/stages' : `/stages/${stageId}`;
      
      // Get updated template IDs from the templates object
      const selectionTemplateId = templates.stage_selection.id;
      const extractionTemplateId = templates.data_extraction.id;
      const responseTemplateId = templates.response_generation.id;
      
      const stageData = {
        stage_name: stageName,
        stage_description: stageDescription,
        business_id: businessId,
        stage_selection_template_id: selectionTemplateId || null,
        data_extraction_template_id: extractionTemplateId || null,
        response_generation_template_id: responseTemplateId || null,
        stage_type: stage?.stage_type || 'conversation'
      };
      
      // Only include agent_id if it exists
      if (agentId) {
        stageData.agent_id = agentId;
      }
      
      console.log(`Saving stage with ${method} to ${url}:`, stageData);
      
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${businessApiKey}`
        },
        credentials: 'include',
        body: JSON.stringify(stageData),
      });
      
      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || 'Failed to save stage');
      }
      
      const result = await response.json();
      console.log('Stage saved successfully:', result);
      
      setSuccess('Stage saved successfully');
      
      // If this was a new stage, update the stageId and URL
      if (stageId === 'new' && result.stage_id) {
        setStageId(result.stage_id);
        
        // Update URL without page reload
        const newUrl = `/stage-editor/${result.stage_id}?business_id=${businessId}${agentId ? `&agent_id=${agentId}` : ''}`;
        window.history.pushState({}, '', newUrl);
      }
      
      // Fetch the updated stage data
      if (result.stage_id) {
        await fetchStage(result.stage_id);
      }
      
    } catch (err) {
      console.error('Error saving stage:', err);
      setError(err.message || 'Failed to save stage');
    } finally {
      setIsLoading(false);
    }
  };

  const handleCancel = () => {
    // Navigate back to stage manager
    let backUrl;
    if (businessId && agentId) {
      backUrl = `/stage-management/business_id=${businessId}/agent_id=${agentId}`;
    } else if (businessId) {
      backUrl = `/stage-management/business_id=${businessId}`;
    } else {
      backUrl = '/stage-management';
    }
    navigate(backUrl);
  };

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  // Update the credentials save function
  const saveCredentials = () => {
    if (!businessId || !businessApiKey) {
      setError('Please enter both Business ID and API Key');
      return;
    }
    
    // Validate credentials first
    validateCredentials().then(isValid => {
      if (isValid) {
        // Save to both localStorage and cookies
        localStorage.setItem('businessId', businessId);
        localStorage.setItem('businessApiKey', businessApiKey);
        document.cookie = `businessId=${businessId}; path=/; max-age=86400`;
        document.cookie = `businessApiKey=${businessApiKey}; path=/; max-age=86400`;
        
        setShowBusinessIdInput(false);
        setSuccess('Business credentials validated and saved');
        
        // Reload templates
        fetchAllTemplates();
      }
    });
  };

  return (
    <Box>
      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}
      
      {success && (
        <Alert severity="success" sx={{ mb: 2 }}>
          {success}
        </Alert>
      )}
      
      <Paper sx={{ p: 3, mb: 3 }}>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Stage Name"
              value={stageName}
              onChange={(e) => setStageName(e.target.value)}
              required
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Stage Description"
              value={stageDescription}
              onChange={(e) => setStageDescription(e.target.value)}
              multiline
              rows={3}
            />
          </Grid>
        </Grid>
      </Paper>
      
      <Paper sx={{ p: 3, mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          Templates
        </Typography>
        <Tabs value={tabValue} onChange={handleTabChange} sx={{ mb: 2 }}>
          <Tab label="Stage Selection" />
          <Tab label="Data Extraction" />
          <Tab label="Response Generation" />
        </Tabs>
        
        {/* Template Selection */}
        <Box sx={{ mb: 3 }}>
          <FormControl fullWidth>
            <InputLabel>Select Template</InputLabel>
            <Select
              value={templates[templateTypes[tabValue]]?.id || ''}
              onChange={(e) => handleTemplateSelect(templateTypes[tabValue], e.target.value)}
              label="Select Template"
            >
              <MenuItem value="">
                <em>None</em>
              </MenuItem>
              {availableTemplates[templateTypes[tabValue]]?.map((template) => (
                <MenuItem key={template.template_id} value={template.template_id}>
                  {template.template_name}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </Box>
        
        {/* Template Editor */}
        <TextField
          fullWidth
          label={`${templateTypes[tabValue]} Template`}
          value={templates[templateTypes[tabValue]]?.text || ''}
          onChange={(e) => handleTemplateChange(templateTypes[tabValue], e.target.value)}
          multiline
          rows={10}
          disabled={!templates[templateTypes[tabValue]]?.id}
        />
        
        {/* Variables */}
        {variables[templateTypes[tabValue]]?.length > 0 && (
          <Box sx={{ mt: 2 }}>
            <Typography variant="subtitle1" gutterBottom>
              Variables
            </Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
              {variables[templateTypes[tabValue]].map((variable) => (
                <Chip key={variable} label={variable} />
              ))}
            </Box>
          </Box>
        )}
      </Paper>
      
      {/* Action Buttons */}
      <Box sx={{ display: 'flex', justifyContent: 'flex-end', gap: 2 }}>
        <Button
          variant="outlined"
          onClick={handleReset}
          startIcon={<RestartAltIcon />}
        >
          Reset
        </Button>
        <Button
          variant="contained"
          color="primary"
          onClick={handleSave}
          startIcon={<SaveIcon />}
          disabled={isLoading || savingTemplates[templateTypes[tabValue]]}
        >
          {isLoading || savingTemplates[templateTypes[tabValue]] ? (
            <CircularProgress size={24} />
          ) : (
            'Save'
          )}
        </Button>
      </Box>
      
      {/* Preview Dialog */}
      <Dialog
        open={previewOpen}
        onClose={handlePreviewClose}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>Stage Preview</DialogTitle>
        <DialogContent>
          <Box sx={{ mb: 2 }}>
            <Typography variant="h6">Stage Information</Typography>
            <Typography><strong>Name:</strong> {stageName}</Typography>
            <Typography><strong>Description:</strong> {stageDescription}</Typography>
          </Box>
          
          <Divider sx={{ my: 2 }} />
          
          <Box sx={{ mb: 2 }}>
            <Typography variant="h6">Templates</Typography>
            {Object.entries(templates).map(([type, template]) => (
              <Box key={type} sx={{ mt: 2 }}>
                <Typography variant="subtitle1"><strong>{type}:</strong></Typography>
                {template?.text ? (
                  <Paper sx={{ p: 2, bgcolor: 'grey.100' }}>
                    <pre style={{ whiteSpace: 'pre-wrap', margin: 0 }}>
                      {template.text}
                    </pre>
                  </Paper>
                ) : (
                  <Typography color="text.secondary">No template selected</Typography>
                )}
              </Box>
            ))}
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handlePreviewClose}>Close</Button>
        </DialogActions>
      </Dialog>
      
      {/* Help Dialog */}
      <Dialog
        open={helpOpen}
        onClose={handleHelpClose}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>Stage Editor Help</DialogTitle>
        <DialogContent>
          <Typography variant="h6" gutterBottom>Stage Information</Typography>
          <Typography paragraph>
            The stage name and description help identify the purpose of this stage in your workflow.
          </Typography>
          
          <Typography variant="h6" gutterBottom>Templates</Typography>
          <Typography paragraph>
            Each stage can have three types of templates:
          </Typography>
          <ul>
            <li><strong>Stage Selection:</strong> Used to determine which stage to go to next</li>
            <li><strong>Data Extraction:</strong> Used to extract data from the user's input</li>
            <li><strong>Response Generation:</strong> Used to generate a response to the user</li>
          </ul>
          
          <Typography variant="h6" gutterBottom>Variables</Typography>
          <Typography paragraph>
            Variables are placeholders in your templates that will be replaced with actual values.
            They are enclosed in curly braces, like {'{variable_name}'}.
          </Typography>
          
          <Typography variant="h6" gutterBottom>Saving</Typography>
          <Typography paragraph>
            Click the Save button to save your changes. You can preview your stage before saving by clicking the Preview button.
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleHelpClose}>Close</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default StageEditor;
