import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import {
  Box,
  Paper,
  Typography,
  TextField,
  Button,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  CircularProgress,
  Grid,
  Alert,
  Divider,
  Chip,
  IconButton,
  Tooltip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Snackbar
} from '@mui/material';
import SaveIcon from '@mui/icons-material/Save';
import PreviewIcon from '@mui/icons-material/Preview';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import HelpOutlineIcon from '@mui/icons-material/HelpOutline';
import VisibilityIcon from '@mui/icons-material/Visibility';

const TemplateEditor = () => {
  const { templateId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  
  // Parse query parameters
  const queryParams = new URLSearchParams(location.search);
  const queryBusinessId = queryParams.get('business_id');
  const queryAgentId = queryParams.get('agent_id');
  
  // Get stored business ID and API key from localStorage
  const storedBusinessId = localStorage.getItem('businessId');
  const storedApiKey = localStorage.getItem('businessApiKey');
  const storedAgentId = localStorage.getItem('agentId');
  
  // State for template data
  const [template, setTemplate] = useState({
    template_id: templateId === 'new' ? '' : templateId,
    template_name: '',
    template_text: '',
    template_type: 'stage_selection',
    template_description: '',
    variables: [],
    agent_id: ''
  });
  
  // State for UI
  const [businessId, setBusinessId] = useState(queryBusinessId || storedBusinessId || '');
  const [businessApiKey, setBusinessApiKey] = useState(storedApiKey || '');
  const [agentId, setAgentId] = useState(queryAgentId || storedAgentId || '');
  const [showBusinessIdInput, setShowBusinessIdInput] = useState(!businessId || !businessApiKey);
  const [showAgentIdInput, setShowAgentIdInput] = useState(!agentId);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [previewData, setPreviewData] = useState(null);
  const [showPreview, setShowPreview] = useState(false);
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'success'
  });
  
  // Add state for template types array
  const templateTypes = [
    { value: 'stage_selection', label: 'Stage Selection', group: 'Regular' },
    { value: 'data_extraction', label: 'Data Extraction', group: 'Regular' },
    { value: 'response_generation', label: 'Response Generation', group: 'Regular' },
    { value: 'default_stage_selection', label: 'Default Stage Selection', group: 'Default' },
    { value: 'default_data_extraction', label: 'Default Data Extraction', group: 'Default' },
    { value: 'default_response_generation', label: 'Default Response Generation', group: 'Default' }
  ];
  
  // Extract variables from template text
  const extractVariables = (text) => {
    if (!text) return [];
    const matches = text.match(/\{([^}]+)\}/g) || [];
    return matches.map(match => match.slice(1, -1));
  };
  
  // Update variables whenever template text changes
  useEffect(() => {
    const variables = extractVariables(template.template_text);
    setTemplate(prev => ({
      ...prev,
      variables
    }));
  }, [template.template_text]);
  
  // Update template's agent_id when agentId changes
  useEffect(() => {
    if (agentId) {
      setTemplate(prev => ({
        ...prev,
        agent_id: agentId
      }));
    }
  }, [agentId]);
  
  // Fetch template data if editing an existing template
  useEffect(() => {
    const fetchTemplateData = async () => {
      if (templateId === 'new') return;
      
      if (!businessId) {
        showSnackbar('Business ID is required to fetch template', 'error');
        setShowBusinessIdInput(true);
        return;
      }
      
      if (!businessApiKey) {
        showSnackbar('Business API Key is required', 'error');
        setShowBusinessIdInput(true);
        return;
      }
      
      setIsLoading(true);
      try {
        const response = await fetch(`/templates/${templateId}?business_id=${businessId}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json',
            'Authorization': `Bearer ${businessApiKey}`
          },
          credentials: 'include'
        });
        
        if (!response.ok) {
          const errorData = await response.json().catch(() => ({}));
          throw new Error(errorData.error || errorData.message || 'Failed to fetch template');
        }
        
        const data = await response.json();
        console.log('Fetched template:', data);
        
        // Don't modify the template_type, preserve its original value
        setTemplate({
          template_id: data.template_id,
          template_name: data.template_name || '',
          template_text: data.template_text || '',
          template_type: data.template_type || 'stage_selection',
          template_description: data.template_description || '',
          variables: data.variables || extractVariables(data.template_text),
          agent_id: data.agent_id || ''
        });
      } catch (err) {
        console.error('Error fetching template:', err);
        showSnackbar(err.message, 'error');
      } finally {
        setIsLoading(false);
      }
    };
    
    fetchTemplateData();
  }, [templateId, businessId, businessApiKey]);
  
  // Validate credentials
  const validateCredentials = async () => {
    if (!businessId || !businessApiKey) {
      showSnackbar('Business ID and API Key are required', 'error');
      setShowBusinessIdInput(true);
      return false;
    }
    
    try {
      const response = await fetch(`/businesses/validate-credentials?business_id=${businessId}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Authorization': `Bearer ${businessApiKey}`
        },
        credentials: 'include'
      });
      
      if (!response.ok) {
        throw new Error('Invalid business ID or API key');
      }
      
      return true;
    } catch (err) {
      console.error('Credential validation error:', err);
      showSnackbar('Failed to validate credentials: ' + err.message, 'error');
      setShowBusinessIdInput(true);
      return false;
    }
  };
  
  // Save credentials
  const saveCredentials = async () => {
    if (!businessId || !businessApiKey) {
      showSnackbar('Please enter both Business ID and API Key', 'error');
      return;
    }
    
    const isValid = await validateCredentials();
    if (isValid) {
      // Save to both localStorage and cookies
      localStorage.setItem('businessId', businessId);
      localStorage.setItem('businessApiKey', businessApiKey);
      document.cookie = `businessId=${businessId}; path=/; max-age=86400`;
      document.cookie = `businessApiKey=${businessApiKey}; path=/; max-age=86400`;
      
      setShowBusinessIdInput(false);
      showSnackbar('Business credentials validated and saved', 'success');
    }
  };
  
  // Handle template preview
  const handlePreview = async () => {
    if (!template.template_text.trim()) {
      showSnackbar('Template text is empty', 'error');
      return;
    }
    
    setIsLoading(true);
    try {
      // Prepare preview request
      const previewRequest = {
        template_type: template.template_type,
        template_text: template.template_text,
        business_id: businessId,
        agent_id: agentId,
        context: {
          conversation_history: [
            { role: "user", content: "I'm interested in your products" },
            { role: "assistant", content: "I'd be happy to tell you about our products. What type are you interested in?" }
          ],
          extracted_data: {
            customer_name: "John Doe",
            product_interest: "shoes",
            budget: "$100"
          }
        }
      };
      
      let previewSuccess = false;
      let errorMessage = '';
      
      // Try multiple possible endpoints (for compatibility)
      const endpoints = [
        '/templates/preview',
        '/templates/render',
        '/render_template',
        '/api/templates/preview'
      ];
      
      let response = null;
      for (const endpoint of endpoints) {
        try {
          console.log(`Trying to preview template with endpoint: ${endpoint}`);
          
          response = await fetch(endpoint, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              'Accept': 'application/json',
              'Authorization': `Bearer ${businessApiKey}`
            },
            credentials: 'include',
            body: JSON.stringify(previewRequest),
          });
          
          if (response.ok) {
            const data = await response.json();
            console.log('Preview response:', data);
            setPreviewData(data);
            setShowPreview(true);
            previewSuccess = true;
            break;
          } else {
            const errData = await response.json().catch(() => ({}));
            errorMessage = errData.message || errData.error || `Failed with status ${response.status}`;
            console.warn(`Endpoint ${endpoint} failed: ${errorMessage}`);
          }
        } catch (endpointErr) {
          console.warn(`Endpoint ${endpoint} failed:`, endpointErr);
          errorMessage = endpointErr.message;
        }
      }
      
      // If no endpoint worked, use fallback preview
      if (!previewSuccess) {
        console.log('All endpoint attempts failed, using local preview');
        
        // Simple variable replacement for demonstration
        let previewText = template.template_text;
        
        // Replace variables with sample values
        template.variables.forEach(variable => {
          const sampleValue = `[Sample ${variable}]`;
          previewText = previewText.replace(new RegExp(`{${variable}}`, 'g'), sampleValue);
        });
        
        setPreviewData({ 
          rendered_text: previewText,
          message: "Using client-side preview (API endpoints not available)",
          is_fallback: true
        });
        setShowPreview(true);
        
        // Show notification
        showSnackbar(`Using fallback preview. Server error: ${errorMessage}`, 'warning');
      }
    } catch (err) {
      console.error('Error generating preview:', err);
      showSnackbar(`Failed to generate preview: ${err.message}`, 'error');
    } finally {
      setIsLoading(false);
    }
  };
  
  // Save template
  const saveTemplate = async () => {
    if (!template.template_name.trim()) {
      showSnackbar('Template name is required', 'error');
      return;
    }
    
    if (!template.template_text.trim()) {
      showSnackbar('Template text is required', 'error');
      return;
    }
    
    if (!businessId) {
      showSnackbar('Business ID is required', 'error');
      setShowBusinessIdInput(true);
      return;
    }
    
    if (!agentId) {
      showSnackbar('Agent ID is required', 'error');
      setShowAgentIdInput(true);
      return;
    }
    
    if (!businessApiKey) {
      showSnackbar('Business API Key is required', 'error');
      setShowBusinessIdInput(true);
      return;
    }
    
    setIsLoading(true);
    try {
      // Determine if we're creating a new template or updating an existing one
      const isNewTemplate = !template.template_id || template.template_id === 'new';
      
      // Prepare template data
      const templateData = {
        template_name: template.template_name,
        template_text: template.template_text,
        template_type: template.template_type,
        business_id: businessId,
        agent_id: agentId,
        template_description: template.template_description || '',
        variables: template.variables || []
      };
      
      console.log('Saving template data:', templateData);
      
      // Construct URL and method
      const method = isNewTemplate ? 'POST' : 'PUT';
      const url = isNewTemplate ? '/templates' : `/templates/${template.template_id}`;
      
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Authorization': `Bearer ${businessApiKey}`
        },
        credentials: 'include',
        body: JSON.stringify(templateData),
      });
      
      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.error || errorData.message || 'Failed to save template');
      }
      
      const result = await response.json();
      console.log('Template saved successfully:', result);
      
      // Update template ID if this was a new template
      if (isNewTemplate && result.template_id) {
        setTemplate(prev => ({
          ...prev,
          template_id: result.template_id
        }));
        
        // Update URL without reloading
        const newUrl = `/template-editor/${result.template_id}?business_id=${businessId}&agent_id=${agentId}`;
        window.history.pushState({}, '', newUrl);
      }
      
      showSnackbar(`Template ${isNewTemplate ? 'created' : 'updated'} successfully. ID: ${result.template_id || template.template_id}`, 'success');
      
      // Store a flag in localStorage to trigger refresh when returning to the templates list
      localStorage.setItem('template_updated', 'true');
      
      // Force a delay before navigating back to ensure the backend has time to process
      setTimeout(() => {
        // Navigate back to templates list with query params for both business and agent
        navigate(`/templates?business_id=${businessId}&agent_id=${agentId}&refresh=${Date.now()}`);
      }, 2000);
    } catch (err) {
      console.error('Error saving template:', err);
      showSnackbar(err.message, 'error');
    } finally {
      setIsLoading(false);
    }
  };
  
  // Navigate back to templates list
  const handleCancel = () => {
    navigate('/templates');
  };
  
  // Handle template type change
  const handleTemplateTypeChange = (e) => {
    const newType = e.target.value;
    setTemplate(prev => ({ 
      ...prev, 
      template_type: newType
    }));
  };
  
  // Snackbar functions
  const showSnackbar = (message, severity = 'success') => {
    setSnackbar({
      open: true,
      message,
      severity
    });
  };
  
  const handleCloseSnackbar = () => {
    setSnackbar(prev => ({
      ...prev,
      open: false
    }));
  };
  
  return (
    <Paper sx={{ p: 3, mb: 3, maxWidth: 1200, mx: 'auto' }}>
      {/* Back button and title */}
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
        <IconButton onClick={handleCancel} sx={{ mr: 1 }}>
          <ArrowBackIcon />
        </IconButton>
        <Typography variant="h5">
          {template.template_id && template.template_id !== 'new' ? 'Edit Template' : 'Create New Template'}
        </Typography>
        <Box sx={{ flexGrow: 1 }} />
        <Button
          variant="contained"
          color="primary"
          onClick={saveTemplate}
          disabled={isLoading || !template.template_name.trim() || !template.template_text.trim()}
          startIcon={isLoading ? <CircularProgress size={24} /> : <SaveIcon />}
        >
          Save Template
        </Button>
      </Box>
      
      {/* Business ID input section */}
      {(showBusinessIdInput || !businessId || !businessApiKey) && (
        <Box sx={{ mb: 3, p: 2, border: '1px dashed', borderColor: 'warning.main', borderRadius: 1 }}>
          <Typography variant="subtitle1" color="warning.main" gutterBottom>
            Business Credentials Required
          </Typography>
          <Grid container spacing={2}>
            <Grid item xs={12} md={5}>
              <TextField
                fullWidth
                label="Business ID"
                value={businessId}
                onChange={(e) => setBusinessId(e.target.value)}
                placeholder="Enter your business ID"
                helperText="Required for saving templates"
                required
              />
            </Grid>
            <Grid item xs={12} md={5}>
              <TextField
                fullWidth
                label="Business API Key"
                value={businessApiKey}
                onChange={(e) => setBusinessApiKey(e.target.value)}
                placeholder="Enter your business API key"
                helperText="Required for authentication"
                type="password"
                required
              />
            </Grid>
            <Grid item xs={12} md={2}>
              <Button 
                variant="contained" 
                color="primary"
                onClick={saveCredentials}
                fullWidth
                sx={{ height: '56px' }}
              >
                Validate & Save
              </Button>
            </Grid>
          </Grid>
        </Box>
      )}
      
      {/* Agent ID input section */}
      {(showAgentIdInput || !agentId) && (
        <Box sx={{ mb: 3, p: 2, border: '1px dashed', borderColor: 'warning.main', borderRadius: 1 }}>
          <Typography variant="subtitle1" color="warning.main" gutterBottom>
            Agent ID Required
          </Typography>
          <Grid container spacing={2}>
            <Grid item xs={12} md={5}>
              <TextField
                fullWidth
                label="Agent ID"
                value={agentId}
                onChange={(e) => setAgentId(e.target.value)}
                placeholder="Enter your agent ID"
                helperText="Required for saving templates"
                required
              />
            </Grid>
          </Grid>
        </Box>
      )}
      
      {/* Template information section */}
      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <TextField
            fullWidth
            label="Template Name"
            value={template.template_name}
            onChange={(e) => setTemplate(prev => ({ ...prev, template_name: e.target.value }))}
            required
            error={!template.template_name.trim()}
            helperText={!template.template_name.trim() ? "Template name is required" : ""}
            sx={{ mb: 2 }}
          />
          
          <FormControl fullWidth sx={{ mb: 2 }}>
            <InputLabel id="template-type-label">Template Type</InputLabel>
            <Select
              labelId="template-type-label"
              value={template.template_type}
              label="Template Type"
              onChange={handleTemplateTypeChange}
            >
              {templateTypes.map((type) => (
                <MenuItem key={type.value} value={type.value}>
                  {type.label}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
          
          <TextField
            fullWidth
            label="Template Description"
            value={template.template_description}
            onChange={(e) => setTemplate(prev => ({ ...prev, template_description: e.target.value }))}
            multiline
            rows={2}
            sx={{ mb: 2 }}
          />
          
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
            <Typography variant="subtitle1" sx={{ mr: 2 }}>
              Detected Variables:
            </Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
              {template.variables.length > 0 ? (
                template.variables.map((variable, index) => (
                  <Chip 
                    key={index} 
                    label={variable} 
                    color="primary" 
                    variant="outlined" 
                  />
                ))
              ) : (
                <Typography variant="body2" color="text.secondary">
                  No variables detected. Use {"{variable_name}"} syntax.
                </Typography>
              )}
            </Box>
          </Box>
          
          <Button
            variant="outlined"
            color="primary"
            onClick={handlePreview}
            disabled={!template.template_text.trim()}
            startIcon={<VisibilityIcon />}
            sx={{ mt: 2 }}
          >
            Preview Template
          </Button>
        </Grid>
        
        <Grid item xs={12} md={6}>
          <Typography variant="subtitle1" gutterBottom>
            Template Text
            <Tooltip title="Use {variable_name} syntax to create variables in your template">
              <IconButton size="small">
                <HelpOutlineIcon fontSize="small" />
              </IconButton>
            </Tooltip>
          </Typography>
          <TextField
            fullWidth
            multiline
            rows={16}
            value={template.template_text}
            onChange={(e) => setTemplate(prev => ({ ...prev, template_text: e.target.value }))}
            placeholder="Enter your template text here. Use {variable_name} syntax for variables."
            sx={{ 
              mb: 2, 
              fontFamily: 'monospace',
              '& .MuiInputBase-input': {
                fontFamily: 'monospace',
              }
            }}
          />
        </Grid>
      </Grid>
      
      {/* Preview Dialog */}
      <Dialog 
        open={showPreview} 
        onClose={() => setShowPreview(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>Template Preview</DialogTitle>
        <DialogContent>
          <Typography variant="subtitle1" gutterBottom>
            Template: {template.template_name}
          </Typography>
          <Typography variant="subtitle2" color="text.secondary" gutterBottom>
            Type: {template.template_type}
          </Typography>
          <Divider sx={{ my: 2 }} />
          <Box sx={{ 
            bgcolor: 'background.paper', 
            p: 2, 
            borderRadius: 1,
            border: '1px solid',
            borderColor: 'divider',
            maxHeight: '500px',
            overflow: 'auto',
            fontFamily: 'monospace',
            whiteSpace: 'pre-wrap',
            wordBreak: 'break-word'
          }}>
            {previewData?.rendered_text || previewData?.template_text || 'No preview data available'}
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setShowPreview(false)}>Close</Button>
        </DialogActions>
      </Dialog>
      
      {/* Snackbar for notifications */}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
      >
        <Alert onClose={handleCloseSnackbar} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Paper>
  );
};

export default TemplateEditor;
