import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import AgentSection from '../AgentSection';
import { 
  Snackbar, 
  Alert, 
  Box, 
  IconButton, 
  Typography, 
  Container, 
  Paper,
  Button,
  TextField,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import EditIcon from '@mui/icons-material/Edit';
import DescriptionIcon from '@mui/icons-material/Description';
import LogoutIcon from '@mui/icons-material/Logout';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';

// Define your backend API base URL
const API_BASE_URL = 'http://localhost:5000';

function BusinessDetailsView() {
  const navigate = useNavigate();
  const [businessData, setBusinessData] = useState(null);
  const [editFormData, setEditFormData] = useState({}); // State for form data
  const [agents, setAgents] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const [isEditingAgent, setIsEditingAgent] = useState(false);
  const [editingAgent, setEditingAgent] = useState(null);
  const [newAgentData, setNewAgentData] = useState({
    agent_name: '',
    agent_description: ''
  });

  // Get the business ID from localStorage instead of hardcoding
  const businessId = localStorage.getItem('businessId');

  const handleSnackbarOpen = (message, severity = 'success') => {
    setSnackbar({ open: true, message, severity });
  };

  const handleSnackbarClose = () => {
    setSnackbar({ ...snackbar, open: false });
  };

  const handleNavigateToLogin = () => {
    navigate('/login');
  };

  const handleLogout = () => {
    // Clear localStorage
    localStorage.removeItem('userId');
    localStorage.removeItem('businessId');
    localStorage.removeItem('businessApiKey');
    
    // Show logout message
    handleSnackbarOpen('Logged out successfully', 'success');
    
    // Navigate to login page
    navigate('/login');
  };

  // Navigate to stage management for a specific agent
  const navigateToStageManagement = (agentId, agentName) => {
    console.log(`Navigating to stage management for agent: ${agentName} (${agentId})`);
    
    // Store agent ID in localStorage for persistence
    localStorage.setItem('agentId', agentId);
    
    // Navigate to stage management with proper URL format
    navigate(`/stage-management/business_id=${businessId}/agent_id=${agentId}`);
  };

  // Handle edit agent
  const handleEditAgent = (agent) => {
    setEditingAgent(agent);
    setIsEditingAgent(true);
  };

  // Handle delete agent
  const handleDeleteAgent = async (agentId) => {
    if (!window.confirm('Are you sure you want to delete this agent?')) {
      return;
    }
    
    setIsLoading(true);
    try {
      const response = await fetch(`/agents/${agentId}`, {
        method: 'DELETE',
        credentials: 'include',
        headers: {
          'Accept': 'application/json',
        },
      });
      
      if (!response.ok) {
        throw new Error('Failed to delete agent');
      }
      
      // Refresh the agents list
      fetchAgents();
      handleSnackbarOpen('Agent deleted successfully', 'success');
    } catch (error) {
      console.error('Error deleting agent:', error);
      handleSnackbarOpen(`Error: ${error.message}`, 'error');
    } finally {
      setIsLoading(false);
    }
  };

  // Fetch business data
  useEffect(() => {
    const fetchData = async () => {
      if (!businessId) {
          setError("Please login to view business details.");
          setIsLoading(false);
          return;
      }
      setIsLoading(true);
      setError(null); // Clear previous errors
      console.log(`Fetching data for business ID: ${businessId}`);

      try {
        // --- Fetch Business Details ---
        const businessResponse = await fetch(`/businesses/${businessId}`, {
          method: 'GET',
          credentials: 'include', // Important to send cookies
          headers: {
            'Accept': 'application/json',
          },
        });

        if (!businessResponse.ok) {
          const errorData = await businessResponse.json();
          throw new Error(errorData.message || 'Failed to fetch business details');
        }

        const businessData = await businessResponse.json();
        console.log('Fetched business data:', businessData);
        setBusinessData(businessData);
        setEditFormData(businessData); // Initialize form data with current values
        
        // After business data is fetched, fetch agents
        fetchAgents();
      } catch (err) {
        console.error('Error fetching business data:', err);
        setError(err.message);
        setIsLoading(false);
      }
    };

    fetchData();
  }, [businessId]);
  
  // Fetch agents for the business
  const fetchAgents = async () => {
    try {
      const response = await fetch(`/agents?business_id=${businessId}`, {
        method: 'GET',
        credentials: 'include',
        headers: {
          'Accept': 'application/json',
        },
      });
      
      if (!response.ok) {
        throw new Error('Failed to fetch agents');
      }
      
      const data = await response.json();
      console.log('Fetched agents:', data);
      setAgents(data || []);
    } catch (error) {
      console.error('Error fetching agents:', error);
      handleSnackbarOpen(`Error fetching agents: ${error.message}`, 'error');
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (e) => {
      const { name, value } = e.target;
      setEditFormData(prevData => ({
          ...prevData,
          [name]: value
      }));
  };

  const handleEditToggle = () => {
      if (!isEditing && businessData) {
          // Reset form data to current business data when entering edit mode
          setEditFormData({
              business_name: businessData.business_name || '',
              business_description: businessData.business_description || '',
              address: businessData.address || '',
              phone_number: businessData.phone_number || '',
              website: businessData.website || ''
          });
      }
      setIsEditing(!isEditing);
      setError(null); // Clear errors when toggling mode
  };

  const handleSaveChanges = async (e) => {
      e.preventDefault(); // Prevent default form submission
      setIsLoading(true); // Indicate saving process
      setError(null);
      console.log("Saving changes:", editFormData);

      try {
           const response = await fetch(`/businesses/${businessId}`, {
               method: 'PUT',
               credentials: 'include',
               headers: {
                   'Content-Type': 'application/json',
                   'Accept': 'application/json',
               },
               body: JSON.stringify(editFormData)
           });

           if (!response.ok) {
                const errorData = await response.json().catch(() => ({}));
                throw new Error(`Error: ${errorData.error || errorData.message || 'Failed to save changes'}`);
           }

           const result = await response.json();
           console.log("Save successful:", result);

           // Update the business data with the new values
           setBusinessData(prevData => ({ ...prevData, ...editFormData }));
           
           // Exit edit mode
           setIsEditing(false);
           
           // Show success message
           handleSnackbarOpen('Business details updated successfully', 'success');

      } catch (err) {
          console.error("Error saving changes:", err);
          setError(err.message || "An unexpected error occurred while saving.");
          handleSnackbarOpen(err.message || "Failed to save changes", 'error');
      } finally {
          setIsLoading(false); // Finish loading indicator
      }
  };

  if (isLoading && !businessData) { // Show initial loading
    return (
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <Paper sx={{ p: 3, display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
          <Typography>Loading business details...</Typography>
        </Paper>
      </Container>
    );
  }

  // Show error prominently if one occurred
  if (error && !isEditing) { // Don't block editing form with load errors
    return (
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <Paper sx={{ p: 3 }}>
          <Typography color="error">Error: {error}</Typography>
          <Button 
            variant="contained" 
            color="primary" 
            onClick={handleNavigateToLogin} 
            sx={{ mt: 2 }}
          >
            Return to Login
          </Button>
        </Paper>
      </Container>
    );
  }

  if (!businessData && !isLoading) { // Handle case where loading finished but no data (and no specific load error)
      return <div>No business data found or failed to load.</div>;
  }
  // Render component content only if businessData is available
  if (!businessData) return null; // Or a placeholder/skeleton UI

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Paper sx={{ p: 3, mb: 4 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
          <IconButton onClick={handleLogout} sx={{ mr: 1 }}>
            <ArrowBackIcon />
          </IconButton>
          <Typography variant="h5" gutterBottom sx={{ flexGrow: 1, mb: 0 }}>
            Business Details
          </Typography>
        </Box>
        
        {/* Display save errors specifically above the form */}
        {isEditing && error && <div style={{ color: 'red', marginBottom: '10px' }}>Save Error: {error}</div>}
        {/* Indicate loading state during save */}
        {isEditing && isLoading && <div>Saving...</div>}

        {isEditing ? (
          // --- Edit Mode ---
          <form onSubmit={handleSaveChanges}>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                  <TextField
                      id="business_name"
                      name="business_name"
                      label="Business Name"
                      value={editFormData.business_name}
                      onChange={handleInputChange}
                      required
                      fullWidth
                  />
                  <TextField
                      id="business_description"
                      name="business_description"
                      label="Description"
                      value={editFormData.business_description}
                      onChange={handleInputChange}
                      multiline
                      rows={4}
                      fullWidth
                  />
                  <TextField
                      id="address"
                      name="address"
                      label="Address"
                      value={editFormData.address}
                      onChange={handleInputChange}
                      fullWidth
                  />
                  <TextField
                      id="phone_number"
                      name="phone_number"
                      label="Phone"
                      value={editFormData.phone_number}
                      onChange={handleInputChange}
                      fullWidth
                  />
                  <TextField
                      id="website"
                      name="website"
                      label="Website"
                      value={editFormData.website}
                      onChange={handleInputChange}
                      fullWidth
                  />
                  <Box sx={{ display: 'flex', gap: 2, mt: 2, justifyContent: 'flex-end' }}>
                      <Button 
                          type="button" 
                          variant="outlined"
                          onClick={handleEditToggle} 
                          disabled={isLoading}
                      >
                          Cancel
                      </Button>
                      <Button 
                          type="submit" 
                          variant="contained"
                          color="primary"
                          disabled={isLoading}
                      >
                          {isLoading ? 'Saving...' : 'Save Changes'}
                      </Button>
                  </Box>
              </Box>
          </form>
        ) : (
          // --- View Mode ---
          <div>
            <p><strong>Name:</strong> {businessData.business_name}</p>
            <p><strong>Description:</strong> {businessData.business_description || '-'}</p>
            <p><strong>Address:</strong> {businessData.address || '-'}</p>
            <p><strong>Phone:</strong> {businessData.phone_number || '-'}</p>
            <p><strong>Website:</strong> {businessData.website ? <a href={businessData.website} target="_blank" rel="noopener noreferrer">{businessData.website}</a> : '-'}</p>
            <hr/>
            <p><strong>Business ID:</strong> {businessData.business_id}</p>
            <p><strong>Owner ID:</strong> {businessData.owner_id}</p>
            <Box sx={{ display: 'flex', gap: 2, mt: 2 }}>
              <Button 
                variant="contained"
                color="primary"
                onClick={() => setIsEditing(true)}
                startIcon={<EditIcon />}
                sx={{ mr: 1 }}
              >
                Edit Details
              </Button>
              <Button 
                variant="outlined"
                color="secondary"
                onClick={() => navigate('/templates')}
                startIcon={<DescriptionIcon />}
              >
                Manage Templates
              </Button>
              <Button 
                variant="outlined"
                color="error"
                onClick={handleLogout}
                startIcon={<LogoutIcon />}
                sx={{ ml: 'auto' }}
              >
                Logout
              </Button>
            </Box>
          </div>
        )}

        <hr />

        {/* Replace the old agent list with our AgentSection component */}
        <Typography variant="h6" gutterBottom>
          Agents
        </Typography>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
          <Button 
            variant="contained" 
            color="primary" 
            onClick={() => {
              setEditingAgent(null);
              setNewAgentData({
                agent_name: '',
                agent_description: ''
              });
              setIsEditingAgent(true);
            }}
            startIcon={<AddIcon />}
          >
            Add Agent
          </Button>
        </Box>
        {agents.length === 0 ? (
          <Typography variant="body1">No agents found.</Typography>
        ) : (
          <List>
            {agents.map((agent) => (
              <ListItem key={agent.agent_id} 
                onClick={() => navigateToStageManagement(agent.agent_id, agent.agent_name)}
                sx={{ 
                  cursor: 'pointer', 
                  border: '1px solid #e0e0e0', 
                  borderRadius: 1, 
                  mb: 1,
                  '&:hover': {
                    backgroundColor: '#f5f5f5'
                  }
                }}
              >
                <ListItemText 
                  primary={agent.agent_name} 
                  secondary={
                    <>
                      <Typography component="span" variant="body2" color="text.primary">
                        {agent.agent_description || 'No description'}
                      </Typography>
                      <Typography component="div" variant="caption" color="text.secondary">
                        Agent ID: {agent.agent_id}
                      </Typography>
                    </>
                  }
                />
                <ListItemSecondaryAction>
                  <IconButton edge="end" aria-label="edit" onClick={(e) => {
                    e.stopPropagation();
                    handleEditAgent(agent);
                  }}>
                    <EditIcon />
                  </IconButton>
                  <IconButton edge="end" aria-label="delete" onClick={(e) => {
                    e.stopPropagation();
                    handleDeleteAgent(agent.agent_id);
                  }}>
                    <DeleteIcon />
                  </IconButton>
                </ListItemSecondaryAction>
              </ListItem>
            ))}
          </List>
        )}
        
        {/* Agent Edit Dialog */}
        <Dialog open={isEditingAgent} onClose={() => setIsEditingAgent(false)} maxWidth="sm" fullWidth>
          <DialogTitle>{editingAgent ? 'Edit Agent' : 'Create New Agent'}</DialogTitle>
          <DialogContent>
            <TextField
              autoFocus
              margin="dense"
              id="agent_name"
              label="Agent Name"
              type="text"
              fullWidth
              value={editingAgent ? editingAgent.agent_name : newAgentData.agent_name}
              onChange={(e) => {
                if (editingAgent) {
                  setEditingAgent({
                    ...editingAgent,
                    agent_name: e.target.value
                  });
                } else {
                  setNewAgentData({
                    ...newAgentData,
                    agent_name: e.target.value
                  });
                }
              }}
              required
            />
            <TextField
              margin="dense"
              id="agent_description"
              label="Description"
              type="text"
              fullWidth
              multiline
              rows={4}
              value={editingAgent ? editingAgent.agent_description : newAgentData.agent_description}
              onChange={(e) => {
                if (editingAgent) {
                  setEditingAgent({
                    ...editingAgent,
                    agent_description: e.target.value
                  });
                } else {
                  setNewAgentData({
                    ...newAgentData,
                    agent_description: e.target.value
                  });
                }
              }}
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setIsEditingAgent(false)} color="primary">
              Cancel
            </Button>
            <Button 
              onClick={async () => {
                try {
                  const agentData = editingAgent ? editingAgent : newAgentData;
                  
                  if (!agentData.agent_name) {
                    handleSnackbarOpen('Agent name is required', 'error');
                    return;
                  }
                  
                  const url = editingAgent 
                    ? `/agents/${editingAgent.agent_id}` 
                    : '/agents';
                  
                  const method = editingAgent ? 'PUT' : 'POST';
                  
                  const requestBody = {
                    ...agentData,
                    business_id: businessId
                  };
                  
                  const response = await fetch(url, {
                    method,
                    headers: {
                      'Content-Type': 'application/json',
                      'Accept': 'application/json',
                    },
                    credentials: 'include',
                    body: JSON.stringify(requestBody)
                  });
                  
                  if (!response.ok) {
                    throw new Error('Failed to save agent');
                  }
                  
                  handleSnackbarOpen(
                    editingAgent ? 'Agent updated successfully' : 'Agent created successfully', 
                    'success'
                  );
                  
                  setIsEditingAgent(false);
                  fetchAgents();
                } catch (error) {
                  console.error('Error saving agent:', error);
                  handleSnackbarOpen(`Error: ${error.message}`, 'error');
                }
              }} 
              color="primary"
              variant="contained"
            >
              {editingAgent ? 'Update' : 'Create'}
            </Button>
          </DialogActions>
        </Dialog>
        
        {/* Snackbar for notifications */}
        <Snackbar 
          open={snackbar.open}
          autoHideDuration={6000}
          onClose={handleSnackbarClose}
          anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
        >
          <Alert onClose={handleSnackbarClose} severity={snackbar.severity} sx={{ width: '100%' }}>
            {snackbar.message}
          </Alert>
        </Snackbar>
      </Paper>
    </Container>
  );
}

export default BusinessDetailsView;
