// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { Container, Snackbar, Alert } from '@mui/material';
import Configuration from './components/Configuration';
import Login from './components/Login';

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [apiKey, setApiKey] = useState('');
    const [userId, setUserId] = useState('');
    const [businessId, setBusinessId] = useState('');
    const [businessApiKey, setBusinessApiKey] = useState('');
    const [snackbar, setSnackbar] = useState({
        open: false,
        message: '',
        severity: 'info'
    });

    const handleSnackbarOpen = (message, severity = 'info') => {
        setSnackbar({
            open: true,
            message,
            severity
        });
    };

    const handleSnackbarClose = () => {
        setSnackbar(prev => ({
            ...prev,
            open: false
        }));
    };

    // Check authentication status on mount
    useEffect(() => {
        const checkAuth = async () => {
            // Skip validation if we don't have stored credentials
            const storedApiKey = localStorage.getItem('apiKey');
            const storedUserId = localStorage.getItem('userId');
            const storedBusinessId = localStorage.getItem('businessId');
            const storedBusinessApiKey = localStorage.getItem('businessApiKey');

            if (!storedApiKey || !storedUserId || !storedBusinessId || !storedBusinessApiKey) {
                setIsAuthenticated(false);
                return;
            }

            // Set the stored values
            setApiKey(storedApiKey);
            setUserId(storedUserId);
            setBusinessId(storedBusinessId);
            setBusinessApiKey(storedBusinessApiKey);

            try {
                const response = await fetch('/validate_config', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    credentials: 'include',
                    body: JSON.stringify({
                        apiKey: storedApiKey,
                        userId: storedUserId,
                        businessId: storedBusinessId,
                        businessApiKey: storedBusinessApiKey
                    })
                });

                if (!response.ok) {
                    const errorData = await response.json();
                    throw new Error(errorData.message || 'Authentication failed');
                }

                const data = await response.json();
                if (data.isValid) {
                    setIsAuthenticated(true);
                } else {
                    throw new Error(data.error || 'Authentication failed');
                }
            } catch (error) {
                console.error('Auth check failed:', error);
                setIsAuthenticated(false);
                handleSnackbarOpen(error.message || 'Authentication failed', 'error');
                // Clear stored credentials on authentication failure
                localStorage.removeItem('apiKey');
                localStorage.removeItem('userId');
                localStorage.removeItem('businessId');
                localStorage.removeItem('businessApiKey');
            }
        };
        checkAuth();
    }, [handleSnackbarOpen]);

    return (
        <Router>
            <Container maxWidth="md">
                <Routes>
                    <Route 
                        path="/login" 
                        element={
                            isAuthenticated ? 
                            <Navigate to="/" replace /> : 
                            <Login 
                                setIsAuthenticated={setIsAuthenticated}
                                handleSnackbarOpen={handleSnackbarOpen}
                            />
                        } 
                    />
                    <Route 
                        path="/" 
                        element={
                            isAuthenticated ? (
                                <Configuration
                                    apiKey={apiKey}
                                    setApiKey={setApiKey}
                                    userId={userId}
                                    setUserId={setUserId}
                                    businessId={businessId}
                                    setBusinessId={setBusinessId}
                                    businessApiKey={businessApiKey}
                                    setBusinessApiKey={setBusinessApiKey}
                                    handleSnackbarOpen={handleSnackbarOpen}
                                    setIsAuthenticated={setIsAuthenticated}
                                />
                            ) : (
                                <Navigate to="/login" replace />
                            )
                        } 
                    />
                </Routes>
                <Snackbar 
                    open={snackbar.open} 
                    autoHideDuration={6000} 
                    onClose={handleSnackbarClose}
                >
                    <Alert 
                        onClose={handleSnackbarClose} 
                        severity={snackbar.severity}
                    >
                        {snackbar.message}
                    </Alert>
                </Snackbar>
            </Container>
        </Router>
    );
}

export default App;
