import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { cachedFetch } from '../utils/fetchUtils';
import {
  Box,
  Paper,
  Typography,
  TextField,
  Button,
  Tabs,
  Tab,
  Grid,
  Alert,
  Snackbar,
  IconButton,
  Tooltip,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  CircularProgress,
  Divider,
  Chip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions
} from '@mui/material';
import SaveIcon from '@mui/icons-material/Save';
import PreviewIcon from '@mui/icons-material/Preview';
import RestartAltIcon from '@mui/icons-material/RestartAlt';
import HelpOutlineIcon from '@mui/icons-material/HelpOutline';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';

// Global request cache to prevent duplicate API calls
const apiCache = {
  cache: {},
  get: function(url) {
    return this.cache[url]?.data;
  },
  set: function(url, data, ttl = 60000) { // Default TTL: 1 minute
    this.cache[url] = {
      data,
      expiry: Date.now() + ttl
    };
  },
  isValid: function(url) {
    const item = this.cache[url];
    return item && item.expiry > Date.now();
  },
  clear: function() {
    this.cache = {};
  }
};

// TabPanel component for the tabs
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`tabpanel-${index}`}
      aria-labelledby={`tab-${index}`}
      {...other}
      style={{ padding: '20px 0' }}
    >
      {value === index && (
        <Box>
          {children}
        </Box>
      )}
    </div>
  );
}

const StageEditor = () => {
  const { stageId: urlStageId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  
  // Add stageId state
  const [stageId, setStageId] = useState(urlStageId || 'new');
  
  // Check local storage and cookies for business ID and API key
  const getStoredValue = (key) => {
    // Try localStorage first
    const localStorageValue = localStorage.getItem(key);
    if (localStorageValue) return localStorageValue;
    
    // Then try cookies
    return document.cookie
      .split('; ')
      .find(row => row.startsWith(`${key}=`))
      ?.split('=')[1];
  };
  
  const queryParams = new URLSearchParams(location.search);
  const queryBusinessId = queryParams.get('business_id');
  
  // Location state values as fallback
  const { businessId: locationBusinessId, agentId: locationAgentId, isNewStage } = location.state || {};
  
  // Get stored values
  const storedBusinessId = getStoredValue('businessId');
  const storedApiKey = getStoredValue('businessApiKey');
  
  // Use query parameters first, then location state, then stored values
  const [businessId, setBusinessId] = useState(queryBusinessId || locationBusinessId || storedBusinessId || '');
  const [businessApiKey, setBusinessApiKey] = useState(storedApiKey || '');
  const [showBusinessIdInput, setShowBusinessIdInput] = useState(!businessId || !businessApiKey);
  
  const agentId = queryParams.get('agent_id') || locationAgentId;
  
  const [stage, setStage] = useState(null);
  const [stageName, setStageName] = useState('');
  const [stageDescription, setStageDescription] = useState('');
  const [tabValue, setTabValue] = useState(0);
  const [templates, setTemplates] = useState({
    stage_selection: { id: '', text: '', type: '' },
    data_extraction: { id: '', text: '', type: '' },
    response_generation: { id: '', text: '', type: '' }
  });
  const [availableTemplates, setAvailableTemplates] = useState({
    stage_selection: [],
    data_extraction: [],
    response_generation: [],
    default_stage_selection: [],
    default_data_extraction: [],
    default_response_generation: []
  });
  const [variables, setVariables] = useState({
    stage_selection: [],
    data_extraction: [],
    response_generation: []
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [previewData, setPreviewData] = useState(null);
  const [previewLoading, setPreviewLoading] = useState(false);
  const [previewError, setPreviewError] = useState('');
  const [showPreview, setShowPreview] = useState(false);
  
  // Add template saving loading states
  const [savingTemplates, setSavingTemplates] = useState({
    stage_selection: false,
    data_extraction: false,
    response_generation: false
  });

  // Add template save confirmation dialog
  const [showConfirmDialog, setShowConfirmDialog] = useState(false);
  const [templateToSave, setTemplateToSave] = useState('');
  const [saveAsDefault, setSaveAsDefault] = useState(false);

  // Track if component is mounted
  const isMounted = useRef(true);
  
  // Clear any previous fetch timeouts on unmount
  useEffect(() => {
    return () => {
      isMounted.current = false;
    };
  }, []);

  // Function to open the confirmation dialog
  const confirmSaveTemplate = (type, asDefault = false) => {
    setTemplateToSave(type);
    setSaveAsDefault(asDefault);
    setShowConfirmDialog(true);
  };

  // Function to handle dialog confirm
  const handleConfirmSave = async () => {
    setShowConfirmDialog(false);
    if (templateToSave) {
      await saveTemplate(templateToSave, saveAsDefault);
    }
  };

  // Add validation function for credentials
  const validateCredentials = useCallback(async () => {
    if (!businessId || !businessApiKey) {
      setError('Business ID and API Key are required');
      setShowBusinessIdInput(true);
      return false;
    }

    try {
      // Test credentials with the validate-credentials endpoint
      const response = await fetch(`/businesses/validate-credentials?business_id=${businessId}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Authorization': businessApiKey,
          'X-API-Key': businessApiKey,
          'API-Key': businessApiKey
        },
        credentials: 'include'
      });

      if (!response.ok) {
        throw new Error('Invalid business ID or API key');
      }

      return true;
    } catch (err) {
      console.error('Credential validation error:', err);
      setError('Failed to validate credentials: ' + err.message);
      setShowBusinessIdInput(true);
      return false;
    }
  }, [businessId, businessApiKey]);

  // Add a throttled fetch for all templates with proper dependencies
  const fetchAllTemplates = useCallback(async () => {
    const { businessId, businessApiKey } = getStoredCredentials();
    
    if (!businessId || !businessApiKey || !isMounted.current) {
      return;
    }
    
    console.log('Fetching templates for business:', businessId);
    
    try {
      const response = await cachedFetch('/templates', {
        method: 'POST',
        body: { business_id: businessId }
      });
      
      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        console.error('Error fetching templates:', response.status, errorData);
        throw new Error(`Failed to fetch templates: ${response.status} ${errorData.message || ''}`);
      }
      
      const templates = await response.json();
      console.log('Fetched templates:', templates);
      
      // Organize templates by type
      const templatesByType = templates.reduce((acc, template) => {
        const type = template.template_type || 'other';
        if (!acc[type]) {
          acc[type] = [];
        }
        acc[type].push(template);
        return acc;
      }, {});
      
      if (isMounted.current) {
        setAvailableTemplates(templatesByType);
      }
    } catch (error) {
      console.error('Error in fetchAllTemplates:', error);
      if (isMounted.current) {
        setError(`Failed to fetch templates: ${error.message}`);
      }
    }
  }, []);

  useEffect(() => {
    let isActive = true;
    
    const initializeComponent = async () => {
      try {
        // Start with loading state
        setIsLoading(true);
        
        // Check if we have the required credentials
        if (!businessId || !businessApiKey) {
          setShowBusinessIdInput(true);
          setIsLoading(false);
          return;
        }
        
        // Validate credentials before proceeding
        const isValid = await validateCredentials();
        if (!isActive) return;
        
        if (!isValid) {
          setIsLoading(false);
          return;
        }
        
        // Store validated credentials
        localStorage.setItem('businessId', businessId);
        localStorage.setItem('businessApiKey', businessApiKey);
        document.cookie = `businessId=${businessId}; path=/; max-age=86400`;
        document.cookie = `businessApiKey=${businessApiKey}; path=/; max-age=86400`;
        
        // Load stage data if needed
        if (stageId === 'new' || isNewStage) {
          // For new stage, just set up defaults
          if (isActive) {
            setStage({
              stage_name: '',
              stage_description: '',
              business_id: businessId,
              agent_id: agentId,
              stage_selection_template_id: null,
              data_extraction_template_id: null,
              response_generation_template_id: null,
              stage_config: { order: 0 }
            });
          }
        } else if (stageId) {
          // Fetch existing stage
          await fetchStage(stageId);
        }
        
        // Always fetch templates with validated credentials
        if (isActive) {
          await fetchAllTemplates();
        }
      } catch (err) {
        console.error('Error initializing component:', err);
        if (isActive) {
          setError('Error initializing: ' + err.message);
        }
      } finally {
        if (isActive) {
          setIsLoading(false);
        }
      }
    };
    
    initializeComponent();
    
    return () => {
      isActive = false;
    };
  }, [stageId, businessId, businessApiKey, agentId, isNewStage, fetchAllTemplates, validateCredentials]);

  // Fetch specific stage data
  const fetchStage = async (id) => {
    if (!isMounted.current || !businessId || !businessApiKey) return;
    
    setIsLoading(true);
    try {
      const response = await cachedFetch(`/stages/${id}`);
      
      if (!response.ok) throw new Error('Failed to fetch stage');
      
      const data = await response.json();
      
      if (isMounted.current) {
        setStage(data);
        setStageName(data.stage_name || '');
        setStageDescription(data.stage_description || '');
        
        // For each template ID in the stage, fetch the template content
        if (data.stage_selection_template_id) {
          fetchTemplate(data.stage_selection_template_id, 'stage_selection');
        }
        if (data.data_extraction_template_id) {
          fetchTemplate(data.data_extraction_template_id, 'data_extraction');
        }
        if (data.response_generation_template_id) {
          fetchTemplate(data.response_generation_template_id, 'response_generation');
        }
      }
    } catch (err) {
      if (isMounted.current) {
        setError(err.message);
      }
    } finally {
      if (isMounted.current) {
        setIsLoading(false);
      }
    }
  };

  const fetchTemplate = useCallback(async (templateId, type) => {
    if (!isMounted.current || !businessId || !businessApiKey) return;
    
    try {
      console.log(`Fetching template ${templateId} for type ${type}`);
      
      const response = await cachedFetch(`/templates/${templateId}`, {}, 5 * 60 * 1000, {
        headers: {
          'Authorization': `Bearer ${businessApiKey}`,
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        }
      });
      
      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || errorData.error || `Failed to fetch ${type} template`);
      }
      
      const data = await response.json();
      console.log(`Fetched template data:`, data);
      
      if (isMounted.current) {
        setTemplates(prev => ({
          ...prev,
          [type]: {
            id: data.template_id,
            text: data.template_text,
            type: data.template_type
          }
        }));
        
        // Extract variables from template
        setVariables(prev => ({
          ...prev,
          [type]: extractVariables(data.template_text)
        }));
      }
    } catch (err) {
      console.error(`Error fetching template ${templateId}:`, err);
      if (isMounted.current) {
        setError(err.message);
      }
    }
  }, [businessId, businessApiKey]);

  const extractVariables = (template) => {
    if (!template) return [];
    const matches = template.match(/\{([^}]+)\}/g) || [];
    return matches.map(match => match.slice(1, -1));
  };

  const handleTemplateChange = (type, value) => {
    setTemplates(prev => ({
      ...prev,
      [type]: {
        ...prev[type],
        text: value
      }
    }));
    // Update variables when template changes
    setVariables(prev => ({
      ...prev,
      [type]: extractVariables(value)
    }));
  };

  const handleTemplateSelect = async (type, templateId) => {
    // If selecting "None", clear the template
    if (!templateId) {
      setTemplates(prev => ({
        ...prev,
        [type]: { id: '', text: '', type: '' }
      }));
      setVariables(prev => ({
        ...prev,
        [type]: []
      }));
      return;
    }
    
    // Fetch the selected template
    await fetchTemplate(templateId, type);
  };

  const handleApplyDefaultTemplate = async (type) => {
    // Map regular types to their default counterparts
    const defaultTypeMap = {
      'stage_selection': 'default_stage_selection',
      'data_extraction': 'default_data_extraction',
      'response_generation': 'default_response_generation'
    };
    
    const defaultType = defaultTypeMap[type];
    
    // Find default templates of the corresponding type
    const defaultTemplates = availableTemplates[defaultType];
    
    if (defaultTemplates.length === 0) {
      setError(`No default templates available for ${type}`);
      return;
    }
    
    // For simplicity, use the first default template
    const defaultTemplate = defaultTemplates[0];
    
    // Apply the default template text (but keep the original ID)
    setTemplates(prev => ({
      ...prev,
      [type]: {
        ...prev[type],
        text: defaultTemplate.template_text,
        type: type // Keep the original type, not the default type
      }
    }));
    
    // Update variables
    setVariables(prev => ({
      ...prev,
      [type]: extractVariables(defaultTemplate.template_text)
    }));
    
    setSuccess(`Applied default template for ${type}`);
  };

  const handlePreview = async (templateType) => {
    try {
      setPreviewLoading(true);
      setPreviewError('');
      
      const templateText = templates[templateType].text;
      if (!templateText.trim()) {
        setPreviewError('Template text is empty');
        return;
      }
      
      if (!businessApiKey) {
        setPreviewError('Business API key is required but was not found');
        setShowBusinessIdInput(true);
        return;
      }
      
      // Prepare preview request
      const previewRequest = {
        template_type: templateType,
        template_text: templateText,
        context: {
          conversation_history: [
            { role: "user", content: "I'm interested in your products" },
            { role: "assistant", content: "I'd be happy to tell you about our products. What type are you interested in?" }
          ],
          extracted_data: {
            customer_name: "John Doe",
            product_interest: "shoes",
            budget: "$100"
          }
        }
      };
      
      // Try multiple possible endpoints (for compatibility)
      const endpoints = [
        '/templates/render',
        '/render_template',
        '/api/templates/preview'
      ];
      
      let response = null;
      for (const endpoint of endpoints) {
        try {
          console.log(`Trying to preview template with endpoint: ${endpoint}`);
          
          response = await cachedFetch(endpoint, {
            method: 'POST',
            body: JSON.stringify(previewRequest)
          });
          
          if (response.ok) break;
        } catch (endpointErr) {
          console.warn(`Endpoint ${endpoint} failed:`, endpointErr);
        }
      }
      
      // If we got a successful response from any endpoint
      if (response && response.ok) {
        const data = await response.json();
        console.log('Preview response:', data);
        setPreviewData(data);
        setShowPreview(true);
      } else {
        // Local fallback preview if no endpoint worked
        console.log('All endpoint attempts failed, using local preview');
        
        // Simple variable replacement for demonstration
        let previewText = templateText;
        const variables = extractVariables(templateText);
        
        // Replace variables with sample values
        variables.forEach(variable => {
          const sampleValue = `[Sample ${variable}]`;
          previewText = previewText.replace(new RegExp(`{{\\s*${variable}\\s*}}`, 'g'), sampleValue);
        });
        
        setPreviewData({ rendered_text: previewText });
        setShowPreview(true);
      }
    } catch (err) {
      console.error('Error generating preview:', err);
      setPreviewError(`Failed to generate preview: ${err.message}`);
    } finally {
      setPreviewLoading(false);
    }
  };

  // Update saveTemplate function to use cachedFetch
  const saveTemplate = async (type, asDefault = false) => {
    try {
      setSavingTemplates(prev => ({ ...prev, [type]: true }));
      
      if (!businessId) {
        setError('Business ID is required to save template');
        return;
      }

      const template = templates[type];
      if (!template.text.trim()) {
        setError(`Template text is required for ${type}`);
        return;
      }

      if (!businessApiKey) {
        setError('Business API key is required but was not found');
        setShowBusinessIdInput(true);
        return;
      }

      // If there's an ID, update existing template, otherwise create new
      const url = template.id && !asDefault ? `/templates/${template.id}` : '/templates';
      const method = template.id && !asDefault ? 'PUT' : 'POST';

      // Map regular types to their default counterparts if saving as default
      const templateType = asDefault ? `default_${type}` : type;

      // Extract variables from template text
      const variables = extractVariables(template.text);

      const templateData = {
        template_name: `${stageName || 'New Stage'} - ${asDefault ? 'Default ' : ''}${type.replace(/_/g, ' ')}`,
        template_text: template.text,
        template_type: templateType,
        business_id: businessId,
        variables: variables,
        template_description: `Template for ${type.replace(/_/g, ' ')} ${asDefault ? '(Default)' : ''}`
      };

      console.log(`Saving ${asDefault ? 'default ' : ''}${type} template:`, templateData);

      const response = await cachedFetch(url, {
        method,
        body: templateData,
        headers: {
          'Authorization': `Bearer ${businessApiKey}`,
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        }
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || errorData.error || `Failed to save ${asDefault ? 'default ' : ''}${type} template`);
      }

      const data = await response.json();
      console.log(`Template ${asDefault ? 'default ' : ''}${type} saved successfully:`, data);
      
      // Only update the current template ID if not saving as default
      if (!asDefault && !template.id) {
        setTemplates(prev => ({
          ...prev,
          [type]: {
            ...prev[type],
            id: data.template_id
          }
        }));
      }

      setSuccess(`${asDefault ? 'Default ' : ''}${type} template saved successfully`);
      
      // After successful save, refresh the available templates
      fetchAllTemplates();
      
      return data;
    } catch (err) {
      console.error(`Error saving ${type} template:`, err);
      setError(`Error saving template: ${err.message}`);
      throw err;
    } finally {
      setSavingTemplates(prev => ({ ...prev, [type]: false }));
    }
  };

  // Update handleSave function
  const handleSave = async () => {
    if (!stage) return;
    
    try {
      // Validate stage name
      if (!stageName.trim()) {
        setError('Stage name is required');
        return;
      }
      
      // Ensure businessId is available
      if (!businessId) {
        setError('Business ID is required but was not found');
        console.error('Business ID is missing', { businessId, locationBusinessId, queryBusinessId });
        return;
      }
      
      if (!businessApiKey) {
        setError('Business API key is required but was not found');
        setShowBusinessIdInput(true);
        return;
      }

      // First, save all templates that have text but no ID
      const templatePromises = [];
      const savedTemplateIds = {};
      
      if (templates.stage_selection.text && !templates.stage_selection.id) {
        templatePromises.push(
          saveTemplate('stage_selection')
            .then(response => {
              if (response && response.template_id) {
                savedTemplateIds.stage_selection = response.template_id;
                setTemplates(prev => ({
                  ...prev,
                  stage_selection: { ...prev.stage_selection, id: response.template_id }
                }));
                return response.template_id;
              }
              throw new Error('Failed to save stage selection template');
            })
        );
      }
      
      if (templates.data_extraction.text && !templates.data_extraction.id) {
        templatePromises.push(
          saveTemplate('data_extraction')
            .then(response => {
              if (response && response.template_id) {
                savedTemplateIds.data_extraction = response.template_id;
                setTemplates(prev => ({
                  ...prev,
                  data_extraction: { ...prev.data_extraction, id: response.template_id }
                }));
                return response.template_id;
              }
              throw new Error('Failed to save data extraction template');
            })
        );
      }
      
      if (templates.response_generation.text && !templates.response_generation.id) {
        templatePromises.push(
          saveTemplate('response_generation')
            .then(response => {
              if (response && response.template_id) {
                savedTemplateIds.response_generation = response.template_id;
                setTemplates(prev => ({
                  ...prev,
                  response_generation: { ...prev.response_generation, id: response.template_id }
                }));
                return response.template_id;
              }
              throw new Error('Failed to save response generation template');
            })
        );
      }

      // Wait for all templates to be saved
      if (templatePromises.length > 0) {
        setSuccess(`Saving ${templatePromises.length} template(s) first...`);
        await Promise.all(templatePromises);
      }
      
      // Prepare stage data with template IDs
      const stageData = {
        stage_name: stageName,
        stage_description: stageDescription,
        business_id: businessId,
        agent_id: agentId || null,
        stage_type: 'conversation',
        stage_selection_template_id: savedTemplateIds.stage_selection || templates.stage_selection.id || null,
        data_extraction_template_id: savedTemplateIds.data_extraction || templates.data_extraction.id || null,
        response_generation_template_id: savedTemplateIds.response_generation || templates.response_generation.id || null,
        stage_config: {
          order: 0
        }
      };

      // Determine if this is a create or update operation
      const url = stageId === 'new' ? '/stages' : `/stages/${stageId}`;
      const method = stageId === 'new' ? 'POST' : 'PUT';

      console.log('Saving stage with data:', stageData);
      
      const response = await cachedFetch(url, {
        method,
        body: stageData
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || errorData.error || 'Failed to save stage');
      }

      const data = await response.json();
      console.log('Stage saved successfully:', data);
      
      // Update the stage ID if this was a new stage
      if (stageId === 'new' && data.stage_id) {
        setStageId(data.stage_id);
        setStage(prev => ({ ...prev, stage_id: data.stage_id }));
      }
      
      setSuccess('Stage saved successfully');
      
      // Navigate back to the stages list
      navigate('/stages');
    } catch (err) {
      console.error('Error saving stage:', err);
      setError(err.message || 'Failed to save stage');
    }
  };

  const handleCancel = () => {
    // Navigate back to stage manager
    let backUrl;
    if (businessId && agentId) {
      backUrl = `/stage-management/business_id=${businessId}/agent_id=${agentId}`;
    } else if (businessId) {
      backUrl = `/stage-management/business_id=${businessId}`;
    } else {
      backUrl = '/stage-management';
    }
    navigate(backUrl);
  };

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  // Update the credentials save function
  const saveCredentials = () => {
    if (!businessId || !businessApiKey) {
      setError('Please enter both Business ID and API Key');
      return;
    }
    
    // Validate credentials first
    validateCredentials().then(isValid => {
      if (isValid) {
        // Save to both localStorage and cookies
        localStorage.setItem('businessId', businessId);
        localStorage.setItem('businessApiKey', businessApiKey);
        document.cookie = `businessId=${businessId}; path=/; max-age=86400`;
        document.cookie = `businessApiKey=${businessApiKey}; path=/; max-age=86400`;
        
        setShowBusinessIdInput(false);
        setSuccess('Business credentials validated and saved');
        
        // Reload templates
        fetchAllTemplates();
      }
    });
  };

  return (
    <Paper sx={{ p: 3, mb: 3 }}>
      {/* Back button and title */}
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
        <IconButton onClick={handleCancel} sx={{ mr: 1 }}>
          <ArrowBackIcon />
        </IconButton>
        <Typography variant="h5">
          {stageId && stageId !== 'new' ? 'Edit Stage' : 'Create New Stage'}
        </Typography>
      </Box>
      
      {error && (
        <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
          {error}
        </Alert>
      )}
      
      {success && (
        <Alert severity="success" sx={{ mb: 2 }} onClose={() => setSuccess('')}>
          {success}
        </Alert>
      )}
      
      {/* Business ID input section */}
      {(showBusinessIdInput || !businessId || !businessApiKey) && (
        <Box sx={{ mb: 3, p: 2, border: '1px dashed', borderColor: 'warning.main', borderRadius: 1 }}>
          <Typography variant="subtitle1" color="warning.main" gutterBottom>
            Business Credentials Required
          </Typography>
          <Grid container spacing={2}>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="Business ID"
                value={businessId}
                onChange={(e) => setBusinessId(e.target.value)}
                placeholder="Enter your business ID"
                helperText="Required for saving templates and stages"
                required
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="Business API Key"
                value={businessApiKey}
                onChange={(e) => setBusinessApiKey(e.target.value)}
                placeholder="Enter your business API key"
                helperText="Required for authentication"
                required
              />
            </Grid>
            <Grid item xs={12}>
              <Button 
                variant="contained" 
                color="primary"
                onClick={saveCredentials}
              >
                Validate & Save Credentials
              </Button>
            </Grid>
          </Grid>
        </Box>
      )}
      
      {isLoading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
          <CircularProgress />
        </Box>
      ) : (
        <>
          {/* Basic stage information */}
          <Box sx={{ mb: 3 }}>
            <Typography variant="h6" gutterBottom>
              Stage Information
            </Typography>
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Stage Name"
                  value={stageName}
                  onChange={(e) => setStageName(e.target.value)}
                  required
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Stage Description"
                  value={stageDescription}
                  onChange={(e) => setStageDescription(e.target.value)}
                  multiline
                  rows={2}
                />
              </Grid>
            </Grid>
          </Box>
          
          <Divider sx={{ my: 3 }} />
          
          {/* Template editing section */}
          <Box>
            <Typography variant="h6" gutterBottom>
              Templates
            </Typography>
            
            <Tabs value={tabValue} onChange={handleTabChange} sx={{ mb: 2 }}>
              <Tab label="Stage Selection" />
              <Tab label="Data Extraction" />
              <Tab label="Response Generation" />
            </Tabs>
            
            {/* Stage Selection Template */}
            <TabPanel value={tabValue} index={0}>
              <Box sx={{ mb: 2 }}>
                <FormControl fullWidth sx={{ mb: 2 }}>
                  <InputLabel id="stage-selection-template-label">Template</InputLabel>
                  <Select
                    labelId="stage-selection-template-label"
                    value={templates.stage_selection.id || ''}
                    label="Template"
                    onChange={(e) => handleTemplateSelect('stage_selection', e.target.value)}
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {availableTemplates.stage_selection.map((template) => (
                      <MenuItem key={template.template_id} value={template.template_id}>
                        {template.template_name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                  <Typography variant="subtitle2" color="text.secondary">
                    {templates.stage_selection.id ? 
                      `Template ID: ${templates.stage_selection.id}` : 
                      'No template selected'}
                  </Typography>
                  <Box sx={{ display: 'flex', gap: 1 }}>
                    <Button
                      variant="outlined"
                      color="primary"
                      onClick={() => confirmSaveTemplate('stage_selection')}
                      disabled={!templates.stage_selection.text || savingTemplates.stage_selection}
                      startIcon={savingTemplates.stage_selection ? <CircularProgress size={20} /> : null}
                    >
                      {savingTemplates.stage_selection ? 'Saving...' : 'Save Template'}
                    </Button>
                    <Button
                      variant="outlined"
                      color="secondary"
                      onClick={() => confirmSaveTemplate('stage_selection', true)}
                      disabled={!templates.stage_selection.text || savingTemplates.stage_selection}
                    >
                      Save as Default
                    </Button>
                  </Box>
                </Box>
                
                <Box sx={{ display: 'flex', gap: 1, mb: 2 }}>
                  <Button
                    variant="outlined"
                    startIcon={<RestartAltIcon />}
                    onClick={() => handleApplyDefaultTemplate('stage_selection')}
                    disabled={availableTemplates.default_stage_selection.length === 0}
                  >
                    Apply Default
                  </Button>
                  <Button
                    variant="outlined"
                    startIcon={<PreviewIcon />}
                    onClick={() => handlePreview('stage_selection')}
                    disabled={!templates.stage_selection.text}
                  >
                    Preview
                  </Button>
                  <Tooltip title="Variables are enclosed in curly braces like {user_name}">
                    <IconButton>
                      <HelpOutlineIcon />
                    </IconButton>
                  </Tooltip>
                </Box>
                
                {previewData && previewData.type === 'stage_selection' && (
                  <Alert severity="info" sx={{ mb: 2 }}>
                    <Typography variant="subtitle2">Preview:</Typography>
                    <Typography variant="body2" sx={{ whiteSpace: 'pre-wrap' }}>
                      {previewData.result}
                    </Typography>
                  </Alert>
                )}
                
                <TextField
                  fullWidth
                  label="Template Text"
                  value={templates.stage_selection.text || ''}
                  onChange={(e) => handleTemplateChange('stage_selection', e.target.value)}
                  multiline
                  rows={8}
                  placeholder="Enter template text with variables in {curly_braces}"
                />
                
                {variables.stage_selection.length > 0 && (
                  <Box sx={{ mt: 2 }}>
                    <Typography variant="subtitle2">Variables:</Typography>
                    <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
                      {variables.stage_selection.map((variable) => (
                        <Chip key={variable} label={variable} size="small" />
                      ))}
                    </Box>
                  </Box>
                )}
              </Box>
            </TabPanel>
            
            {/* Data Extraction Template */}
            <TabPanel value={tabValue} index={1}>
              <Box sx={{ mb: 2 }}>
                <FormControl fullWidth sx={{ mb: 2 }}>
                  <InputLabel id="data-extraction-template-label">Template</InputLabel>
                  <Select
                    labelId="data-extraction-template-label"
                    value={templates.data_extraction.id || ''}
                    label="Template"
                    onChange={(e) => handleTemplateSelect('data_extraction', e.target.value)}
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {availableTemplates.data_extraction.map((template) => (
                      <MenuItem key={template.template_id} value={template.template_id}>
                        {template.template_name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                  <Typography variant="subtitle2" color="text.secondary">
                    {templates.data_extraction.id ? 
                      `Template ID: ${templates.data_extraction.id}` : 
                      'No template selected'}
                  </Typography>
                  <Box sx={{ display: 'flex', gap: 1 }}>
                    <Button
                      variant="outlined"
                      color="primary"
                      onClick={() => confirmSaveTemplate('data_extraction')}
                      disabled={!templates.data_extraction.text || savingTemplates.data_extraction}
                      startIcon={savingTemplates.data_extraction ? <CircularProgress size={20} /> : null}
                    >
                      {savingTemplates.data_extraction ? 'Saving...' : 'Save Template'}
                    </Button>
                    <Button
                      variant="outlined"
                      color="secondary"
                      onClick={() => confirmSaveTemplate('data_extraction', true)}
                      disabled={!templates.data_extraction.text || savingTemplates.data_extraction}
                    >
                      Save as Default
                    </Button>
                  </Box>
                </Box>
                
                <Box sx={{ display: 'flex', gap: 1, mb: 2 }}>
                  <Button
                    variant="outlined"
                    startIcon={<RestartAltIcon />}
                    onClick={() => handleApplyDefaultTemplate('data_extraction')}
                    disabled={availableTemplates.default_data_extraction.length === 0}
                  >
                    Apply Default
                  </Button>
                  <Button
                    variant="outlined"
                    startIcon={<PreviewIcon />}
                    onClick={() => handlePreview('data_extraction')}
                    disabled={!templates.data_extraction.text}
                  >
                    Preview
                  </Button>
                  <Tooltip title="Variables are enclosed in curly braces like {user_name}">
                    <IconButton>
                      <HelpOutlineIcon />
                    </IconButton>
                  </Tooltip>
                </Box>
                
                {previewData && previewData.type === 'data_extraction' && (
                  <Alert severity="info" sx={{ mb: 2 }}>
                    <Typography variant="subtitle2">Preview:</Typography>
                    <Typography variant="body2" sx={{ whiteSpace: 'pre-wrap' }}>
                      {previewData.result}
                    </Typography>
                  </Alert>
                )}
                
                <TextField
                  fullWidth
                  label="Template Text"
                  value={templates.data_extraction.text || ''}
                  onChange={(e) => handleTemplateChange('data_extraction', e.target.value)}
                  multiline
                  rows={8}
                  placeholder="Enter template text with variables in {curly_braces}"
                />
                
                {variables.data_extraction.length > 0 && (
                  <Box sx={{ mt: 2 }}>
                    <Typography variant="subtitle2">Variables:</Typography>
                    <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
                      {variables.data_extraction.map((variable) => (
                        <Chip key={variable} label={variable} size="small" />
                      ))}
                    </Box>
                  </Box>
                )}
              </Box>
            </TabPanel>
            
            {/* Response Generation Template */}
            <TabPanel value={tabValue} index={2}>
              <Box sx={{ mb: 2 }}>
                <FormControl fullWidth sx={{ mb: 2 }}>
                  <InputLabel id="response-generation-template-label">Template</InputLabel>
                  <Select
                    labelId="response-generation-template-label"
                    value={templates.response_generation.id || ''}
                    label="Template"
                    onChange={(e) => handleTemplateSelect('response_generation', e.target.value)}
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {availableTemplates.response_generation.map((template) => (
                      <MenuItem key={template.template_id} value={template.template_id}>
                        {template.template_name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                  <Typography variant="subtitle2" color="text.secondary">
                    {templates.response_generation.id ? 
                      `Template ID: ${templates.response_generation.id}` : 
                      'No template selected'}
                  </Typography>
                  <Box sx={{ display: 'flex', gap: 1 }}>
                    <Button
                      variant="outlined"
                      color="primary"
                      onClick={() => confirmSaveTemplate('response_generation')}
                      disabled={!templates.response_generation.text || savingTemplates.response_generation}
                      startIcon={savingTemplates.response_generation ? <CircularProgress size={20} /> : null}
                    >
                      {savingTemplates.response_generation ? 'Saving...' : 'Save Template'}
                    </Button>
                    <Button
                      variant="outlined"
                      color="secondary"
                      onClick={() => confirmSaveTemplate('response_generation', true)}
                      disabled={!templates.response_generation.text || savingTemplates.response_generation}
                    >
                      Save as Default
                    </Button>
                  </Box>
                </Box>
                
                <Box sx={{ display: 'flex', gap: 1, mb: 2 }}>
                  <Button
                    variant="outlined"
                    startIcon={<RestartAltIcon />}
                    onClick={() => handleApplyDefaultTemplate('response_generation')}
                    disabled={availableTemplates.default_response_generation.length === 0}
                  >
                    Apply Default
                  </Button>
                  <Button
                    variant="outlined"
                    startIcon={<PreviewIcon />}
                    onClick={() => handlePreview('response_generation')}
                    disabled={!templates.response_generation.text}
                  >
                    Preview
                  </Button>
                  <Tooltip title="Variables are enclosed in curly braces like {user_name}">
                    <IconButton>
                      <HelpOutlineIcon />
                    </IconButton>
                  </Tooltip>
                </Box>
                
                {previewData && previewData.type === 'response_generation' && (
                  <Alert severity="info" sx={{ mb: 2 }}>
                    <Typography variant="subtitle2">Preview:</Typography>
                    <Typography variant="body2" sx={{ whiteSpace: 'pre-wrap' }}>
                      {previewData.result}
                    </Typography>
                  </Alert>
                )}
                
                <TextField
                  fullWidth
                  label="Template Text"
                  value={templates.response_generation.text || ''}
                  onChange={(e) => handleTemplateChange('response_generation', e.target.value)}
                  multiline
                  rows={8}
                  placeholder="Enter template text with variables in {curly_braces}"
                />
                
                {variables.response_generation.length > 0 && (
                  <Box sx={{ mt: 2 }}>
                    <Typography variant="subtitle2">Variables:</Typography>
                    <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
                      {variables.response_generation.map((variable) => (
                        <Chip key={variable} label={variable} size="small" />
                      ))}
                    </Box>
                  </Box>
                )}
              </Box>
            </TabPanel>
          </Box>
          
          {/* Save button */}
          <Box sx={{ mt: 3, display: 'flex', justifyContent: 'space-between' }}>
            <Button
              variant="outlined"
              onClick={handleCancel}
            >
              Cancel
            </Button>
            <Button
              variant="contained"
              startIcon={<SaveIcon />}
              onClick={handleSave}
              disabled={isLoading}
            >
              {isLoading ? <CircularProgress size={24} /> : (stageId === 'new' ? 'Create Stage' : 'Save Stage')}
            </Button>
          </Box>
        </>
      )}

      {/* Template Save Confirmation Dialog */}
      <Dialog open={showConfirmDialog} onClose={() => setShowConfirmDialog(false)}>
        <DialogTitle>Save Template</DialogTitle>
        <DialogContent>
          <Typography>
            {saveAsDefault
              ? `Are you sure you want to save this as a default ${templateToSave?.replace(/_/g, ' ')} template?`
              : templates[templateToSave]?.id 
                ? `Are you sure you want to update this ${templateToSave?.replace(/_/g, ' ')} template?` 
                : `Are you sure you want to create a new ${templateToSave?.replace(/_/g, ' ')} template?`}
          </Typography>
          {saveAsDefault && (
            <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
              This will create a new default template that can be applied to any stage.
            </Typography>
          )}
          {!templates[templateToSave]?.id && !saveAsDefault && (
            <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
              A new template will be created in the database.
            </Typography>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setShowConfirmDialog(false)}>Cancel</Button>
          <Button onClick={handleConfirmSave} color="primary" variant="contained">
            Save
          </Button>
        </DialogActions>
      </Dialog>
    </Paper>
  );
};

export default StageEditor;
