// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { Container, CssBaseline, ThemeProvider, createTheme, Box } from '@mui/material';
import Login from './components/Login';
import BusinessDetailsView from './components/BusinessDetailsView/BusinessDetailsView.jsx';
import StageManager from './components/StageManager';
import TemplateEditor from './components/TemplateEditor';
import TemplateManagement from './components/TemplateManagement';
import Snackbar from '@mui/material/Snackbar';
import Alert from '@mui/material/Alert';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './components/Home';
import StageViewPage from './pages/StageViewPage';
import MessageDebugPage from './pages/MessageDebugPage';
import { isLoggedIn, getStoredCredentials, logout } from './services/authService';
import { UI_CONFIG } from './config';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import './App.css';

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userId, setUserId] = useState('');
  const [businessId, setBusinessId] = useState('');
  const [businessApiKey, setBusinessApiKey] = useState('');
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  // --- Add Logging to Setters --- 
  const updateUserId = (value) => {
    console.log("App.js: Updating userId to:", value);
    setUserId(value);
  };
  const updateBusinessId = (value) => {
    console.log("App.js: Updating businessId to:", value);
    setBusinessId(value);
  };
  const updateBusinessApiKey = (value) => {
    console.log("App.js: Updating businessApiKey to:", value);
    setBusinessApiKey(value);
  };
  const updateIsAuthenticated = (value) => {
    console.log("App.js: Updating isAuthenticated to:", value);
    setIsAuthenticated(value);
  };
  // -----------------------------

  useEffect(() => {
    // Check if user is logged in on component mount
    const loggedIn = isLoggedIn();
    if (loggedIn) {
      const { userId, businessId, businessApiKey } = getStoredCredentials();
      setUserId(userId);
      setBusinessId(businessId);
      setBusinessApiKey(businessApiKey);
      setIsAuthenticated(true);
    }
  }, []);

  const handleSnackbarOpen = (message, severity = 'success') => {
    setSnackbar({ open: true, message, severity });
  };

  const handleSnackbarClose = () => {
    setSnackbar({ ...snackbar, open: false });
  };

  const handleLogout = () => {
    // Logout using the authService
    logout();

    // Reset state
    setUserId('');
    setBusinessId('');
    setBusinessApiKey('');
    setIsAuthenticated(false);

    // Show success message
    handleSnackbarOpen('Logged out successfully', 'success');
  };

  return (
    <Router>
      <ThemeProvider theme={theme}>
        <CssBaseline />
        <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
          <Header />
          <Container component="main" sx={{ flexGrow: 1, py: 3 }}>
            <Routes>
              <Route 
                path="/" 
                element={
                  isAuthenticated ? (
                    <Navigate to="/business" replace />
                  ) : (
                    <Navigate to="/login" replace />
                  )
                } 
              />
              <Route 
                path="/stages" 
                element={
                  isAuthenticated ? <StageManager /> : <Navigate to="/login" replace />
                } 
              />
              <Route 
                path="/stages/:stageId" 
                element={
                  isAuthenticated ? <StageViewPage /> : <Navigate to="/login" replace />
                } 
              />
              <Route 
                path="/debug/conversation/:conversationId" 
                element={
                  isAuthenticated ? <MessageDebugPage /> : <Navigate to="/login" replace />
                } 
              />
              <Route 
                path="/login" 
                element={
                  isAuthenticated ? (
                    <Navigate to="/business" replace />
                  ) : (
                    <Login 
                      userId={userId}
                      setUserId={updateUserId}
                      businessId={businessId}
                      setBusinessId={updateBusinessId}
                      businessApiKey={businessApiKey}
                      setBusinessApiKey={updateBusinessApiKey}
                      setIsAuthenticated={updateIsAuthenticated}
                      handleSnackbarOpen={handleSnackbarOpen}
                    />
                  )
                } 
              />
              <Route 
                path="/business" 
                element={
                  isAuthenticated ? (
                    <BusinessDetailsView />
                  ) : (
                    <Navigate to="/login" replace />
                  )
                } 
              />
              <Route 
                path="/templates" 
                element={
                  isAuthenticated ? (
                    <TemplateManagement />
                  ) : (
                    <Navigate to="/login" replace />
                  )
                } 
              />
              <Route 
                path="/template-editor/:templateId" 
                element={
                  isAuthenticated ? (
                    <TemplateEditor />
                  ) : (
                    <Navigate to="/login" replace />
                  )
                } 
              />
              <Route 
                path="/template-editor/new" 
                element={
                  isAuthenticated ? (
                    <TemplateEditor />
                  ) : (
                    <Navigate to="/login" replace />
                  )
                } 
              />
            </Routes>
          </Container>
          <Footer />
        </Box>
      </ThemeProvider>
      <Snackbar 
        open={snackbar.open} 
        autoHideDuration={UI_CONFIG.SNACKBAR.AUTO_HIDE_DURATION} 
        onClose={handleSnackbarClose}
        anchorOrigin={UI_CONFIG.SNACKBAR.POSITION}
      >
        <Alert onClose={handleSnackbarClose} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Router>
  );
}

export default App;
