// Configuration settings for the application

// API Configuration
const API_CONFIG = {
  // Base URL for API requests
  BASE_URL: process.env.REACT_APP_API_BASE_URL || 'http://127.0.0.1:5000',
  
  // API endpoints
  ENDPOINTS: {
    LOGIN: '/api/save-config',
    MESSAGE: '/message',
    CONVERSATIONS: '/conversations',
    BUSINESSES: '/businesses',
    STAGES: '/stages',
    TEMPLATES: '/templates',
    DEBUG: '/debug'
  },
  
  // Default values
  DEFAULTS: {
    BUSINESS_ID: '7ae167a0-d864-43b9-bdaf-fcba35b33f27',
    API_KEY: 'da828cae6a3e46228aa09d65ba9066e3'
  }
};

// Authentication settings
const AUTH_CONFIG = {
  // Cookie names
  COOKIES: {
    BUSINESS_API_KEY: 'businessApiKey'
  },
  
  // Local storage keys
  STORAGE_KEYS: {
    USER_ID: 'userId',
    BUSINESS_ID: 'businessId',
    BUSINESS_API_KEY: 'businessApiKey'
  }
};

// UI Configuration
const UI_CONFIG = {
  // Snackbar settings
  SNACKBAR: {
    AUTO_HIDE_DURATION: 6000,
    POSITION: { vertical: 'top', horizontal: 'center' }
  },
  
  // Debug panel settings
  DEBUG_PANEL: {
    ID: 'debugPanel'
  }
};

export { API_CONFIG, AUTH_CONFIG, UI_CONFIG };
