import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import {
  Container,
  Typography,
  Paper,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  CircularProgress,
  Grid,
  Box,
  IconButton,
  Divider,
  Alert,
  Snackbar,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  Tooltip,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  Card,
  CardContent
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import ArrowDownwardIcon from '@mui/icons-material/ArrowDownward';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import SaveIcon from '@mui/icons-material/Save';
import DescriptionIcon from '@mui/icons-material/Description';
import VisibilityIcon from '@mui/icons-material/Visibility';
import { fetchStages, createStage, updateStage, deleteStage } from '../services/stageService';

const StageManager = () => {
  // Get parameters from URL and location
  const { businessId: paramBusinessId, agentId: paramAgentId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  
  // Extract query parameters if not using path params
  const queryParams = new URLSearchParams(location.search);
  const queryBusinessId = queryParams.get('business_id');
  const queryAgentId = queryParams.get('agent_id');
  
  // Use params or query params, then fallback to localStorage
  const businessId = paramBusinessId || queryBusinessId || localStorage.getItem('businessId');
  const agentId = paramAgentId || queryAgentId || localStorage.getItem('agentId');
  
  // States for stages and templates
  const [stages, setStages] = useState([]);
  const [selectedStage, setSelectedStage] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [openDialog, setOpenDialog] = useState(false);
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
  const [stageToDelete, setStageToDelete] = useState(null);
  const [newStageName, setNewStageName] = useState('');
  const [stageDescription, setStageDescription] = useState('');
  const [stageType, setStageType] = useState('conversation');
  const [availableTemplates, setAvailableTemplates] = useState({
    stage_selection: [],
    data_extraction: [],
    response_generation: []
  });
  const [selectedTemplates, setSelectedTemplates] = useState({
    stage_selection: '',
    data_extraction: '',
    response_generation: ''
  });
  const [defaultTemplates, setDefaultTemplates] = useState({
    stage_selection: '',
    data_extraction: '',
    response_generation: ''
  });
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'success'
  });
  const [agentName, setAgentName] = useState('');

  // Store current values in localStorage for persistence
  useEffect(() => {
    if (businessId) {
      localStorage.setItem('businessId', businessId);
    }
    if (agentId) {
      localStorage.setItem('agentId', agentId);
    }
  }, [businessId, agentId]);

  // Logging for debugging
  useEffect(() => {
    console.log("StageManager - Current params:", { 
      paramBusinessId, 
      paramAgentId,
      queryBusinessId,
      queryAgentId,
      resolvedBusinessId: businessId,
      resolvedAgentId: agentId
    });
  }, [
    paramBusinessId, 
    paramAgentId, 
    queryBusinessId, 
    queryAgentId, 
    businessId, 
    agentId
  ]);

  // Fetch templates for dropdowns
  const fetchTemplates = useCallback(async () => {
    if (!businessId) {
      console.error("No business ID available for fetching templates");
      return;
    }
    
    try {
      const response = await fetch(`/templates?business_id=${businessId}`, {
        credentials: 'include'
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to fetch templates');
      }
      
      const templates = await response.json();
      console.log("Fetched templates:", templates);
      
      // Group templates by type
      const templatesByType = {
        stage_selection: [],
        data_extraction: [],
        response_generation: []
      };
      
      const defaultTemplateIds = {
        stage_selection: '',
        data_extraction: '',
        response_generation: ''
      };
      
      // Process all templates
      templates.forEach(template => {
        // Check if this is a default template
        if (template.template_type.startsWith('default_')) {
          // Extract the base type (e.g., from 'default_stage_selection' to 'stage_selection')
          let baseType = template.template_type.replace('default_', '');
          
          // Map the template types correctly
          if (baseType === 'stage_selection') {
            baseType = 'stage_selection';
          } else if (baseType === 'data_extraction') {
            baseType = 'data_extraction';
          } else if (baseType === 'response_generation') {
            baseType = 'response_generation';
          }
          
          // Check if the base type exists in our template categories
          if (baseType in templatesByType) {
            // Add to the appropriate category
            templatesByType[baseType].push({
              ...template,
              // Keep the original template_type for the API
              display_type: baseType // For display purposes
            });
            
            // Store the default template's ID for each type
            if (!defaultTemplateIds[baseType]) {
              defaultTemplateIds[baseType] = template.template_id;
            }
          }
        } else {
          // Regular (non-default) templates
          const type = template.template_type;
          if (type in templatesByType) {
            templatesByType[type].push(template);
          }
        }
      });
      
      console.log("Processed templates by type:", templatesByType);
      console.log("Default template IDs:", defaultTemplateIds);
      
      setAvailableTemplates(templatesByType);
      setDefaultTemplates(defaultTemplateIds);
      
      // Set initial selected templates to defaults
      setSelectedTemplates({
        stage_selection: defaultTemplateIds.stage_selection,
        data_extraction: defaultTemplateIds.data_extraction,
        response_generation: defaultTemplateIds.response_generation
      });
      
    } catch (err) {
      console.error("Error fetching templates:", err);
      showSnackbar(err.message, 'error');
    }
  }, [businessId]);

  // Fetch stages based on business ID and agent ID
  const fetchStagesData = useCallback(async () => {
    if (!businessId) {
      console.error("No business ID available for fetching stages");
      setIsLoading(false);
      return;
    }
    
    setIsLoading(true);
    try {
      console.log("Fetching stages for business:", businessId);
      const stagesData = await fetchStages(businessId);
      console.log("Fetched stages:", stagesData);
      setStages(stagesData);
    } catch (err) {
      console.error("Error fetching stages:", err);
      showSnackbar(err.message, 'error');
    } finally {
      setIsLoading(false);
    }
  }, [businessId]);

  // Fetch agent details
  const fetchAgentDetails = useCallback(async () => {
    if (!businessId || !agentId) {
      return;
    }

    try {
      const response = await fetch(`/agents/${agentId}?business_id=${businessId}`, {
        credentials: 'include'
      });
      
      if (!response.ok) {
        throw new Error('Failed to fetch agent details');
      }
      
      const agentData = await response.json();
      setAgentName(agentData.agent_name || `Agent ${agentId}`);
    } catch (err) {
      console.error("Error fetching agent details:", err);
      setAgentName(`Agent ${agentId}`); // Fallback
    }
  }, [businessId, agentId]);

  // Initial data fetch
  useEffect(() => {
    fetchTemplates();
    fetchStagesData();
  }, [fetchTemplates, fetchStagesData]);

  // Update useEffect to fetch agent details
  useEffect(() => {
    if (agentId) {
      fetchAgentDetails();
    }
  }, [agentId, fetchAgentDetails]);

  // Helper function for showing snackbar messages
  const showSnackbar = (message, severity = 'success') => {
    setSnackbar({
      open: true,
      message,
      severity
    });
  };

  const handleCloseSnackbar = () => {
    setSnackbar({
      ...snackbar,
      open: false
    });
  };

  const handleNavigateToHome = () => {
    navigate('/business');
  };

  const handleOpenNewStageDialog = () => {
    console.log("Opening new stage dialog with default templates:", {
      defaultTemplates,
      availableTemplates
    });
    
    // Check if default templates exist
    if (!defaultTemplates.stage_selection || !defaultTemplates.data_extraction || !defaultTemplates.response_generation) {
      console.warn("Some default templates are missing:", defaultTemplates);
      
      // Try to find default templates manually
      const manualDefaultTemplates = {
        stage_selection: '',
        data_extraction: '',
        response_generation: ''
      };
      
      // Loop through all templates and try to find ones with default_ prefix
      for (const type in availableTemplates) {
        const templates = availableTemplates[type];
        for (const template of templates) {
          // Check if this is a default template
          if (template.template_type && template.template_type.startsWith('default_')) {
            console.log(`Found default template for ${type}:`, template);
            manualDefaultTemplates[type] = template.template_id;
          }
        }
      }
      
      console.log("Manually determined default templates:", manualDefaultTemplates);
      
      // Use the manual defaults if found
      if (manualDefaultTemplates.stage_selection || manualDefaultTemplates.data_extraction || manualDefaultTemplates.response_generation) {
        setSelectedTemplates({
          stage_selection: manualDefaultTemplates.stage_selection || defaultTemplates.stage_selection,
          data_extraction: manualDefaultTemplates.data_extraction || defaultTemplates.data_extraction,
          response_generation: manualDefaultTemplates.response_generation || defaultTemplates.response_generation
        });
      } else {
        // If still not found, just use empty values
        setSelectedTemplates({
          stage_selection: defaultTemplates.stage_selection || '',
          data_extraction: defaultTemplates.data_extraction || '',
          response_generation: defaultTemplates.response_generation || ''
        });
      }
    } else {
      // Normal flow if default templates exist
      setSelectedTemplates({
        stage_selection: defaultTemplates.stage_selection,
        data_extraction: defaultTemplates.data_extraction,
        response_generation: defaultTemplates.response_generation
      });
    }
    
    setNewStageName('');
    setStageDescription('');
    setStageType('conversation');
    setOpenDialog(true);
  };

  const handleCreateStage = async () => {
    try {
      if (!newStageName.trim()) {
        showSnackbar('Stage name is required', 'error');
        return;
      }

      // First, create copies of the selected templates
      const newTemplateIds = {};
      
      for (const templateType of ['stage_selection', 'data_extraction', 'response_generation']) {
        const originalTemplateId = selectedTemplates[templateType];
        if (!originalTemplateId) continue;

        // Find the original template
        const originalTemplate = availableTemplates[templateType].find(t => t.template_id === originalTemplateId);
        if (!originalTemplate) continue;

        try {
          // Create a copy of the template
          const response = await fetch('/templates', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            credentials: 'include',
            body: JSON.stringify({
              template_name: `${newStageName} - ${templateType.replace('_', ' ')}`,
              content: originalTemplate.content || originalTemplate.template_text,
              system_prompt: originalTemplate.system_prompt || '',
              template_type: templateType,
              business_id: businessId,
              template_description: originalTemplate.template_description || ''
            }),
          });

          if (!response.ok) {
            const errorData = await response.json();
            throw new Error(errorData.error || 'Failed to create template copy');
          }

          const newTemplate = await response.json();
          newTemplateIds[templateType] = newTemplate.template_id;
        } catch (err) {
          console.error(`Error copying template for ${templateType}:`, err);
          showSnackbar(`Failed to copy ${templateType} template: ${err.message}`, 'error');
          return;
        }
      }

      const stageData = {
        stage_name: newStageName,
        stage_description: stageDescription,
        stage_type: stageType,
        business_id: businessId,
        stage_selection_template_id: newTemplateIds.stage_selection || selectedTemplates.stage_selection,
        data_extraction_template_id: newTemplateIds.data_extraction || selectedTemplates.data_extraction,
        response_generation_template_id: newTemplateIds.response_generation || selectedTemplates.response_generation,
        stage_config: { order: stages.length + 1 }
      };
      
      // Add agent_id only if it exists
      if (agentId) {
        stageData.agent_id = agentId;
      }
      
      console.log("Creating stage with data:", stageData);
      
      const newStage = await createStage(stageData);
      await fetchStagesData();
      setOpenDialog(false);
      showSnackbar('Stage created successfully', 'success');

      // Navigate to the stage view instead of editor
      if (newStage && newStage.stage_id) {
        navigateToStageView(newStage.stage_id);
      }
    } catch (err) {
      console.error("Error creating stage:", err);
      showSnackbar(err.message, 'error');
    }
  };

  const navigateToStageView = (stageId) => {
    // Construct the URL with query parameters
    let url = `/stages/${stageId}?business_id=${businessId}`;
    if (agentId) {
      url += `&agent_id=${agentId}`;
    }
    navigate(url);
  };

  const handleDeleteStage = async () => {
    if (!stageToDelete || !businessId) return;

    try {
      await deleteStage(stageToDelete, businessId);
      await fetchStagesData();
      setOpenDeleteDialog(false);
      showSnackbar('Stage deleted successfully', 'success');
    } catch (err) {
      console.error("Error deleting stage:", err);
      showSnackbar(err.message, 'error');
    }
  };

  const handleMoveStage = async (stageId, direction) => {
    try {
      const currentIndex = stages.findIndex(s => s.stage_id === stageId);
      if (currentIndex === -1) return;
      
      // If trying to move first item up or last item down, do nothing
      if ((currentIndex === 0 && direction === 'up') || 
          (currentIndex === stages.length - 1 && direction === 'down')) {
        return;
      }
      
      const targetIndex = direction === 'up' ? currentIndex - 1 : currentIndex + 1;
      
      // Swap the order values in the stage_config
      const updatedStages = [...stages];
      const currentStage = { ...updatedStages[currentIndex] };
      const targetStage = { ...updatedStages[targetIndex] };
      
      // Update stage_config.order values
      currentStage.stage_config = { ...currentStage.stage_config, order: targetIndex + 1 };
      targetStage.stage_config = { ...targetStage.stage_config, order: currentIndex + 1 };
      
      // Update both stages in the database
      const currentUpdate = fetch(`/stages/${currentStage.stage_id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(currentStage),
      });
      
      const targetUpdate = fetch(`/stages/${targetStage.stage_id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(targetStage),
      });
      
      await Promise.all([currentUpdate, targetUpdate]);
      
      // Refresh stages
      await fetchStagesData();
      showSnackbar('Stage order updated', 'success');
    } catch (err) {
      showSnackbar(err.message, 'error');
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Paper sx={{ p: 3, mb: 4 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <IconButton onClick={handleNavigateToHome} sx={{ mr: 1 }}>
            <ArrowBackIcon />
          </IconButton>
          <Typography variant="h5" component="h1" gutterBottom sx={{ flexGrow: 1 }}>
            Stage Manager
            {businessId && <Typography variant="subtitle1" component="span" color="text.secondary">
              {` - Business ID: ${businessId}`}
              {agentId && ` - Agent: ${agentName || agentId}`}
            </Typography>}
          </Typography>
          <Button
            variant="contained"
            color="primary"
            startIcon={<AddIcon />}
            onClick={handleOpenNewStageDialog}
            sx={{ mr: 1 }}
          >
            Create Stage
          </Button>
          <Button
            variant="outlined"
            color="secondary"
            startIcon={<DescriptionIcon />}
            onClick={() => navigate('/templates')}
          >
            Manage Templates
          </Button>
        </Box>
        
        <Divider sx={{ mb: 3 }} />
        
        {error && (
          <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
            {error}
          </Alert>
        )}
        
        {success && (
          <Alert severity="success" sx={{ mb: 2 }} onClose={() => setSuccess('')}>
            {success}
          </Alert>
        )}
        
        {isLoading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
            <CircularProgress />
          </Box>
        ) : (
          <>
            {stages.length === 0 ? (
              <Alert severity="info">
                No stages found. Create your first stage to get started.
              </Alert>
            ) : (
              <List sx={{ bgcolor: 'background.paper' }}>
                {[...stages].sort((a, b) => 
                  (a.stage_config?.order || Infinity) - (b.stage_config?.order || Infinity)
                ).map((stage) => (
                  <ListItem 
                    key={stage.stage_id}
                    sx={{ 
                      mb: 1,
                      bgcolor: 'background.paper'
                    }}
                  >
                    <ListItemText
                      primary={stage.stage_name}
                      secondary={
                        <>
                          <Typography component="span" variant="body2" color="text.primary">
                            {stage.stage_description || 'No description'}
                          </Typography>
                          {stage.stage_config && stage.stage_config.order && 
                            ` • Order: ${stage.stage_config.order}`}
                        </>
                      }
                    />
                    <ListItemSecondaryAction>
                      <Tooltip title="Move Up">
                        <IconButton 
                          edge="end" 
                          aria-label="move-up"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleMoveStage(stage.stage_id, 'up');
                          }}
                          disabled={stages.indexOf(stage) === 0}
                        >
                          <ArrowUpwardIcon />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="Move Down">
                        <IconButton 
                          edge="end" 
                          aria-label="move-down"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleMoveStage(stage.stage_id, 'down');
                          }}
                          disabled={stages.indexOf(stage) === stages.length - 1}
                        >
                          <ArrowDownwardIcon />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="View Stage">
                        <IconButton 
                          edge="end" 
                          aria-label="view"
                          onClick={(e) => {
                            e.stopPropagation();
                            navigateToStageView(stage.stage_id);
                          }}
                        >
                          <VisibilityIcon />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="Delete Stage">
                        <IconButton 
                          edge="end" 
                          aria-label="delete"
                          onClick={(e) => {
                            e.stopPropagation();
                            setStageToDelete(stage);
                            setOpenDeleteDialog(true);
                          }}
                        >
                          <DeleteIcon />
                        </IconButton>
                      </Tooltip>
                    </ListItemSecondaryAction>
                  </ListItem>
                ))}
              </List>
            )}
          </>
        )}
      </Paper>
      
      {/* Create Stage Dialog */}
      <Dialog 
        open={openDialog} 
        onClose={() => setOpenDialog(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>Create New Stage</DialogTitle>
        <DialogContent>
          <Grid container spacing={2} sx={{ mt: 1 }}>
            <Grid item xs={12}>
              <TextField
                autoFocus
                label="Stage Name"
                fullWidth
                variant="outlined"
                value={newStageName}
                onChange={(e) => setNewStageName(e.target.value)}
                required
                error={!newStageName.trim()}
                helperText={!newStageName.trim() ? "Stage name is required" : ""}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                label="Stage Description"
                fullWidth
                variant="outlined"
                value={stageDescription}
                onChange={(e) => setStageDescription(e.target.value)}
                multiline
                rows={2}
              />
            </Grid>
            <Grid item xs={12}>
              <FormControl fullWidth>
                <InputLabel>Stage Type</InputLabel>
                <Select
                  value={stageType}
                  onChange={(e) => setStageType(e.target.value)}
                  label="Stage Type"
                >
                  <MenuItem value="conversation">Conversation</MenuItem>
                  <MenuItem value="form">Form</MenuItem>
                  <MenuItem value="document">Document</MenuItem>
                </Select>
              </FormControl>
            </Grid>
            
            {/* Template Selection Dropdowns */}
            <Grid item xs={12}>
              <Typography variant="h6" gutterBottom>
                Template Selection
              </Typography>
            </Grid>
            
            <Grid item xs={12}>
              <FormControl fullWidth>
                <InputLabel>Stage Selection Template</InputLabel>
                <Select
                  value={selectedTemplates.stage_selection}
                  onChange={(e) => setSelectedTemplates({
                    ...selectedTemplates,
                    stage_selection: e.target.value
                  })}
                  label="Stage Selection Template"
                >
                  <MenuItem value="">None</MenuItem>
                  {availableTemplates.stage_selection.map(template => (
                    <MenuItem key={template.template_id} value={template.template_id}>
                      {template.template_name || template.template_id}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
            
            <Grid item xs={12}>
              <FormControl fullWidth>
                <InputLabel>Data Extraction Template</InputLabel>
                <Select
                  value={selectedTemplates.data_extraction}
                  onChange={(e) => setSelectedTemplates({
                    ...selectedTemplates,
                    data_extraction: e.target.value
                  })}
                  label="Data Extraction Template"
                >
                  <MenuItem value="">None</MenuItem>
                  {availableTemplates.data_extraction.map(template => (
                    <MenuItem key={template.template_id} value={template.template_id}>
                      {template.template_name || template.template_id}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
            
            <Grid item xs={12}>
              <FormControl fullWidth>
                <InputLabel>Response Generation Template</InputLabel>
                <Select
                  value={selectedTemplates.response_generation}
                  onChange={(e) => setSelectedTemplates({
                    ...selectedTemplates,
                    response_generation: e.target.value
                  })}
                  label="Response Generation Template"
                >
                  <MenuItem value="">None</MenuItem>
                  {availableTemplates.response_generation.map(template => (
                    <MenuItem key={template.template_id} value={template.template_id}>
                      {template.template_name || template.template_id}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Cancel</Button>
          <Button 
            onClick={handleCreateStage} 
            variant="contained" 
            color="primary"
            disabled={!newStageName.trim()}
            startIcon={<SaveIcon />}
          >
            Create & Save
          </Button>
        </DialogActions>
      </Dialog>

      {/* Delete Confirmation Dialog */}
      <Dialog
        open={openDeleteDialog}
        onClose={() => setOpenDeleteDialog(false)}
      >
        <DialogTitle>Confirm Delete</DialogTitle>
        <DialogContent>
          <Typography>
            Are you sure you want to delete the stage "{stageToDelete?.stage_name}"?
            This action cannot be undone.
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDeleteDialog(false)}>Cancel</Button>
          <Button 
            onClick={handleDeleteStage} 
            variant="contained" 
            color="error"
          >
            Delete
          </Button>
        </DialogActions>
      </Dialog>

      {/* Snackbar for notifications */}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={handleCloseSnackbar}
      >
        <Alert severity={snackbar.severity} onClose={handleCloseSnackbar}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default StageManager;
