import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Paper,
  Typography,
  Button,
  Alert,
  CircularProgress,
  Grid,
  Card,
  CardContent,
  CardHeader
} from '@mui/material';

const StageEditor = ({ stageId }) => {
  const navigate = useNavigate();

  // State variables
  const [stage, setStage] = useState(null);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Get stored credentials
  const getStoredCredentials = () => {
    return {
      businessId: localStorage.getItem('businessId') || '',
      businessApiKey: localStorage.getItem('businessApiKey') || ''
    };
  };

  // Track if component is mounted
  const isMounted = useRef(true);

  useEffect(() => {
    return () => {
      isMounted.current = false;
    };
  }, []);

  // Fetch stage data on component mount
  useEffect(() => {
    const { businessId, businessApiKey } = getStoredCredentials();

    if (!businessId || !businessApiKey) {
      setError('Authentication required. Please log in.');
      return;
    }

    if (stageId && stageId !== 'new') {
      fetchStage(stageId, businessId, businessApiKey);
    }
  }, [stageId]);

  // Fetch specific stage data
  const fetchStage = async (id, businessId, businessApiKey) => {
    if (!isMounted.current || !businessId || !businessApiKey) {
      console.error('Missing required parameters:', { id, businessId, hasApiKey: !!businessApiKey });
      setIsLoading(false);
      return;
    }

    setIsLoading(true);
    setError('');
    try {
      const params = new URLSearchParams();
      params.append('business_id', businessId);

      const url = `/stages/${id}?${params.toString()}`;
      console.log('Fetching stage from URL:', url);

      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${businessApiKey}`
        },
        credentials: 'include'
      });

      console.log('Response status:', response.status);

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        console.error('Error response:', errorData);
        throw new Error(`Failed to fetch stage: ${response.status} ${errorData.message || ''}`);
      }

      const data = await response.json();
      console.log('Received stage data:', data);

      if (isMounted.current) {
        if (!data || typeof data !== 'object') {
          throw new Error('Invalid stage data received');
        }
        setStage(data);
        setError('');
        console.log('Stage state set to:', data);
      }
    } catch (err) {
      console.error('Error in fetchStage:', err);
      if (isMounted.current) {
        setError(err.message);
        setStage(null);
      }
    } finally {
      if (isMounted.current) {
        setIsLoading(false);
      }
    }
  };

  // Handle return to stage management
  const handleReturn = () => {
    navigate('/stage-management');
  };

  if (isLoading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '200px' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box>
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
        <Button
          variant="outlined"
          onClick={handleReturn}
          sx={{ mt: 2 }}
        >
          Return to Stage Management
        </Button>
      </Box>
    );
  }

  if (!stage) {
    return (
      <Box>
        <Alert severity="info" sx={{ mb: 2 }}>
          No stage data available. This could mean either:
          <ul>
            <li>The stage doesn't exist</li>
            <li>The data format is invalid</li>
            <li>There was an error fetching the data</li>
          </ul>
          {error && <Typography color="error">Error details: {error}</Typography>}
        </Alert>
        <Button 
          variant="outlined"
          onClick={handleReturn}
          sx={{ mt: 2 }}
        >
          Return to Stage Management
        </Button>
      </Box>
    );
  }

  // Add debug output before render
  console.log('Rendering stage:', stage);

  return (
    <Box>
      <Paper sx={{ p: 3, mb: 3 }}>
        <Typography variant="h5" gutterBottom>
          Stage Details: {stage?.stage_name || 'Unnamed Stage'}
        </Typography>

        <Grid container spacing={3}>
          {/* Basic Information */}
          <Grid item xs={12} md={6}>
            <Card variant="outlined">
              <CardHeader title="Stage Information" />
              <CardContent>
                <Typography variant="subtitle2" color="text.secondary">Stage Name</Typography>
                <Typography variant="body1" sx={{ mb: 2 }}>{stage?.stage_name || 'Not set'}</Typography>

                <Typography variant="subtitle2" color="text.secondary">Description</Typography>
                <Typography variant="body1" sx={{ mb: 2 }}>{stage?.stage_description || 'No description'}</Typography>

                <Typography variant="subtitle2" color="text.secondary">Stage Type</Typography>
                <Typography variant="body1" sx={{ mb: 2 }}>{stage?.stage_type || 'Not specified'}</Typography>
              </CardContent>
            </Card>
          </Grid>

          {/* Template Information */}
          <Grid item xs={12} md={6}>
            <Card variant="outlined">
              <CardHeader title="Template Information" />
              <CardContent>
                <Typography variant="subtitle2" color="text.secondary">Stage Selection Template ID</Typography>
                <Typography variant="body1" sx={{ mb: 2 }}>{stage?.stage_selection_template_id || 'Not set'}</Typography>

                <Typography variant="subtitle2" color="text.secondary">Data Extraction Template ID</Typography>
                <Typography variant="body1" sx={{ mb: 2 }}>{stage?.data_extraction_template_id || 'Not set'}</Typography>

                <Typography variant="subtitle2" color="text.secondary">Response Generation Template ID</Typography>
                <Typography variant="body1" sx={{ mb: 2 }}>{stage?.response_generation_template_id || 'Not set'}</Typography>
              </CardContent>
            </Card>
          </Grid>
        </Grid>

        <Box sx={{ mt: 3, display: 'flex', justifyContent: 'flex-start' }}>
          <Button
            variant="outlined"
            onClick={handleReturn}
            sx={{ mr: 2 }}
          >
            Return to Stage Management
          </Button>
        </Box>
      </Paper>
    </Box>
  );
};

export default StageEditor;
