import React, { useState, useEffect, useCallback } from 'react';
import { TextField, Button, Typography, Card, CardContent, Box, CircularProgress } from '@mui/material';
import { useNavigate } from 'react-router-dom';

function Configuration({
    apiKey,
    setApiKey,
    userId,
    setUserId,
    businessId,
    setBusinessId,
    businessApiKey,
    setBusinessApiKey,
    handleSnackbarOpen,
    setIsAuthenticated
}) {
    const [isLoading, setIsLoading] = useState(false);
    const [configValidated, setConfigValidated] = useState(false);
    const navigate = useNavigate();

    const checkAuthentication = useCallback(async () => {
        // Skip validation if we don't have all credentials
        if (!apiKey || !userId || !businessId || !businessApiKey) {
            setIsAuthenticated(false);
            setConfigValidated(false);
            navigate('/login');
            return;
        }

        try {
            const response = await fetch('/validate_config', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${apiKey}`
                },
                credentials: 'include',
                body: JSON.stringify({
                    apiKey,
                    userId,
                    businessId,
                    businessApiKey
                })
            });

            const data = await response.json();

            if (!response.ok || !data.isValid) {
                console.error('Authentication failed:', response.status, data.error);
                setIsAuthenticated(false);
                setConfigValidated(false);
                handleSnackbarOpen(data.error || "Authentication failed", "error");
                navigate('/login');
                return;
            }

            setConfigValidated(true);
            setIsAuthenticated(true);
        } catch (error) {
            console.error('Authentication check failed:', error);
            setIsAuthenticated(false);
            setConfigValidated(false);
            handleSnackbarOpen("Network error during authentication", "error");
            navigate('/login');
        }
    }, [apiKey, userId, businessId, businessApiKey, navigate, setIsAuthenticated, handleSnackbarOpen]);

    // Check authentication on component mount and when credentials change
    useEffect(() => {
        if (apiKey && userId && businessId && businessApiKey) {
            checkAuthentication();
        } else {
            navigate('/login');
        }
    }, [checkAuthentication, apiKey, userId, businessId, businessApiKey, navigate]);

    const saveConfig = async () => {
        if (!apiKey || !userId || !businessId || !businessApiKey) {
            handleSnackbarOpen("Please enter all the configuration values", "warning");
            return;
        }

        setIsLoading(true);
        try {
            const credentials = {
                apiKey,
                userId,
                businessId,
                businessApiKey
            };

            const response = await fetch('/api/save-config', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${apiKey}`
                },
                credentials: 'include',
                body: JSON.stringify(credentials)
            });

            if (!response.ok) {
                throw new Error(`Server error: ${response.status}`);
            }

            const data = await response.json();

            if (data.success) {
                handleSnackbarOpen("Configuration saved successfully!", "success");
                setConfigValidated(true);
                // Don't clear credentials on success as they're needed for subsequent requests
            } else {
                handleSnackbarOpen(data.error || "Failed to save configuration", "error");
                setConfigValidated(false);
                setIsAuthenticated(false);
                navigate('/login');
            }
        } catch (error) {
            console.error('Save config error:', error);
            handleSnackbarOpen(error.message || "An error occurred while saving configuration", "error");
            setConfigValidated(false);
            setIsAuthenticated(false);
            navigate('/login');
        } finally {
            setIsLoading(false);
        }
    };

    // If not validated and not loading, show nothing (will redirect in useEffect)
    if (!configValidated && !isLoading) {
        return null;
    }

    return (
        <Card sx={{ mt: 2 }}>
            <CardContent>
                <Typography variant="h6" gutterBottom>Configuration</Typography>
                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
                    <TextField
                        label="ICMP API Key"
                        variant="outlined"
                        value={apiKey}
                        onChange={(e) => setApiKey(e.target.value)}
                        disabled={isLoading}
                        type="password"
                        required
                        error={!apiKey}
                        helperText={!apiKey ? "ICMP API Key is required" : ""}
                    />
                    <TextField
                        label="Business API Key"
                        variant="outlined"
                        value={businessApiKey}
                        onChange={(e) => setBusinessApiKey(e.target.value)}
                        disabled={isLoading}
                        type="password"
                        required
                        error={!businessApiKey}
                        helperText={!businessApiKey ? "Business API Key is required" : ""}
                    />
                    <TextField 
                        label="User ID" 
                        variant="outlined" 
                        value={userId}
                        onChange={(e) => setUserId(e.target.value)}
                        disabled={isLoading}
                        required
                        error={!userId}
                        helperText={!userId ? "User ID is required" : ""}
                    />
                    <TextField 
                        label="Business ID" 
                        variant="outlined" 
                        value={businessId}
                        onChange={(e) => setBusinessId(e.target.value)}
                        disabled={isLoading}
                        required
                        error={!businessId}
                        helperText={!businessId ? "Business ID is required" : ""}
                    />

                    <Button 
                        variant="contained" 
                        onClick={saveConfig} 
                        disabled={isLoading || !apiKey || !userId || !businessId || !businessApiKey}
                    >
                        {isLoading ? <CircularProgress size={24} /> : 'Save Config'}
                    </Button>
                </Box>
            </CardContent>
        </Card>
    );
}

export default Configuration;
