import React from 'react';
import { TextField, Button, Typography, Card, CardContent, Box } from '@mui/material';

function Configuration({
    apiKey,
    setApiKey,
    userId,
    setUserId,
    businessId,
    setBusinessId,
    businessApiKey,
    setBusinessApiKey,
    handleSnackbarOpen
}) {
    const saveConfig = async () => {
        // Validate local
        if (!apiKey || !userId || !businessId || !businessApiKey) {
            handleSnackbarOpen("Please enter all the configuration", "warning");
            return;
        }

        localStorage.setItem('icmpApiKey', apiKey);
        localStorage.setItem('userId', userId);
        localStorage.setItem('businessId', businessId);
        localStorage.setItem('businessApiKey', businessApiKey);

        handleSnackbarOpen("Configuration Saved!", "success");
    };

    return (
        <Card sx={{ mt: 2 }}>
            <CardContent>
                <Typography variant="h6" gutterBottom>Configuration</Typography>
                <Typography variant="body2" color="error">
                    <b>DISCLAIMER:</b> Storing the ICMP API Key in local storage is NOT SECURE and should never be done in a
                    production environment. This is for demonstration purposes only!
                </Typography>
                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
                    <TextField
                        label="ICMP API Key"
                        variant="outlined"
                        value={apiKey}
                        onChange={(e) => {
                            setApiKey(e.target.value);
                        }}
                    />
                    <TextField
                        label="Business API Key"
                        variant="outlined"
                        value={businessApiKey}
                        onChange={(e) => setBusinessApiKey(e.target.value)}
                    />
                    <TextField label="User ID" variant="outlined" value={userId}
                        onChange={(e) => setUserId(e.target.value)} />
                    <TextField label="Business ID" variant="outlined" value={businessId}
                        onChange={(e) => setBusinessId(e.target.value)} />

                    <Button variant="contained" onClick={saveConfig}>Save Config</Button>

                </Box>
            </CardContent>
        </Card>
    );
}

export default Configuration;
