import React, { useState } from 'react';
import { TextField, Button, Typography } from '@mui/material';
import { sendMessageApi } from '../services/testService';

const SendMessage = ({ businessId, businessApiKey, handleSnackbarOpen }) => {
  const [message, setMessage] = useState('');
  const [messageOutput, setMessageOutput] = useState('');

  const handleSendMessage = async () => {
    if (!message) {
      handleSnackbarOpen("Please enter a message.", "warning");
      return;
    }
    try {
      const response = await sendMessageApi(businessId, message, businessApiKey);
      setMessageOutput(JSON.stringify(response, null, 2));
      handleSnackbarOpen("Message sent!", "success");
    } catch (error) {
      setMessageOutput(`Error: ${error.message}`);
      handleSnackbarOpen(error.message, "error");
    }
  };

  return (
    <div>
      <Typography variant="h6">Send Message</Typography>
      <TextField
        fullWidth
        label="Message"
        variant="outlined"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
      />
      <Button variant="contained" onClick={handleSendMessage}>
        Send Message
      </Button>
      <pre>{messageOutput}</pre>
    </div>
  );
};

export default SendMessage;
