import React, { useState, useEffect } from 'react';
import AgentSection from '../AgentSection';
import { Snackbar, Alert } from '@mui/material';

// Define your backend API base URL
const API_BASE_URL = 'http://localhost:5000';

function BusinessDetailsView() {
  const [businessData, setBusinessData] = useState(null);
  const [editFormData, setEditFormData] = useState({}); // State for form data
  const [agents, setAgents] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  // TODO: Replace with actual business ID from context/props/state management
  const businessId = '7ae167a0-d864-43b9-bdaf-fcba35b33f27'; // Replace with a REAL ID for testing

  const handleSnackbarOpen = (message, severity = 'success') => {
    setSnackbar({ open: true, message, severity });
  };

  const handleSnackbarClose = () => {
    setSnackbar({ ...snackbar, open: false });
  };

  useEffect(() => {
    const fetchData = async () => {
      if (!businessId || businessId === 'YOUR_BUSINESS_ID_HERE') {
          setError("Business ID not available.");
          setIsLoading(false);
          return;
      }
      setIsLoading(true);
      setError(null); // Clear previous errors
      console.log(`Fetching data for business ID: ${businessId}`);

      try {
        // --- Fetch Business Details ---
        const businessResponse = await fetch(`${API_BASE_URL}/businesses/${businessId}`, {
          method: 'GET',
          credentials: 'include', // Important to send cookies
          headers: {
            'Accept': 'application/json',
            // Authorization header might be needed if not using cookies, e.g.:
            // 'Authorization': `Bearer ${your_business_api_key}`
          },
        });

        if (!businessResponse.ok) {
          const errorData = await businessResponse.json().catch(() => ({})); // Try to get error details
          throw new Error(`HTTP error ${businessResponse.status}: ${errorData.message || 'Failed to fetch business details'}`);
        }
        const fetchedBusinessData = await businessResponse.json();
        console.log("Fetched Business Data:", fetchedBusinessData);
        setBusinessData(fetchedBusinessData);
        setEditFormData({ // Initialize form data when data is fetched
            business_name: fetchedBusinessData.business_name || '',
            business_description: fetchedBusinessData.business_description || '',
            address: fetchedBusinessData.address || '',
            phone_number: fetchedBusinessData.phone_number || '',
            website: fetchedBusinessData.website || ''
        });

        // --- Fetch Agents ---
        const agentsResponse = await fetch(`${API_BASE_URL}/agents?business_id=${businessId}`, {
           method: 'GET',
           credentials: 'include', // Important to send cookies
           headers: {
             'Accept': 'application/json',
           },
        });

        if (!agentsResponse.ok) {
           const errorData = await agentsResponse.json().catch(() => ({}));
           // Don't throw, maybe just log? Or set a separate error state?
           console.error(`HTTP error ${agentsResponse.status}: ${errorData.message || 'Failed to fetch agents'}`);
           setError(prev => prev ? `${prev} | Failed to fetch agents.` : "Failed to fetch agents."); // Append or set error
           setAgents([]); // Ensure agents list is empty on error
        } else {
            const fetchedAgents = await agentsResponse.json();
            console.log("Fetched Agents:", fetchedAgents);
            setAgents(fetchedAgents);
        }

      } catch (err) {
        console.error("Error fetching data:", err);
        setError(err.message || "An unexpected error occurred while fetching data.");
        setBusinessData(null); // Clear data on error
        setAgents([]);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();

  }, [businessId]); // Re-fetch if businessId changes

  const handleInputChange = (e) => {
      const { name, value } = e.target;
      setEditFormData(prevData => ({
          ...prevData,
          [name]: value
      }));
  };

  const handleEditToggle = () => {
      if (!isEditing && businessData) {
          // Reset form data to current business data when entering edit mode
          setEditFormData({
              business_name: businessData.business_name || '',
              business_description: businessData.business_description || '',
              address: businessData.address || '',
              phone_number: businessData.phone_number || '',
              website: businessData.website || ''
          });
      }
      setIsEditing(!isEditing);
      setError(null); // Clear errors when toggling mode
  };

  const handleSaveChanges = async (e) => {
      e.preventDefault(); // Prevent default form submission
      setIsLoading(true); // Indicate saving process
      setError(null);
      console.log("Saving changes:", editFormData);

      try {
           const response = await fetch(`${API_BASE_URL}/businesses/${businessId}`, {
               method: 'PUT',
               credentials: 'include',
               headers: {
                   'Content-Type': 'application/json',
                   'Accept': 'application/json',
               },
               body: JSON.stringify(editFormData)
           });

           if (!response.ok) {
                const errorData = await response.json().catch(() => ({}));
                throw new Error(`HTTP error ${response.status}: ${errorData.message || 'Failed to save changes'}`);
           }

           const result = await response.json();
           console.log("Save successful:", result);

           // Optimistically update local state, or re-fetch data
           setBusinessData(prevData => ({ ...prevData, ...editFormData }));
           setIsEditing(false);

      } catch (err) {
          console.error("Error saving changes:", err);
          setError(err.message || "An unexpected error occurred while saving.");
      } finally {
          setIsLoading(false); // Finish loading indicator
      }
  };

  if (isLoading && !businessData) { // Show initial loading
    return <div>Loading business details...</div>;
  }

  // Show error prominently if one occurred
  if (error && !isEditing) { // Don't block editing form with load errors
    return <div style={{ color: 'red' }}>Error: {error}</div>;
  }

  if (!businessData && !isLoading) { // Handle case where loading finished but no data (and no specific load error)
      return <div>No business data found or failed to load.</div>;
  }
  // Render component content only if businessData is available
  if (!businessData) return null; // Or a placeholder/skeleton UI

  return (
    <div>
      <h2>Business Details</h2>
       {/* Display save errors specifically above the form */}
       {isEditing && error && <div style={{ color: 'red', marginBottom: '10px' }}>Save Error: {error}</div>}
       {/* Indicate loading state during save */}
       {isEditing && isLoading && <div>Saving...</div>}

      {isEditing ? (
        // --- Edit Mode ---
        <form onSubmit={handleSaveChanges}>
            <div>
                <label htmlFor="business_name">Name:</label>
                <input
                    type="text"
                    id="business_name"
                    name="business_name"
                    value={editFormData.business_name}
                    onChange={handleInputChange}
                    required // Assuming name is required
                />
            </div>
            <div>
                 <label htmlFor="business_description">Description:</label>
                <textarea
                    id="business_description"
                    name="business_description"
                    value={editFormData.business_description}
                    onChange={handleInputChange}
                />
            </div>
             <div>
                 <label htmlFor="address">Address:</label>
                <input
                    type="text"
                    id="address"
                    name="address"
                    value={editFormData.address}
                    onChange={handleInputChange}
                />
            </div>
             <div>
                 <label htmlFor="phone_number">Phone:</label>
                <input
                    type="tel" // Use tel type for phone numbers
                    id="phone_number"
                    name="phone_number"
                    value={editFormData.phone_number}
                    onChange={handleInputChange}
                />
            </div>
             <div>
                 <label htmlFor="website">Website:</label>
                <input
                    type="url" // Use url type for websites
                    id="website"
                    name="website"
                    value={editFormData.website}
                    onChange={handleInputChange}
                />
            </div>
            {/* Add inputs for address, phone, website */}
            <button type="submit" disabled={isLoading}>Save Changes</button>
            <button type="button" onClick={handleEditToggle} disabled={isLoading}>Cancel</button>
        </form>
      ) : (
        // --- View Mode ---
        <div>
          <p><strong>Name:</strong> {businessData.business_name}</p>
          <p><strong>Description:</strong> {businessData.business_description || '-'}</p>
          <p><strong>Address:</strong> {businessData.address || '-'}</p>
          <p><strong>Phone:</strong> {businessData.phone_number || '-'}</p>
          <p><strong>Website:</strong> {businessData.website ? <a href={businessData.website} target="_blank" rel="noopener noreferrer">{businessData.website}</a> : '-'}</p>
          <hr/>
          <p><strong>Business ID:</strong> {businessData.business_id}</p>
          <p><strong>Owner ID:</strong> {businessData.owner_id}</p>
          <button onClick={handleEditToggle}>Edit Details</button>
        </div>
      )}

      <hr />

      {/* Replace the old agent list with our AgentSection component */}
      <AgentSection 
        handleSnackbarOpen={handleSnackbarOpen} 
        onAgentSelect={(agentId) => console.log('Selected agent:', agentId)}
      />
      
      {/* Snackbar for notifications */}
      <Snackbar 
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={handleSnackbarClose}
        anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
      >
        <Alert onClose={handleSnackbarClose} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </div>
  );
}

export default BusinessDetailsView;
