import React, { useState } from 'react';
import {
    Typography,
    Card,
    CardContent,
    List,
    ListItem,
    ListItemText,
    ListItemIcon,
    IconButton,
    CircularProgress,
    Alert,
    Button,
    Box,
    Divider
} from '@mui/material';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import EditIcon from '@mui/icons-material/Edit';
import useAgents from '../hooks/useAgents';

function AgentSection({ handleSnackbarOpen, onAgentSelect }) {
    const { agents, isLoading, error, refreshAgents } = useAgents(handleSnackbarOpen);
    const [selectedAgentId, setSelectedAgentId] = useState(null);

    const handleAgentClick = (agentId) => {
        console.log("Selected Agent ID:", agentId);
        setSelectedAgentId(agentId);
        if (onAgentSelect) {
            onAgentSelect(agentId);
        }
    };

    const handleCreateAgent = () => {
        console.log("Create New Agent clicked");
        handleSnackbarOpen("Create agent functionality not yet implemented.", "info");
    };

    const handleEditAgent = (agentId, event) => {
        event.stopPropagation();
        console.log("Edit Agent clicked:", agentId);
        handleSnackbarOpen(`Edit agent (${agentId}) functionality not yet implemented.`, "info");
    };

    let agentListContent;
    if (isLoading) {
        agentListContent = (
            <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', p: 2 }}>
                <CircularProgress size={24} />
                <Typography sx={{ ml: 1 }} variant="body2">Loading Agents...</Typography>
            </Box>
        );
    } else if (error) {
        agentListContent = <Alert severity="error">Error loading agents: {error}</Alert>;
    } else if (agents.length > 0) {
        agentListContent = (
            <List dense>
                {agents.map((agent) => (
                    <ListItem
                        key={agent.agent_id}
                        button
                        selected={selectedAgentId === agent.agent_id}
                        onClick={() => handleAgentClick(agent.agent_id)}
                        secondaryAction={
                            <IconButton
                                edge="end"
                                aria-label="edit"
                                onClick={(e) => handleEditAgent(agent.agent_id, e)}
                            >
                                <EditIcon />
                            </IconButton>
                        }
                    >
                        <ListItemText
                            primary={agent.agent_name || 'Unnamed Agent'}
                            secondary={agent.description || 'No description'}
                        />
                    </ListItem>
                ))}
            </List>
        );
    } else {
        agentListContent = <Typography variant="body2" sx={{ p: 2, textAlign: 'center' }}>No agents found for this business.</Typography>;
    }

    return (
        <Card sx={{ mt: 2 }}>
            <CardContent>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 1 }}>
                    <Typography variant="h6" gutterBottom component="div">
                        Agents
                    </Typography>
                    <Button
                        variant="contained"
                        size="small"
                        startIcon={<AddCircleOutlineIcon />}
                        onClick={handleCreateAgent}
                    >
                        Create Agent
                    </Button>
                </Box>
                <Divider sx={{ mb: 1 }}/>
                {agentListContent}
            </CardContent>
        </Card>
    );
}

export default AgentSection;
