import React, { useState, useEffect } from 'react';
import './MyInterface.css';

const API_BASE_URL = 'http://localhost:5000';

function MyInterface() {
    const [apiKey, setApiKey] = useState(localStorage.getItem('icmpApiKey') || '');
    const [userId, setUserId] = useState('');
    const [businessId, setBusinessId] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [businessName, setBusinessName] = useState('');
    const [templateName, setTemplateName] = useState('');
    const [templateText, setTemplateText] = useState('');
    const [stageName, setStageName] = useState('');
    const [stageDescription, setStageDescription] = useState('');
    const [stageType, setStageType] = useState('');
    const [selectionTemplateId, setSelectionTemplateId] = useState('');
    const [selectionCustomPrompt, setSelectionCustomPrompt] = useState('');
    const [extractionTemplateId, setExtractionTemplateId] = useState('');
    const [extractionCustomPrompt, setExtractionCustomPrompt] = useState('');
    const [responseTemplateId, setResponseTemplateId] = useState('');
    const [responseCustomPrompt, setResponseCustomPrompt] = useState('');
    const [messageInput, setMessageInput] = useState('');

    const [configOutput, setConfigOutput] = useState('');
    const [userOutput, setUserOutput] = useState('');
    const [businessOutput, setBusinessOutput] = useState('');
    const [templateOutput, setTemplateOutput] = useState('');
    const [stageOutput, setStageOutput] = useState('');
    const [messageOutput, setMessageOutput] = useState('');


    const apiCall = async (url, method = 'GET', body = null) => {
        const headers = {
            'Authorization': `Bearer ${apiKey}`,
            'Content-Type': 'application/json'
        };

        const config = {
            method,
            headers
        };

        if (body) {
            config.body = JSON.stringify(body);
        }

        try {
            const response = await fetch(url, config);
            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.error || `API call failed with status ${response.status}`);
            }
            return data;
        } catch (error) {
            console.error('API Call Error:', error);
            setMessageOutput(`API Call Error: ${error.message}`);
            throw error;
        }
    };

    const saveConfig = () => {
        localStorage.setItem('icmpApiKey', apiKey);
        setConfigOutput(`Configuration Saved:\nUser ID: ${userId}\nBusiness ID: ${businessId}\nAPI Key: ${apiKey}`);
    };

    const createUser = async () => {
        setUserOutput("User Created (dummy)");
    };

    const fetchBusinessDetails = async () => {
        if (!businessId) {
            setBusinessOutput("Please enter a Business ID.");
            return;
        }
        try {
            setBusinessOutput("Business endpoint not implemented yet.");
        } catch (error) {
            setBusinessOutput(`Error: ${error.message}`);
        }
    };

    const createStage = async () => {
        const stageData = {
            business_id: businessId,
            stage_name: stageName,
            stage_description: stageDescription,
            stage_type: stageType,
            selection_template_id: selectionTemplateId || null,
            selection_custom_prompt: selectionCustomPrompt || null,
            extraction_template_id: extractionTemplateId || null,
            extraction_custom_prompt: extractionCustomPrompt || null,
            response_template_id: responseTemplateId || null,
            response_custom_prompt: responseCustomPrompt || null
        };

        try {
            const data = await apiCall(`${API_BASE_URL}/stages`, 'POST', stageData);
            setStageOutput(`Stage Created: ${data.stage_id}`);
        } catch (error) {
            setStageOutput(`Error: ${error.message}`);
        }
    };

    const fetchStages = async () => {
        if (!businessId) {
            setStageOutput("Please enter a Business ID.");
            return;
        }
        try {
            const data = await apiCall(`${API_BASE_URL}/stages/${businessId}`);
            setStageOutput(JSON.stringify(data, null, 2));
        } catch (error) {
            setStageOutput(`Error: ${error.message}`);
        }
    };

    const createTemplate = async () => {
        const templateData = {
            template_name: templateName,
            template_text: templateText
        };
        try {
            const data = await apiCall(`${API_BASE_URL}/templates`, 'POST', templateData);
            setTemplateOutput(`Template Created: ${data.template_id}`);
        } catch (error) {
            setTemplateOutput(`Error: ${error.message}`);
        }
    };

    const fetchTemplates = async () => {
        try {
            const data = await apiCall(`${API_BASE_URL}/templates`);
            setTemplateOutput(JSON.stringify(data, null, 2));
        } catch (error) {
            setTemplateOutput(`Error: ${error.message}`);
        }
    };

    const processMessage = async () => {
        const messageData = {
            business_id: businessId,
            user_id: userId,
            message: messageInput
        };
        try {
            const data = await apiCall(`${API_BASE_URL}/message`, 'POST', messageData);
            setMessageOutput(`Response: ${data.response}\nConversation ID: ${data.conversation_id}`);
        } catch (error) {
            setMessageOutput(`Error: ${error.message}`);
        }
    };

    useEffect(() => {
        // When the app load, load the API KEY
        const storedApiKey = localStorage.getItem('icmpApiKey');
        if (storedApiKey) {
            setApiKey(storedApiKey);
        }
    }, []);

    return (
        <div className="container">
            <h1>ICMP Proof of Concept</h1>

            <div className="section">
                <h2>Configuration</h2>
                <p style={{ color: "red" }}><b>DISCLAIMER: Storing the API Key in local storage is NOT SECURE and should never be done in a production environment. This is for demonstration purposes only!</b></p>
                <label>API Key:</label><input type="text" id="apiKey" placeholder="Enter ICMP API Key" value={apiKey} onChange={(e) => setApiKey(e.target.value)} />
                <label>User ID:</label><input type="text" id="userId" placeholder="Enter User ID" value={userId} onChange={(e) => setUserId(e.target.value)} />
                <label>Business ID:</label><input type="text" id="businessId" placeholder="Enter Business ID" value={businessId} onChange={(e) => setBusinessId(e.target.value)} />
                <button onClick={saveConfig}>Save Config</button>
                <pre className="output">{configOutput}</pre>
            </div>

            <div className="section">
                <h2>User Management</h2>
                <label>First Name:</label><input type="text" id="firstName" value={firstName} onChange={(e) => setFirstName(e.target.value)} />
                <label>Last Name:</label><input type="text" id="lastName" value={lastName} onChange={(e) => setLastName(e.target.value)} />
                <label>Email:</label><input type="text" id="email" value={email} onChange={(e) => setEmail(e.target.value)} />
                <button onClick={createUser}>Create User</button>
                <pre className="output">{userOutput}</pre>
            </div>

            <div className="section">
                <h2>Business Management</h2>
                <label>Business Name:</label><input type="text" id="businessName" value={businessName} onChange={(e) => setBusinessName(e.target.value)} />
                <button onClick={() => console.log("Create Business not yet implemented")}>Create Business</button>
                <button onClick={fetchBusinessDetails}>Fetch Business Details</button>
                <pre className="output">{businessOutput}</pre>
            </div>

            <div className="section">
                <h2>Template Management</h2>
                <label>Template Name:</label><input type="text" id="templateName" value={templateName} onChange={(e) => setTemplateName(e.target.value)} />
                <label>Template Text:</label><textarea id="templateText" value={templateText} onChange={(e) => setTemplateText(e.target.value)}></textarea>
                <button onClick={createTemplate}>Create Template</button>
                <button onClick={fetchTemplates}>Fetch Templates</button>
                <pre className="output">{templateOutput}</pre>
            </div>

            <div className="section">
                <h2>Stage Management</h2>
                <label>Stage Name:</label><input type="text" id="stageName" value={stageName} onChange={(e) => setStageName(e.target.value)} />
                <label>Description:</label><input type="text" id="stageDescription" value={stageDescription} onChange={(e) => setStageDescription(e.target.value)} />
                <label>Type:</label><input type="text" id="stageType" value={stageType} onChange={(e) => setStageType(e.target.value)} />
                <label>Selection Template ID:</label><input type="text" id="selectionTemplateId" value={selectionTemplateId} onChange={(e) => setSelectionTemplateId(e.target.value)} />
                <label>Selection Custom Prompt:</label><textarea id="selectionCustomPrompt" value={selectionCustomPrompt} onChange={(e) => setSelectionCustomPrompt(e.target.value)}></textarea>
                <label>Extraction Template ID:</label><input type="text" id="extractionTemplateId" value={extractionTemplateId} onChange={(e) => setExtractionTemplateId(e.target.value)} />
                <label>Extraction Custom Prompt:</label><textarea id="extractionCustomPrompt" value={extractionCustomPrompt} onChange={(e) => setExtractionCustomPrompt(e.target.value)}></textarea>
                <label>Response Template ID:</label><input type="text" id="responseTemplateId" value={responseTemplateId} onChange={(e) => setResponseTemplateId(e.target.value)} />
                <label>Response Custom Prompt:</label><textarea id="responseCustomPrompt" value={responseCustomPrompt} onChange={(e) => setResponseCustomPrompt(e.target.value)}></textarea>
                <button onClick={createStage}>Create Stage</button>
                <button onClick={fetchStages}>Fetch Stages</button>
                <pre className="output">{stageOutput}</pre>
            </div>

            <div className="section">
                <h2>Send Message</h2>
                <label>Message:</label><textarea id="messageInput" value={messageInput} onChange={(e) => setMessageInput(e.target.value)}></textarea>
                <button onClick={processMessage}>Send</button>
                <pre className="output">{messageOutput}</pre>
            </div>
        </div>
    );
}

export default MyInterface;
