import React, { useState, useEffect } from 'react';
import { TextField, Button, Typography, Card, CardContent, Box, CircularProgress } from '@mui/material';
import { useNavigate } from 'react-router-dom';

function Configuration({
    apiKey,
    setApiKey,
    userId,
    setUserId,
    businessId,
    setBusinessId,
    businessApiKey,
    setBusinessApiKey,
    handleSnackbarOpen,
    setIsAuthenticated
}) {
    const [isLoading, setIsLoading] = useState(false);
    const [configValidated, setConfigValidated] = useState(false);
    const navigate = useNavigate();

    const validateConfig = async () => {
        if (!apiKey || !userId || !businessId || !businessApiKey) {
            throw new Error('Missing required credentials');
        }

        const response = await fetch('/validate_config', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            credentials: 'include',
            body: JSON.stringify({
                apiKey,
                userId,
                businessId,
                businessApiKey
            })
        });

        if (!response.ok) {
            const errorData = await response.json().catch(() => null);
            throw new Error(errorData?.message || 'Failed to validate configuration');
        }

        const data = await response.json();
        if (!data.isValid) {
            throw new Error(data.error || 'Authentication failed');
        }

        return data;
    };

    // Check authentication on component mount and when credentials change
    useEffect(() => {
        const checkAuth = async () => {
            if (!apiKey || !userId || !businessId || !businessApiKey) {
                setIsAuthenticated(false);
                setConfigValidated(false);
                navigate('/login');
                return;
            }

            try {
                await validateConfig();
                setIsAuthenticated(true);
                setConfigValidated(true);
            } catch (error) {
                console.error('Authentication check failed:', error);
                setIsAuthenticated(false);
                setConfigValidated(false);
                handleSnackbarOpen(error.message || "Authentication failed", "error");
                navigate('/login');
            }
        };

        checkAuth();
    }, [apiKey, userId, businessId, businessApiKey, setIsAuthenticated, handleSnackbarOpen, navigate, validateConfig]);

    const saveConfig = async () => {
        if (!apiKey || !userId || !businessId || !businessApiKey) {
            handleSnackbarOpen("Please enter all the configuration values", "warning");
            return;
        }

        setIsLoading(true);
        try {
            const response = await fetch('/api/save-config', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                },
                credentials: 'include',
                body: JSON.stringify({
                    apiKey,
                    userId,
                    businessId,
                    businessApiKey
                })
            });

            if (!response.ok) {
                const errorData = await response.json().catch(() => null);
                throw new Error(errorData?.message || 'Failed to save configuration');
            }

            const data = await response.json();
            if (data.success) {
                handleSnackbarOpen("Configuration saved successfully!", "success");
                setConfigValidated(true);
            } else {
                throw new Error(data.error || "Failed to save configuration");
            }
        } catch (error) {
            console.error('Save error:', error);
            handleSnackbarOpen(error.message || "An error occurred while saving configuration", "error");
            setConfigValidated(false);
            setIsAuthenticated(false);
            navigate('/login');
        } finally {
            setIsLoading(false);
        }
    };

    const handleLogout = () => {
        // Clear stored credentials
        localStorage.removeItem('apiKey');
        localStorage.removeItem('userId');
        localStorage.removeItem('businessId');
        localStorage.removeItem('businessApiKey');

        // Reset state
        setApiKey('');
        setUserId('');
        setBusinessId('');
        setBusinessApiKey('');
        setIsAuthenticated(false);
        setConfigValidated(false);

        // Show success message
        handleSnackbarOpen('Logged out successfully', 'success');
        navigate('/login');
    };

    return (
        <Card sx={{ mt: 2 }}>
            <CardContent>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                    <Typography variant="h6">Configuration</Typography>
                    <Button 
                        variant="outlined" 
                        color="secondary" 
                        onClick={handleLogout}
                        disabled={isLoading}
                    >
                        Logout
                    </Button>
                </Box>
                {isLoading ? (
                    <Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}>
                        <CircularProgress />
                    </Box>
                ) : (
                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
                        <TextField
                            label="ICMP API Key"
                            variant="outlined"
                            value={apiKey}
                            onChange={(e) => setApiKey(e.target.value)}
                            disabled={isLoading}
                            type="password"
                            required
                            error={!apiKey}
                            helperText={!apiKey ? "ICMP API Key is required" : ""}
                        />
                        <TextField
                            label="Business API Key"
                            variant="outlined"
                            value={businessApiKey}
                            onChange={(e) => setBusinessApiKey(e.target.value)}
                            disabled={isLoading}
                            type="password"
                            required
                            error={!businessApiKey}
                            helperText={!businessApiKey ? "Business API Key is required" : ""}
                        />
                        <TextField 
                            label="User ID" 
                            variant="outlined" 
                            value={userId}
                            onChange={(e) => setUserId(e.target.value)}
                            disabled={isLoading}
                            required
                            error={!userId}
                            helperText={!userId ? "User ID is required" : ""}
                        />
                        <TextField 
                            label="Business ID" 
                            variant="outlined" 
                            value={businessId}
                            onChange={(e) => setBusinessId(e.target.value)}
                            disabled={isLoading}
                            required
                            error={!businessId}
                            helperText={!businessId ? "Business ID is required" : ""}
                        />

                        <Button 
                            variant="contained" 
                            onClick={saveConfig} 
                            disabled={isLoading || !apiKey || !userId || !businessId || !businessApiKey}
                        >
                            Save Config
                        </Button>
                    </Box>
                )}
            </CardContent>
        </Card>
    );
}

export default Configuration;
