import React, { useState, useEffect, useCallback } from 'react';
import {
  Box,
  Paper,
  Typography,
  Button,
  Grid,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Alert,
  Snackbar,
  Divider,
  Tabs,
  Tab
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import TemplateEditor from './TemplateEditor';
import StageFlow from './StageFlow';
import CircularProgress from '@mui/material/CircularProgress';

const StageManager = ({ businessId }) => {
  const [stages, setStages] = useState([]);
  const [selectedStage, setSelectedStage] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [openTemplateEditor, setOpenTemplateEditor] = useState(false);
  const [newStage, setNewStage] = useState({
    stage_name: '',
    stage_description: '',
    stage_type: 'response'
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [activeTab, setActiveTab] = useState(0);
  const [isLoading, setIsLoading] = useState(false);

  const fetchStages = useCallback(async () => {
    setIsLoading(true);
    setError('');
    try {
      const response = await fetch(`/stages?business_id=${businessId}`, {
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to fetch stages');
      const data = await response.json();
      setStages(data);
    } catch (err) {
      setError(err.message || 'Failed to fetch stages');
    } finally {
      setIsLoading(false);
    }
  }, [businessId]);

  useEffect(() => {
    fetchStages();
  }, [fetchStages]);

  const handleCreateStage = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('/stages', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({
          ...newStage,
          business_id: businessId
        }),
      });
      if (!response.ok) throw new Error('Failed to create stage');
      const data = await response.json();
      setStages([...stages, data]);
      setOpenDialog(false);
      setNewStage({ stage_name: '', stage_description: '', stage_type: 'response' });
      setSuccess('Stage created successfully');
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleDeleteStage = async (stageId) => {
    setIsLoading(true);
    try {
      const response = await fetch(`/stages/${stageId}`, {
        method: 'DELETE',
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to delete stage');
      setStages(stages.filter(stage => stage.stage_id !== stageId));
      setSuccess('Stage deleted successfully');
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSaveTemplates = async (updatedStage) => {
    setIsLoading(true);
    try {
      const response = await fetch(`/stages/${updatedStage.stage_id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(updatedStage),
      });
      if (!response.ok) throw new Error('Failed to update templates');
      setStages(stages.map(stage => 
        stage.stage_id === updatedStage.stage_id ? updatedStage : stage
      ));
      setOpenTemplateEditor(false);
      setSuccess('Templates updated successfully');
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handlePreviewTemplates = async (templates) => {
    try {
      const response = await fetch('/stages/preview', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({
          business_id: businessId,
          stage_id: selectedStage.stage_id,
          templates
        }),
      });
      if (!response.ok) throw new Error('Failed to generate preview');
      return await response.json();
    } catch (err) {
      setError(err.message);
      return null;
    }
  };

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  return (
    <Box sx={{ p: 3 }}>
      <Paper sx={{ mb: 3 }}>
        <Tabs value={activeTab} onChange={handleTabChange}>
          <Tab label="Stages" />
          <Tab label="Transitions" />
        </Tabs>
      </Paper>

      {isLoading && (
        <Box sx={{ display: 'flex', justifyContent: 'center', my: 3 }}>
          <CircularProgress />
        </Box>
      )}
      {error && (
        <Alert severity="error" sx={{ my: 2 }}>
          {error}
        </Alert>
      )}

      {!isLoading && !error && activeTab === 0 && (
        <>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
            <Typography variant="h5">Stage Manager</Typography>
            <Button
              startIcon={<AddIcon />}
              variant="contained"
              onClick={() => setOpenDialog(true)}
            >
              Add Stage
            </Button>
          </Box>

          <Grid container spacing={2}>
            <Grid item xs={12} md={4}>
              <Paper>
                <List>
                  {stages.map((stage) => (
                    <ListItem
                      key={stage.stage_id}
                      selected={selectedStage?.stage_id === stage.stage_id}
                      onClick={() => setSelectedStage(stage)}
                    >
                      <ListItemText
                        primary={stage.stage_name}
                        secondary={stage.stage_description}
                      />
                      <ListItemSecondaryAction>
                        <IconButton
                          edge="end"
                          onClick={() => {
                            setSelectedStage(stage);
                            setOpenTemplateEditor(true);
                          }}
                        >
                          <EditIcon />
                        </IconButton>
                        <IconButton
                          edge="end"
                          onClick={() => handleDeleteStage(stage.stage_id)}
                        >
                          <DeleteIcon />
                        </IconButton>
                      </ListItemSecondaryAction>
                    </ListItem>
                  ))}
                </List>
              </Paper>
            </Grid>

            <Grid item xs={12} md={8}>
              {selectedStage && (
                <Paper sx={{ p: 2 }}>
                  <Typography variant="h6" gutterBottom>
                    Stage Details
                  </Typography>
                  <Divider sx={{ my: 1 }} />
                  <Box sx={{ mt: 2 }}>
                    <Typography variant="subtitle2">Name:</Typography>
                    <Typography variant="body1">{selectedStage.stage_name}</Typography>
                  </Box>
                  <Box sx={{ mt: 2 }}>
                    <Typography variant="subtitle2">Description:</Typography>
                    <Typography variant="body1">{selectedStage.stage_description}</Typography>
                  </Box>
                  <Box sx={{ mt: 2 }}>
                    <Typography variant="subtitle2">Type:</Typography>
                    <Typography variant="body1">{selectedStage.stage_type}</Typography>
                  </Box>
                </Paper>
              )}
            </Grid>
          </Grid>
        </>
      )}

      {!isLoading && !error && activeTab === 1 && (
        <StageFlow stages={stages} businessId={businessId} />
      )}

      <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
        <DialogTitle>Create New Stage</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Stage Name"
            fullWidth
            value={newStage.stage_name}
            onChange={(e) => setNewStage({ ...newStage, stage_name: e.target.value })}
          />
          <TextField
            margin="dense"
            label="Description"
            fullWidth
            multiline
            rows={3}
            value={newStage.stage_description}
            onChange={(e) => setNewStage({ ...newStage, stage_description: e.target.value })}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Cancel</Button>
          <Button onClick={handleCreateStage} variant="contained">
            Create
          </Button>
        </DialogActions>
      </Dialog>

      <Dialog
        open={openTemplateEditor}
        onClose={() => setOpenTemplateEditor(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogContent>
          <TemplateEditor
            stage={selectedStage}
            onSave={handleSaveTemplates}
            onPreview={handlePreviewTemplates}
          />
        </DialogContent>
      </Dialog>

      <Snackbar
        open={!!error}
        autoHideDuration={6000}
        onClose={() => setError('')}
      >
        <Alert severity="error" onClose={() => setError('')}>
          {error}
        </Alert>
      </Snackbar>

      <Snackbar
        open={!!success}
        autoHideDuration={6000}
        onClose={() => setSuccess('')}
      >
        <Alert severity="success" onClose={() => setSuccess('')}>
          {success}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default StageManager;
