      try {
        console.log(`Testing with headers:`, headers);

        // Use GET request instead of POST for validate-credentials
        const response = await fetch(`/businesses/validate-credentials?business_id=${businessId}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json',
            ...headers
          },
          credentials: 'include'
        });
        const status = response.status;
        const data = await response.json().catch(() => ({}));
        results.push({
          headers,
