          success: false
        });
        console.error(`Test error:`, err);
      }
    }
    return {
      success: results.some(r => r.success),
      results
    };
  } catch (err) {
    console.error('API key test error:', err);
    return {
      success: false,
      error: err.message
    };
  }
};

