import React, { useState, useEffect } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import { API_BASE_URL } from '../../config';
import {
  Box,
  Paper,
  Typography,
  TextField,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Grid,
  Alert,
  CircularProgress,
  Divider
} from '@mui/material';

function AddEditStageForm() {
    // --- Routing & Context ---
    const { stageId } = useParams(); // Get stageId from URL path for edit mode
    const location = useLocation();
    const queryParams = new URLSearchParams(location.search);
    const agentIdParam = queryParams.get('agent_id'); // Get agent context ('uuid' or 'null')
    const navigate = useNavigate();

    const isEditMode = Boolean(stageId && stageId !== 'new');

    // --- State ---
    const [formData, setFormData] = useState({
        stage_name: '',
        stage_description: 'Default description',
        stage_type: 'conversation',
        stage_selection_template_id: '',
        data_extraction_template_id: '',
        response_generation_template_id: ''
    });
    const [availableTemplates, setAvailableTemplates] = useState({ selection: [], extraction: [], generation: [] });
    const [isLoading, setIsLoading] = useState(true);
    const [isSaving, setIsSaving] = useState(false);
    const [error, setError] = useState(null);
    const [contextAgentName, setContextAgentName] = useState('General');

    // Get stored credentials
    const getStoredCredentials = () => {
        return {
            businessId: localStorage.getItem('businessId') || '',
            businessApiKey: localStorage.getItem('businessApiKey') || ''
        };
    };

    // --- Effects ---
    // 1. Determine context agent name
    useEffect(() => {
        setContextAgentName(agentIdParam === 'null' ? 'General' : `Agent ${agentIdParam}`);
    }, [agentIdParam]);

    // 2. Fetch available templates
    useEffect(() => {
        const fetchTemplates = async () => {
            setError(null);
            setIsLoading(true);
            console.log("Fetching available templates...");
            
            const { businessId, businessApiKey } = getStoredCredentials();
            
            if (!businessId || !businessApiKey) {
                setError('Authentication required. Please log in.');
                setIsLoading(false);
                return;
            }
            
            // Organize templates by type for dropdowns
            const organized = { 
                selection: [], 
                extraction: [], 
                generation: [] 
            };
            
            try {
                // Step 1: Fetch regular templates first
                const regularResponse = await fetch(`${API_BASE_URL}/templates?business_id=${businessId}`, {
                    method: 'GET',
                    headers: { 
                        'Accept': 'application/json',
                        'Authorization': `Bearer ${businessApiKey}`
                    }
                });
                
                if (regularResponse.ok) {
                    const regularTemplates = await regularResponse.json();
                    console.log("Regular templates fetched:", regularTemplates);
                    
                    // Process regular templates
                    regularTemplates.forEach(template => {
                        categorizeTemplate(template, organized);
                    });
                } else {
                    console.warn(`Failed to fetch regular templates: ${regularResponse.status}`);
                }
                
                // Step 2: Now fetch default templates
                const defaultResponse = await fetch(`${API_BASE_URL}/api/templates/default-templates?business_id=${businessId}`, {
                    method: 'GET',
                    headers: { 
                        'Accept': 'application/json',
                        'Authorization': `Bearer ${businessApiKey}`
                    }
                });
                
                if (defaultResponse.ok) {
                    const defaultTemplates = await defaultResponse.json();
                    console.log("Default templates fetched:", defaultTemplates);
                    
                    // Process default templates and add them to the organized object
                    defaultTemplates.forEach(template => {
                        // Add a flag to indicate this is a default template (for UI display)
                        template.isDefault = true;
                        categorizeTemplate(template, organized);
                    });
                } else {
                    console.warn(`Failed to fetch default templates: ${defaultResponse.status}`);
                }
                
                // If we have templates from either source
                if (organized.selection.length > 0 || organized.extraction.length > 0 || organized.generation.length > 0) {
                    setAvailableTemplates(organized);
                    console.log("Templates fetched and organized:", organized);
                } else {
                    throw new Error("No templates found from any source");
                }
            } catch (err) {
                console.error("Error fetching templates:", err);
                setError(err.message || "Failed to load templates. Cannot create/edit stage.");
                setAvailableTemplates({ selection: [], extraction: [], generation: [] }); // Ensure empty on error
            } finally {
                // Don't set isLoading to false here if in edit mode, wait for stage data
                if (!isEditMode) {
                    setIsLoading(false);
                }
            }
        };
        
        // Helper function to categorize a template into the right bucket
        const categorizeTemplate = (template, organized) => {
            const type = template.template_type || '';
            
            // Map template types to categories
            if (type === 'stage_selection' || type === 'default_stage_selection' || 
                type.includes('selection')) {
                organized.selection.push(template);
            } 
            else if (type === 'data_extraction' || type === 'default_data_extraction' || 
                     type.includes('extract')) {
                organized.extraction.push(template);
            } 
            else if (type === 'response_generation' || type === 'default_response_generation' || 
                     type.includes('response') || type.includes('generation')) {
                organized.generation.push(template);
            }
            // For templates with no type, try to infer from name
            else if (type === '' || type === 'generic') {
                const name = (template.template_name || template.name || '').toLowerCase();
                if (name.includes('selection')) {
                    organized.selection.push(template);
                } else if (name.includes('extract')) {
                    organized.extraction.push(template);
                } else if (name.includes('response') || name.includes('generation')) {
                    organized.generation.push(template);
                }
            }
        };
        
        fetchTemplates();
    }, [isEditMode, API_BASE_URL]); // Fetch templates on mount

    // 3. Fetch stage data if in Edit mode (after templates are loaded)
    useEffect(() => {
        const fetchStageDetails = async () => {
            if (!isEditMode) return;

            const { businessId, businessApiKey } = getStoredCredentials();
            
            if (!businessId || !businessApiKey) {
                setError('Authentication required. Please log in.');
                setIsLoading(false);
                return;
            }

            // Continue loading state from template fetch until stage data is loaded/failed
            setIsLoading(true);
            setError(null); // Clear template fetch errors if any
            console.log(`Fetching details for stage ID: ${stageId}, Business ID: ${businessId}`);

            try {
                // Fetch the specific stage
                const response = await fetch(`${API_BASE_URL}/stages/${stageId}?business_id=${businessId}`, {
                    method: 'GET',
                    headers: { 
                        'Accept': 'application/json',
                        'Authorization': `Bearer ${businessApiKey}`
                    }
                });
                
                if (!response.ok) {
                    const errorData = await response.json().catch(() => ({}));
                    throw new Error(`HTTP error ${response.status}: ${errorData.message || 'Failed to fetch stage details'}`);
                }
                
                const stageData = await response.json();
                console.log("Stage data fetched:", stageData);

                // Set form data with existing stage details
                setFormData({
                    stage_name: stageData.stage_name || '',
                    stage_description: stageData.stage_description || '',
                    stage_type: stageData.stage_type || '',
                    stage_selection_template_id: stageData.stage_selection_template_id || '',
                    data_extraction_template_id: stageData.data_extraction_template_id || '',
                    response_generation_template_id: stageData.response_generation_template_id || ''
                });

            } catch (err) {
                console.error("Error fetching stage details for edit:", err);
                setError(err.message || "Failed to load stage details for editing.");
                // Clear form data on error
                setFormData({ 
                    stage_name: '', 
                    stage_description: '', 
                    stage_type: '', 
                    stage_selection_template_id: '', 
                    data_extraction_template_id: '', 
                    response_generation_template_id: '' 
                });
            } finally {
                setIsLoading(false); // Finish loading indicator
            }
        };

        // Only run fetchStageDetails if in edit mode and templates are potentially loaded
        if (isEditMode && availableTemplates.selection.length > 0) {
            fetchStageDetails();
        } else if (isEditMode && error) {
            // If template fetch failed, stop loading for edit mode too
            setIsLoading(false);
        }
    }, [isEditMode, stageId, availableTemplates]); // Dependencies for fetching stage data

    // --- Handlers ---
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        console.log(`Field ${name} changed to: ${value}`);
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsSaving(true);
        setError(null);
        
        const { businessId, businessApiKey } = getStoredCredentials();
        
        if (!businessId || !businessApiKey) {
            setError('Authentication required. Please log in.');
            setIsSaving(false);
            return;
        }
        
        // Log the form state to verify values
        console.log("Form state before submission:", formData);
        
        try {
            const url = isEditMode 
                ? `${API_BASE_URL}/stages/${stageId}?business_id=${businessId}`
                : `${API_BASE_URL}/stages?business_id=${businessId}`;
                
            const method = isEditMode ? 'PUT' : 'POST';
            
            console.log(`Submitting stage data to ${url} using ${method}`);
            
            const response = await fetch(url, {
                method: method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${businessApiKey}`
                },
                body: JSON.stringify(formData)
            });
            
            if (!response.ok) {
                const errorData = await response.json().catch(() => ({}));
                throw new Error(`HTTP error ${response.status}: ${errorData.message || 'Failed to save stage'}`);
            }
            
            const result = await response.json();
            console.log(`Stage ${isEditMode ? 'updated' : 'created'} successfully:`, result);
            
            // Navigate to the stage editor for the newly created stage
            if (!isEditMode && result.stage_id) {
                navigate(`/stage-editor/${result.stage_id}?business_id=${businessId}${agentIdParam ? `&agent_id=${agentIdParam}` : ''}`);
            } else {
                // Show success message and return to stage management
                navigate('/stage-management');
            }
            
        } catch (err) {
            console.error(`Error ${isEditMode ? 'updating' : 'creating'} stage:`, err);
            setError(err.message || `An unexpected error occurred while ${isEditMode ? 'saving' : 'creating'} the stage.`);
        } finally {
            setIsSaving(false);
        }
    };

    const handleCancel = () => {
        navigate('/stage-management');
    };

    // --- Render ---
    if (isLoading) {
        return (
            <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '200px' }}>
                <CircularProgress />
            </Box>
        );
    }

    // If edit mode failed to load stage data
    if (isEditMode && !formData.stage_name && error) {
        return (
            <Box>
                <Alert severity="error" sx={{ mb: 2 }}>
                    {error}
                </Alert>
                <Button variant="outlined" onClick={handleCancel} sx={{ mt: 2 }}>
                    Return to Stage Management
                </Button>
            </Box>
        );
    }

    return (
        <Box component="form" onSubmit={handleSubmit} noValidate>
            <Paper sx={{ p: 3, mb: 3 }}>
                <Typography variant="h6" gutterBottom>
                    {isEditMode ? 'Edit Stage' : 'Add New Stage'}
                </Typography>
                
                {error && (
                    <Alert severity="error" sx={{ mb: 2 }}>
                        {error}
                    </Alert>
                )}
                
                <Grid container spacing={3}>
                    {/* Basic Information */}
                    <Grid item xs={12}>
                        <Typography variant="subtitle1" gutterBottom>
                            Basic Information
                        </Typography>
                        <Divider sx={{ mb: 2 }} />
                    </Grid>
                    
                    <Grid item xs={12} md={6}>
                        <TextField
                            required
                            fullWidth
                            id="stage_name"
                            name="stage_name"
                            label="Stage Name"
                            value={formData.stage_name}
                            onChange={handleInputChange}
                            error={!formData.stage_name}
                            helperText={!formData.stage_name ? "Stage name is required" : ""}
                        />
                    </Grid>
                    
                    <Grid item xs={12} md={6}>
                        <FormControl fullWidth required>
                            <InputLabel id="stage_type_label">Stage Type</InputLabel>
                            <Select
                                labelId="stage_type_label"
                                id="stage_type"
                                name="stage_type"
                                value={formData.stage_type}
                                label="Stage Type"
                                onChange={handleInputChange}
                            >
                                <MenuItem value="conversation">Conversation</MenuItem>
                                <MenuItem value="response">Response</MenuItem>
                                <MenuItem value="input">Input</MenuItem>
                                <MenuItem value="processing">Processing</MenuItem>
                            </Select>
                        </FormControl>
                    </Grid>
                    
                    <Grid item xs={12}>
                        <TextField
                            required
                            fullWidth
                            multiline
                            rows={4}
                            id="stage_description"
                            name="stage_description"
                            label="Stage Description"
                            value={formData.stage_description}
                            onChange={handleInputChange}
                            error={!formData.stage_description}
                            helperText={!formData.stage_description ? "Stage description is required" : ""}
                        />
                    </Grid>
                    
                    {/* Template Information */}
                    <Grid item xs={12}>
                        <Typography variant="subtitle1" gutterBottom sx={{ mt: 2 }}>
                            Template Information
                        </Typography>
                        <Divider sx={{ mb: 2 }} />
                    </Grid>
                    
                    <Grid item xs={12} md={4}>
                        <FormControl fullWidth>
                            <InputLabel id="stage_selection_template_id_label">Stage Selection Template</InputLabel>
                            <Select
                                labelId="stage_selection_template_id_label"
                                id="stage_selection_template_id"
                                name="stage_selection_template_id"
                                value={formData.stage_selection_template_id}
                                label="Stage Selection Template"
                                onChange={handleInputChange}
                            >
                                <MenuItem value="">
                                    <em>None</em>
                                </MenuItem>
                                {availableTemplates.selection.map((template) => (
                                    <MenuItem key={template.template_id} value={template.template_id}>
                                        {template.template_name || template.name || template.template_id}
                                        {template.isDefault ? ' (Default)' : ''}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                    </Grid>
                    
                    <Grid item xs={12} md={4}>
                        <FormControl fullWidth>
                            <InputLabel id="data_extraction_template_id_label">Data Extraction Template</InputLabel>
                            <Select
                                labelId="data_extraction_template_id_label"
                                id="data_extraction_template_id"
                                name="data_extraction_template_id"
                                value={formData.data_extraction_template_id}
                                label="Data Extraction Template"
                                onChange={handleInputChange}
                            >
                                <MenuItem value="">
                                    <em>None</em>
                                </MenuItem>
                                {availableTemplates.extraction.map((template) => (
                                    <MenuItem key={template.template_id} value={template.template_id}>
                                        {template.template_name || template.name || template.template_id}
                                        {template.isDefault ? ' (Default)' : ''}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                    </Grid>
                    
                    <Grid item xs={12} md={4}>
                        <FormControl fullWidth>
                            <InputLabel id="response_generation_template_id_label">Response Generation Template</InputLabel>
                            <Select
                                labelId="response_generation_template_id_label"
                                id="response_generation_template_id"
                                name="response_generation_template_id"
                                value={formData.response_generation_template_id}
                                label="Response Generation Template"
                                onChange={handleInputChange}
                            >
                                <MenuItem value="">
                                    <em>None</em>
                                </MenuItem>
                                {availableTemplates.generation.map((template) => (
                                    <MenuItem key={template.template_id} value={template.template_id}>
                                        {template.template_name || template.name || template.template_id}
                                        {template.isDefault ? ' (Default)' : ''}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                    </Grid>
                </Grid>
                
                <Box sx={{ mt: 3, display: 'flex', justifyContent: 'flex-end' }}>
                    <Button
                        variant="outlined"
                        onClick={handleCancel}
                        sx={{ mr: 2 }}
                    >
                        Cancel
                    </Button>
                    <Button
                        type="submit"
                        variant="contained"
                        color="primary"
                        disabled={isSaving || !formData.stage_name || !formData.stage_description}
                    >
                        {isSaving ? 'Saving...' : (isEditMode ? 'Update Stage' : 'Create Stage')}
                    </Button>
                </Box>
            </Paper>
        </Box>
    );
}

export default AddEditStageForm;
