import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Paper,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  CircularProgress,
  Grid,
  Box,
  IconButton,
  Divider,
  Alert,
  Snackbar,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  Tooltip,
  FormControl,
  InputLabel,
  MenuItem,
  Select
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import ArrowDownwardIcon from '@mui/icons-material/ArrowDownward';
import StageEditor from './StageEditor';

const StageManager = () => {
  const { businessId, agentId } = useParams();
  const navigate = useNavigate();
  const [stages, setStages] = useState([]);
  const [selectedStage, setSelectedStage] = useState(null);
  const [showStageEditor, setShowStageEditor] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [openDialog, setOpenDialog] = useState(false);
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
  const [stageToDelete, setStageToDelete] = useState(null);
  const [newStageName, setNewStageName] = useState('');
  const [availableTemplates, setAvailableTemplates] = useState({
    stage_selection: [],
    data_extraction: [],
    response_generation: []
  });
  const [defaultTemplates, setDefaultTemplates] = useState({
    stage_selection: '',
    data_extraction: '',
    response_generation: ''
  });

  // Fetch templates for dropdowns
  const fetchTemplates = useCallback(async () => {
    try {
      const response = await fetch('/templates', {
        credentials: 'include'
      });
      
      if (!response.ok) throw new Error('Failed to fetch templates');
      
      const templates = await response.json();
      
      // Group templates by type
      const templatesByType = {
        stage_selection: [],
        data_extraction: [],
        response_generation: []
      };
      
      const defaultTemplateIds = {
        stage_selection: '',
        data_extraction: '',
        response_generation: ''
      };
      
      templates.forEach(template => {
        // Check if this is a regular template type
        if (template.template_type in templatesByType) {
          templatesByType[template.template_type].push(template);
        }
        
        // Check if this is a default template type
        if (template.template_type.startsWith('default_')) {
          // Extract the base type (e.g., from 'default_stage_selection' to 'stage_selection')
          const baseType = template.template_type.replace('default_', '');
          if (baseType in defaultTemplateIds) {
            // Store the first default template's ID for each type
            if (!defaultTemplateIds[baseType]) {
              defaultTemplateIds[baseType] = template.template_id;
            }
          }
        }
      });
      
      setAvailableTemplates(templatesByType);
      setDefaultTemplates(defaultTemplateIds);
    } catch (err) {
      setError(err.message);
    }
  }, []);

  // Fetch stages based on business ID and agent ID
  const fetchStages = useCallback(async () => {
    setIsLoading(true);
    try {
      let url = '/stages';
      if (businessId) {
        url += `?business_id=${businessId}`;
        if (agentId) {
          url += `&agent_id=${agentId}`;
        }
      }
      
      const response = await fetch(url, {
        credentials: 'include'
      });
      
      if (!response.ok) throw new Error('Failed to fetch stages');
      
      const data = await response.json();
      setStages(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  }, [businessId, agentId]);

  useEffect(() => {
    fetchTemplates();
    fetchStages();
  }, [fetchTemplates, fetchStages]);

  const handleCreateStage = async () => {
    try {
      if (!newStageName.trim()) {
        setError('Stage name is required');
        return;
      }

      const response = await fetch('/stages', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({
          stage_name: newStageName,
          business_id: businessId,
          agent_id: agentId,
          // Use default template IDs from our state
          stage_selection_template_id: defaultTemplates.stage_selection || null,
          data_extraction_template_id: defaultTemplates.data_extraction || null,
          response_generation_template_id: defaultTemplates.response_generation || null,
          stage_config: { order: stages.length + 1 }
        }),
      });
      
      if (!response.ok) throw new Error('Failed to create stage');
      
      await fetchStages();
      setOpenDialog(false);
      setNewStageName('');
      setSuccess('Stage created successfully');

      // Get the new stage ID and open editor
      const newStage = await response.json();
      if (newStage && newStage.stage_id) {
        const createdStage = {
          stage_id: newStage.stage_id,
          stage_name: newStageName,
          business_id: businessId,
          agent_id: agentId,
          stage_selection_template_id: defaultTemplates.stage_selection || null,
          data_extraction_template_id: defaultTemplates.data_extraction || null,
          response_generation_template_id: defaultTemplates.response_generation || null,
          stage_config: { order: stages.length + 1 }
        };
        setSelectedStage(createdStage);
        setShowStageEditor(true);
      }
    } catch (err) {
      setError(err.message);
    }
  };

  const handleDeleteStage = async () => {
    if (!stageToDelete) return;
    
    try {
      const response = await fetch(`/stages/${stageToDelete.stage_id}`, {
        method: 'DELETE',
        credentials: 'include'
      });
      
      if (!response.ok) throw new Error('Failed to delete stage');
      
      await fetchStages();
      setOpenDeleteDialog(false);
      setStageToDelete(null);
      setSuccess('Stage deleted successfully');
      
      // If this was the selected stage, clear selection
      if (selectedStage && selectedStage.stage_id === stageToDelete.stage_id) {
        setSelectedStage(null);
        setShowStageEditor(false);
      }
    } catch (err) {
      setError(err.message);
    }
  };

  const handleUpdateStage = async (updatedStage) => {
    try {
      const response = await fetch(`/stages/${updatedStage.stage_id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(updatedStage),
      });
      
      if (!response.ok) throw new Error('Failed to update stage');
      
      await fetchStages();
      setShowStageEditor(false);
      setSelectedStage(null);
      setSuccess('Stage updated successfully');
    } catch (err) {
      setError(err.message);
    }
  };

  const handleMoveStage = async (stageId, direction) => {
    try {
      const currentIndex = stages.findIndex(s => s.stage_id === stageId);
      if (currentIndex === -1) return;
      
      // If trying to move first item up or last item down, do nothing
      if ((currentIndex === 0 && direction === 'up') || 
          (currentIndex === stages.length - 1 && direction === 'down')) {
        return;
      }
      
      const targetIndex = direction === 'up' ? currentIndex - 1 : currentIndex + 1;
      
      // Swap the order values in the stage_config
      const updatedStages = [...stages];
      const currentStage = { ...updatedStages[currentIndex] };
      const targetStage = { ...updatedStages[targetIndex] };
      
      // Update stage_config.order values
      currentStage.stage_config = { ...currentStage.stage_config, order: targetIndex + 1 };
      targetStage.stage_config = { ...targetStage.stage_config, order: currentIndex + 1 };
      
      // Update both stages in the database
      const currentUpdate = fetch(`/stages/${currentStage.stage_id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(currentStage),
      });
      
      const targetUpdate = fetch(`/stages/${targetStage.stage_id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(targetStage),
      });
      
      await Promise.all([currentUpdate, targetUpdate]);
      
      // Refresh stages
      await fetchStages();
      setSuccess('Stage order updated');
    } catch (err) {
      setError(err.message);
    }
  };

  const handlePreviewTemplate = async (templates) => {
    try {
      const response = await fetch('/templates/preview', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({
          templates,
          context: {
            conversation_history: ["User: Hello, I need help.", "AI: How can I assist you today?"],
            message: "I want to book a flight",
            stage_list: ["Greeting", "Flight Booking", "Confirmation"],
            extracted_data: { intent: "flight_booking" }
          }
        }),
      });
      
      if (!response.ok) throw new Error('Failed to preview template');
      
      const data = await response.json();
      return data;
    } catch (err) {
      setError(err.message);
      throw err;
    }
  };

  // If the StageEditor is shown, render that instead of the stage list
  if (showStageEditor && selectedStage) {
    return (
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <StageEditor
          stage={selectedStage}
          onSave={handleUpdateStage}
          onCancel={() => {
            setShowStageEditor(false);
            setSelectedStage(null);
          }}
        />
      </Container>
    );
  }

  // Otherwise render the list of stages
  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Paper sx={{ p: 3, mb: 4 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Typography variant="h5" component="h1" gutterBottom sx={{ flexGrow: 1 }}>
            Stage Manager
            {businessId && <Typography variant="subtitle1" component="span" color="text.secondary">
              {` - Business ID: ${businessId}`}
              {agentId && ` - Agent ID: ${agentId}`}
            </Typography>}
          </Typography>
          <Button
            variant="contained"
            color="primary"
            startIcon={<AddIcon />}
            onClick={() => setOpenDialog(true)}
          >
            Create Stage
          </Button>
        </Box>
        
        <Divider sx={{ mb: 3 }} />
        
        {isLoading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
            <CircularProgress />
          </Box>
        ) : (
          <>
            {stages.length === 0 ? (
              <Alert severity="info">
                No stages found. Create your first stage to get started.
              </Alert>
            ) : (
              <List sx={{ bgcolor: 'background.paper' }}>
                {[...stages].sort((a, b) => 
                  (a.stage_config?.order || Infinity) - (b.stage_config?.order || Infinity)
                ).map((stage) => (
                  <ListItem 
                    key={stage.stage_id}
                    button
                    selected={selectedStage?.stage_id === stage.stage_id}
                    onClick={() => {
                      setSelectedStage(stage);
                      setShowStageEditor(true);
                    }}
                    sx={{ 
                      borderLeft: selectedStage?.stage_id === stage.stage_id 
                        ? '4px solid #1976d2' 
                        : '4px solid transparent',
                      mb: 1,
                      bgcolor: 'background.paper'
                    }}
                  >
                    <ListItemText
                      primary={stage.stage_name}
                      secondary={
                        <>
                          <Typography component="span" variant="body2" color="text.primary">
                            {`Stage ID: ${stage.stage_id}`}
                          </Typography>
                          {stage.stage_config && stage.stage_config.order && 
                            ` â€¢ Order: ${stage.stage_config.order}`}
                        </>
                      }
                    />
                    <ListItemSecondaryAction>
                      <Tooltip title="Move Up">
                        <IconButton 
                          edge="end" 
                          onClick={(e) => {
                            e.stopPropagation();
                            handleMoveStage(stage.stage_id, 'up');
                          }} 
                          disabled={stages.indexOf(stage) === 0}
                        >
                          <ArrowUpwardIcon />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="Move Down">
                        <IconButton 
                          edge="end" 
                          onClick={(e) => {
                            e.stopPropagation();
                            handleMoveStage(stage.stage_id, 'down');
                          }} 
                          disabled={stages.indexOf(stage) === stages.length - 1}
                        >
                          <ArrowDownwardIcon />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="Edit Stage">
                        <IconButton 
                          edge="end" 
                          onClick={(e) => {
                            e.stopPropagation();
                            setSelectedStage(stage);
                            setShowStageEditor(true);
                          }}
                        >
                          <EditIcon />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="Delete Stage">
                        <IconButton 
                          edge="end" 
                          onClick={(e) => {
                            e.stopPropagation();
                            setStageToDelete(stage);
                            setOpenDeleteDialog(true);
                          }}
                        >
                          <DeleteIcon />
                        </IconButton>
                      </Tooltip>
                    </ListItemSecondaryAction>
                  </ListItem>
                ))}
              </List>
            )}
          </>
        )}
      </Paper>
      
      {/* Create Stage Dialog */}
      <Dialog open={openDialog} onClose={() => setOpenDialog(false)} maxWidth="sm" fullWidth>
        <DialogTitle>Create New Stage</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Stage Name"
            type="text"
            fullWidth
            variant="outlined"
            value={newStageName}
            onChange={(e) => setNewStageName(e.target.value)}
          />
          
          <Box sx={{ mt: 2 }}>
            <Typography variant="subtitle2" gutterBottom>
              Template Selection
            </Typography>
            <Typography variant="body2" color="text.secondary" gutterBottom>
              The stage will be created with the following default templates. You can change them later.
            </Typography>
            
            <Grid container spacing={2} sx={{ mt: 1 }}>
              <Grid item xs={12}>
                <FormControl fullWidth>
                  <InputLabel id="stage-selection-template-label">Stage Selection Template</InputLabel>
                  <Select
                    labelId="stage-selection-template-label"
                    value={defaultTemplates.stage_selection}
                    label="Stage Selection Template"
                    onChange={(e) => setDefaultTemplates(prev => ({
                      ...prev,
                      stage_selection: e.target.value
                    }))}
                  >
                    <MenuItem value=""><em>None</em></MenuItem>
                    {availableTemplates.stage_selection.map(template => (
                      <MenuItem key={template.template_id} value={template.template_id}>
                        {template.template_name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              
              <Grid item xs={12}>
                <FormControl fullWidth>
                  <InputLabel id="data-extraction-template-label">Data Extraction Template</InputLabel>
                  <Select
                    labelId="data-extraction-template-label"
                    value={defaultTemplates.data_extraction}
                    label="Data Extraction Template"
                    onChange={(e) => setDefaultTemplates(prev => ({
                      ...prev,
                      data_extraction: e.target.value
                    }))}
                  >
                    <MenuItem value=""><em>None</em></MenuItem>
                    {availableTemplates.data_extraction.map(template => (
                      <MenuItem key={template.template_id} value={template.template_id}>
                        {template.template_name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              
              <Grid item xs={12}>
                <FormControl fullWidth>
                  <InputLabel id="response-generation-template-label">Response Generation Template</InputLabel>
                  <Select
                    labelId="response-generation-template-label"
                    value={defaultTemplates.response_generation}
                    label="Response Generation Template"
                    onChange={(e) => setDefaultTemplates(prev => ({
                      ...prev,
                      response_generation: e.target.value
                    }))}
                  >
                    <MenuItem value=""><em>None</em></MenuItem>
                    {availableTemplates.response_generation.map(template => (
                      <MenuItem key={template.template_id} value={template.template_id}>
                        {template.template_name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
            </Grid>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Cancel</Button>
          <Button onClick={handleCreateStage} variant="contained" color="primary">Create</Button>
        </DialogActions>
      </Dialog>
      
      {/* Delete Confirmation Dialog */}
      <Dialog
        open={openDeleteDialog}
        onClose={() => setOpenDeleteDialog(false)}
      >
        <DialogTitle>Confirm Delete</DialogTitle>
        <DialogContent>
          <Typography>
            Are you sure you want to delete the stage "{stageToDelete?.stage_name}"? This action cannot be undone.
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDeleteDialog(false)}>Cancel</Button>
          <Button onClick={handleDeleteStage} color="error" variant="contained">Delete</Button>
        </DialogActions>
      </Dialog>
      
      {/* Error and Success Snackbars */}
      <Snackbar
        open={!!error}
        autoHideDuration={6000}
        onClose={() => setError('')}
      >
        <Alert severity="error" onClose={() => setError('')}>
          {error}
        </Alert>
      </Snackbar>
      
      <Snackbar
        open={!!success}
        autoHideDuration={6000}
        onClose={() => setSuccess('')}
      >
        <Alert severity="success" onClose={() => setSuccess('')}>
          {success}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default StageManager;
